import React from "react";

function Analytics({ tasks, studyStats, currentTheme }) {
  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";
  const isSunsetGlow = currentTheme === "Sunset Glow";
  const isForestWhisper = currentTheme === "Forest Whisper";

  // Helper function to format the local date as YYYY-MM-DD
  const getLocalDateString = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0"); // getMonth() is 0-based
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  const getTaskCompletionRate = () => {
    if (tasks.length === 0) return 0;
    const completedTasks = tasks.filter((task) => task.completed).length;
    return Math.round((completedTasks / tasks.length) * 100);
  };

  const completedTasksCount = tasks.filter((task) => task.completed).length;

  const today = new Date();
  const daysOfWeek = ["S", "M", "T", "W", "T", "F", "S"];
  const past7Days = Array(7)
    .fill(0)
    .map((_, index) => {
      const date = new Date(today);
      date.setDate(today.getDate() - (6 - index));
      return {
        date: getLocalDateString(date),
        day: daysOfWeek[date.getDay()],
      };
    });

  const completionData = past7Days.map((dayObj) => {
    return tasks.filter(
      (task) => task.completed && task.completedDate === dayObj.date
    ).length;
  });

  const maxCompletions = Math.max(...completionData, 1);
  const completionHeights = completionData.map(
    (count) => (count / maxCompletions) * 20
  );

  // Study Hours Trend Data
  const studyHoursData = past7Days.map((dayObj) => {
    const log = (studyStats.studyHoursLog || []).find(
      (log) => log.date === dayObj.date
    );
    return log ? log.hours : 0;
  });

  const maxHours = Math.max(...studyHoursData, 1); // Avoid division by zero
  const studyHoursHeights = studyHoursData.map(
    (hours) => (hours / maxHours) * 20
  );

  const priorityCounts = {
    High: tasks.filter((task) => task.priority === "High").length,
    Medium: tasks.filter((task) => task.priority === "Medium").length,
    Low: tasks.filter((task) => task.priority === "Low").length,
  };
  const totalTasks = tasks.length || 1;
  const priorityPercentages = {
    High: (priorityCounts.High / totalTasks) * 100,
    Medium: (priorityCounts.Medium / totalTasks) * 100,
    Low: (priorityCounts.Low / totalTasks) * 100,
  };

  const todayStr = getLocalDateString(today);
  const overdueTasks = tasks.filter(
    (task) => !task.completed && task.dueDate && task.dueDate < todayStr
  );

  const completedTasksByDay = past7Days.map((dayObj) => ({
    day: dayObj.day,
    date: dayObj.date,
    count: tasks.filter(
      (task) => task.completed && task.completedDate === dayObj.date
    ).length,
  }));
  const mostProductiveDay = completedTasksByDay.reduce(
    (max, day) => (day.count > max.count ? day : max),
    { day: "", date: "", count: 0 }
  );
  const mostProductiveInsight =
    mostProductiveDay.count > 0
      ? `You're most productive on ${mostProductiveDay.day}s, with ${
          mostProductiveDay.count
        } task${mostProductiveDay.count !== 1 ? "s" : ""} completed on ${
          mostProductiveDay.date
        }.`
      : "You're just getting startedâ€”complete your first task today to build your streak!";

  const overdueInsight =
    overdueTasks.length > 0
      ? `You have ${overdueTasks.length} overdue task${
          overdueTasks.length !== 1 ? "s" : ""
        }. Focus on completing these to stay on track!`
      : "Great job! You have no overdue tasks.";

  const incompleteHighPriorityTasks = tasks.filter(
    (task) => !task.completed && task.priority === "High"
  ).length;
  const priorityInsight =
    incompleteHighPriorityTasks > 0
      ? `You have ${incompleteHighPriorityTasks} incomplete high-priority task${
          incompleteHighPriorityTasks !== 1 ? "s" : ""
        }. Prioritize these to maximize your productivity.`
      : "No high-priority tasks pending. Keep up the good work!";

  return (
    <div
      className={`rounded-xl shadow-lg transition-all duration-300 ${
        isDarkMode
          ? "bg-gray-800 border border-gray-700"
          : isOceanBreeze
            ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
            : isSunsetGlow
              ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
              : isForestWhisper
                ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                : "bg-white border border-gray-200"
      }`}
    >
      <div
        className={`p-5 border-b ${
          isDarkMode
            ? "border-gray-600"
            : isOceanBreeze
              ? "border-teal-400"
              : isSunsetGlow
                ? "border-orange-400"
                : isForestWhisper
                  ? "border-green-400"
                  : "border-gray-300"
        }`}
      >
        <h2
          className={`text-xl font-bold tracking-tight ${
            isDarkMode
              ? "text-gray-100"
              : isOceanBreeze
                ? "text-teal-900"
                : isSunsetGlow
                  ? "text-orange-900"
                  : isForestWhisper
                    ? "text-green-900"
                    : "text-gray-900"
          }`}
        >
          Study Analytics
        </h2>
      </div>
      <div className="p-8 text-center space-y-10">
        <div
          className={`inline-block p-12 rounded-full mb-4 shadow-lg transition-all duration-300 hover:shadow-xl ${
            isDarkMode
              ? "bg-gray-700"
              : isOceanBreeze
                ? "bg-teal-50 bg-opacity-90 backdrop-blur-md"
                : isSunsetGlow
                  ? "bg-pink-50 bg-opacity-90 backdrop-blur-md"
                  : isForestWhisper
                    ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md"
                    : "bg-gray-50"
          }`}
        >
          <div
            className={`text-5xl font-extrabold tracking-tight ${
              isDarkMode
                ? "text-indigo-400"
                : isOceanBreeze
                  ? "text-teal-700"
                  : isSunsetGlow
                    ? "text-orange-600"
                    : isForestWhisper
                      ? "text-green-600"
                      : "text-indigo-600"
            }`}
          >
            {getTaskCompletionRate()}%
          </div>
        </div>
        <h3
          className={`text-xl font-semibold tracking-tight ${
            isDarkMode
              ? "text-gray-100"
              : isOceanBreeze
                ? "text-teal-800"
                : isSunsetGlow
                  ? "text-orange-800"
                  : isForestWhisper
                    ? "text-green-800"
                    : "text-gray-800"
          }`}
        >
          Task Completion Rate
        </h3>
        <p
          className={`mt-2 text-sm ${
            isDarkMode
              ? "text-gray-400"
              : isOceanBreeze
                ? "text-blue-600"
                : isSunsetGlow
                  ? "text-orange-500"
                  : isForestWhisper
                    ? "text-green-500"
                    : "text-gray-500"
          }`}
        >
          You've completed {completedTasksCount} out of {tasks.length} tasks
        </p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Task Completion Timeline */}
          <div
            className={`p-5 rounded-xl shadow-md transition-all duration-300 hover:shadow-xl ${
              isDarkMode
                ? "bg-gray-700"
                : isOceanBreeze
                  ? "bg-amber-50 bg-opacity-90 backdrop-blur-md"
                  : isSunsetGlow
                    ? "bg-pink-50 bg-opacity-90 backdrop-blur-md"
                    : isForestWhisper
                      ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md"
                      : "bg-gray-50"
            }`}
          >
            <h4
              className={`text-lg font-semibold mb-4 tracking-tight ${
                isDarkMode
                  ? "text-gray-100"
                  : isOceanBreeze
                    ? "text-teal-800"
                    : isSunsetGlow
                      ? "text-orange-800"
                      : isForestWhisper
                        ? "text-green-800"
                        : "text-gray-800"
              }`}
            >
              Task Completion Timeline
            </h4>
            <div className="flex justify-center space-x-3 mt-4">
              {past7Days.map((dayObj, index) => (
                <div key={dayObj.date} className="flex flex-col items-center">
                  <div
                    className={`w-6 h-${Math.round(
                      completionHeights[index]
                    )} rounded-t-md transition-all duration-300 hover:scale-105 ${
                      isDarkMode
                        ? "bg-indigo-500"
                        : isOceanBreeze
                          ? "bg-teal-500"
                          : isSunsetGlow
                            ? "bg-orange-500"
                            : isForestWhisper
                              ? "bg-green-500"
                              : "bg-indigo-600"
                    }`}
                  ></div>
                  <span
                    className={`text-xs font-medium mt-2 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                          ? "text-blue-600"
                          : isSunsetGlow
                            ? "text-orange-500"
                            : isForestWhisper
                              ? "text-green-500"
                              : "text-gray-500"
                    }`}
                  >
                    {dayObj.day}
                  </span>
                  <span
                    className={`text-xs font-semibold mt-1 ${
                      isDarkMode
                        ? "text-gray-300"
                        : isOceanBreeze
                          ? "text-teal-700"
                          : isSunsetGlow
                            ? "text-orange-600"
                            : isForestWhisper
                              ? "text-green-600"
                              : "text-gray-600"
                    }`}
                  >
                    {completionData[index]}
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Priority Breakdown */}
          <div
            className={`p-5 rounded-xl shadow-md transition-all duration-300 hover:shadow-xl ${
              isDarkMode
                ? "bg-gray-700"
                : isOceanBreeze
                  ? "bg-amber-50 bg-opacity-90 backdrop-blur-md"
                  : isSunsetGlow
                    ? "bg-pink-50 bg-opacity-90 backdrop-blur-md"
                    : isForestWhisper
                      ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md"
                      : "bg-gray-50"
            }`}
          >
            <h4
              className={`text-lg font-semibold mb-4 tracking-tight ${
                isDarkMode
                  ? "text-gray-100"
                  : isOceanBreeze
                    ? "text-teal-800"
                    : isSunsetGlow
                      ? "text-orange-800"
                      : isForestWhisper
                        ? "text-green-800"
                        : "text-gray-800"
              }`}
            >
              Priority Breakdown
            </h4>
            <div className="space-y-4 mt-4">
              {["High", "Medium", "Low"].map((priority, index) => (
                <div key={priority} className="flex flex-col items-start">
                  <div className="flex items-center w-full">
                    <div
                      className={`h-5 rounded-lg shadow-sm transition-all duration-300 hover:scale-[1.02] ${
                        index === 0
                          ? "bg-red-500"
                          : index === 1
                            ? "bg-yellow-500"
                            : "bg-blue-500"
                      }`}
                      style={{ width: `${priorityPercentages[priority]}%` }}
                    ></div>
                    <span
                      className={`text-sm font-medium ml-3 ${
                        isDarkMode
                          ? "text-gray-300"
                          : isOceanBreeze
                            ? "text-teal-700"
                            : isSunsetGlow
                              ? "text-orange-600"
                              : isForestWhisper
                                ? "text-green-600"
                                : "text-gray-600"
                      }`}
                    >
                      {Math.round(priorityPercentages[priority])}%
                    </span>
                  </div>
                  <span
                    className={`text-xs font-medium mt-1 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                          ? "text-blue-600"
                          : isSunsetGlow
                            ? "text-orange-500"
                            : isForestWhisper
                              ? "text-green-500"
                              : "text-gray-500"
                    }`}
                  >
                    {priority}: {priorityCounts[priority]} task
                    {priorityCounts[priority] !== 1 ? "s" : ""}
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Study Hours Trend */}
          <div
            className={`p-5 rounded-xl shadow-md transition-all duration-300 hover:shadow-xl ${
              isDarkMode
                ? "bg-gray-700"
                : isOceanBreeze
                  ? "bg-amber-50 bg-opacity-90 backdrop-blur-md"
                  : isSunsetGlow
                    ? "bg-pink-50 bg-opacity-90 backdrop-blur-md"
                    : isForestWhisper
                      ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md"
                      : "bg-gray-50"
            }`}
          >
            <h4
              className={`text-lg font-semibold mb-4 tracking-tight ${
                isDarkMode
                  ? "text-gray-100"
                  : isOceanBreeze
                    ? "text-teal-800"
                    : isSunsetGlow
                      ? "text-orange-800"
                      : isForestWhisper
                        ? "text-green-800"
                        : "text-gray-800"
              }`}
            >
              Study Hours Trend
            </h4>
            <div className="flex justify-center space-x-3 mt-4">
              {past7Days.map((dayObj, index) => (
                <div key={dayObj.date} className="flex flex-col items-center">
                  <div
                    className={`w-6 h-${Math.round(
                      studyHoursHeights[index]
                    )} rounded-t-md transition-all duration-300 hover:scale-105 ${
                      isDarkMode
                        ? "bg-purple-500"
                        : isOceanBreeze
                          ? "bg-cyan-500"
                          : isSunsetGlow
                            ? "bg-amber-500"
                            : isForestWhisper
                              ? "bg-lime-500"
                              : "bg-purple-600"
                    }`}
                  ></div>
                  <span
                    className={`text-xs font-medium mt-2 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                          ? "text-blue-600"
                          : isSunsetGlow
                            ? "text-orange-500"
                            : isForestWhisper
                              ? "text-green-500"
                              : "text-gray-500"
                    }`}
                  >
                    {dayObj.day}
                  </span>
                  <span
                    className={`text-xs font-semibold mt-1 ${
                      isDarkMode
                        ? "text-gray-300"
                        : isOceanBreeze
                          ? "text-teal-700"
                          : isSunsetGlow
                            ? "text-orange-600"
                            : isForestWhisper
                              ? "text-green-600"
                              : "text-gray-600"
                    }`}
                  >
                    {studyHoursData[index]}h
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div
          className={`mt-8 p-5 rounded-xl shadow-md transition-all duration-300 ${
            isDarkMode
              ? "bg-indigo-900 border border-indigo-800"
              : isOceanBreeze
                ? "bg-teal-50 bg-opacity-90 backdrop-blur-md border border-teal-400"
                : isSunsetGlow
                  ? "bg-pink-50 bg-opacity-90 backdrop-blur-md border border-orange-400"
                  : isForestWhisper
                    ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md border border-green-400"
                    : "bg-indigo-50 border border-indigo-200"
          } text-left`}
        >
          <h4
            className={`text-lg font-semibold mb-4 tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            AI Insights
          </h4>
          <ul className="space-y-3 text-sm">
            <li className="flex items-start">
              <div className="w-5 h-5 rounded-full bg-green-500 mt-1 mr-3 shadow-sm"></div>
              <p
                className={`leading-relaxed ${
                  isDarkMode
                    ? "text-gray-300"
                    : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                          ? "text-green-500"
                          : "text-gray-500"
                }`}
              >
                {mostProductiveInsight}
              </p>
            </li>
            <li className="flex items-start">
              <div className="w-5 h-5 rounded-full bg-yellow-500 mt-1 mr-3 shadow-sm"></div>
              <p
                className={`leading-relaxed ${
                  isDarkMode
                    ? "text-gray-300"
                    : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                          ? "text-green-500"
                          : "text-gray-500"
                }`}
              >
                {overdueInsight}
              </p>
            </li>
            <li className="flex items-start">
              <div className="w-5 h-5 rounded-full bg-blue-500 mt-1 mr-3 shadow-sm"></div>
              <p
                className={`leading-relaxed ${
                  isDarkMode
                    ? "text-gray-300"
                    : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                          ? "text-green-500"
                          : "text-gray-500"
                }`}
              >
                {priorityInsight}
              </p>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Analytics;
