import React from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import withDragAndDrop from "react-big-calendar/lib/addons/dragAndDrop";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "react-big-calendar/lib/addons/dragAndDrop/styles.css";

// Custom CSS to fix toolbar visibility
const styles = `
  .rbc-toolbar {
    display: flex !important;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
  }
  .rbc-toolbar button {
    background-color: #4f46e5 !important;
    color: white !important;
    border: none !important;
    padding: 8px 16px !important;
    border-radius: 4px !important;
    cursor: pointer !important;
  }
  .rbc-toolbar button:hover {
    background-color: #4338ca !important;
  }
  .rbc-toolbar-label {
    font-size: 1.25rem;
    font-weight: 600;
  }
`;

// Inject custom styles
const styleSheet = document.createElement("style");
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);

const localizer = momentLocalizer(moment);
const DnDCalendar = withDragAndDrop(Calendar);

function CalendarView({ tasks, scheduleEvents, handleEventDrop }) {
  const taskEvents = tasks.map((task) => ({
    id: String(task.id),
    title: task.title,
    start: new Date(task.dueDate),
    end: new Date(task.dueDate),
    allDay: true,
    priority: task.priority,
    completed: task.completed,
    isSchedule: false,
  }));

  const formattedScheduleEvents = scheduleEvents.map((event) => ({
    ...event,
    id: String(event.id),
    isSchedule: true,
  }));

  const events = [...taskEvents, ...formattedScheduleEvents];

  const eventStyleGetter = (event) => {
    let backgroundColor = "#3174ad";
    if (event.isSchedule) {
      backgroundColor = "#10b981";
    } else if (event.priority === "High") {
      backgroundColor = "#ef4444";
    } else if (event.priority === "Medium") {
      backgroundColor = "#f59e0b";
    }

    const style = {
      backgroundColor,
      borderRadius: "5px",
      opacity: event.completed ? 0.5 : 1,
      color: "white",
      border: "0px",
      display: "block",
    };
    return { style };
  };

  return (
    <div className="bg-white rounded-lg shadow">
      <div className="p-4 border-b border-gray-200">
        <h2 className="font-semibold">Task Calendar</h2>
      </div>
      <div className="p-4">
        <DnDCalendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 600 }}
          eventPropGetter={eventStyleGetter}
          onEventDrop={(event) => {
            console.log("Dropped event:", event);
            handleEventDrop(event);
          }}
          draggableAccessor={(event) => !event.isSchedule}
          defaultView="month"
          views={["month", "week", "day"]}
          popup
          onView={(view) => console.log("View changed to:", view)} // Debug view changes
          onNavigate={(date) => console.log("Navigated to:", date)} // Debug navigation
        />
      </div>
    </div>
  );
}

export default CalendarView;
