import React from "react";

function Analytics({ tasks, studyStats, currentTheme }) {
  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";

  const getTaskCompletionRate = () => {
    if (tasks.length === 0) return 0;
    const completedTasks = tasks.filter((task) => task.completed).length;
    return Math.round((completedTasks / tasks.length) * 100);
  };

  const completedTasksCount = tasks.filter((task) => task.completed).length;

  // Calculate Subject Distribution
  const subjects = ["Math", "Science", "English", "History"];
  const subjectCounts = subjects.map(
    (subject) =>
      tasks.filter((task) => task.subject === subject && task.completed).length
  );
  const maxSubjectCount = Math.max(...subjectCounts, 1); // Avoid division by 0
  const subjectHeights = subjectCounts.map(
    (count) => (count / maxSubjectCount) * 20
  ); // Scale to max height of 20

  // Calculate Study Time by Day (Last 7 Days)
  const today = new Date();
  const days = ["M", "T", "W", "T", "F", "S", "S"];
  const studyHoursData = Array(7)
    .fill(0)
    .map((_, index) => {
      const date = new Date(today);
      date.setDate(today.getDate() - (6 - index));
      const dateStr = date.toISOString().split("T")[0];
      const logEntry = studyStats?.studyHoursLog?.find(
        (log) => log.date === dateStr
      );
      return logEntry ? logEntry.hours : 0;
    });
  const maxStudyHours = Math.max(...studyHoursData, 1); // Avoid division by 0
  const studyHoursHeights = studyHoursData.map(
    (hours) => (hours / maxStudyHours) * 20
  ); // Scale to max height of 20

  return (
    <div
      className={`rounded-lg shadow ${
        isDarkMode
          ? "bg-gray-700"
          : isOceanBreeze
          ? "bg-opacity-70 backdrop-blur-lg"
          : "bg-white"
      }`}
    >
      <div
        className={`p-4 border-b ${
          isDarkMode
            ? "border-gray-600"
            : isOceanBreeze
            ? "border-teal-300"
            : "border-gray-200"
        }`}
      >
        <h2
          className={`font-semibold ${
            isDarkMode
              ? "text-gray-200"
              : isOceanBreeze
              ? "text-teal-800"
              : "text-gray-800"
          }`}
        >
          Study Analytics
        </h2>
      </div>
      <div className="p-8 text-center">
        <div
          className={`inline-block p-12 rounded-full mb-4 ${
            isDarkMode
              ? "bg-gray-600"
              : isOceanBreeze
              ? "bg-teal-100 bg-opacity-70 backdrop-blur-sm"
              : "bg-gray-100"
          }`}
        >
          <div
            className={`text-4xl font-bold ${
              isDarkMode
                ? "text-indigo-400"
                : isOceanBreeze
                ? "text-teal-700"
                : "text-indigo-600"
            }`}
          >
            {getTaskCompletionRate()}%
          </div>
        </div>
        <h3
          className={`text-lg font-medium ${
            isDarkMode
              ? "text-gray-200"
              : isOceanBreeze
              ? "text-teal-800"
              : "text-gray-800"
          }`}
        >
          Task Completion Rate
        </h3>
        <p
          className={`mt-2 ${
            isDarkMode
              ? "text-gray-400"
              : isOceanBreeze
              ? "text-blue-600"
              : "text-gray-500"
          }`}
        >
          You've completed {completedTasksCount} out of {tasks.length} tasks
        </p>

        <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-4">
          <div
            className={`p-4 rounded-lg ${
              isDarkMode
                ? "bg-gray-600"
                : isOceanBreeze
                ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm"
                : "bg-gray-50"
            }`}
          >
            <h4
              className={`font-medium mb-2 ${
                isDarkMode
                  ? "text-gray-200"
                  : isOceanBreeze
                  ? "text-teal-800"
                  : "text-gray-800"
              }`}
            >
              Subject Distribution
            </h4>
            <div className="flex justify-center space-x-2 mt-4">
              {subjects.map((subject, index) => (
                <div key={subject} className="flex flex-col items-center">
                  <div
                    className={`w-4 h-${Math.round(
                      subjectHeights[index]
                    )} rounded-t-sm ${
                      isDarkMode
                        ? "bg-indigo-500"
                        : isOceanBreeze
                        ? "bg-teal-500"
                        : "bg-indigo-600"
                    }`}
                  ></div>
                  <span
                    className={`text-xs mt-1 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                        ? "text-blue-600"
                        : "text-gray-800"
                    }`}
                  >
                    {subject.slice(0, 4)}
                  </span>
                </div>
              ))}
            </div>
          </div>

          <div
            className={`p-4 rounded-lg ${
              isDarkMode
                ? "bg-gray-600"
                : isOceanBreeze
                ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm"
                : "bg-gray-50"
            }`}
          >
            <h4
              className={`font-medium mb-2 ${
                isDarkMode
                  ? "text-gray-200"
                  : isOceanBreeze
                  ? "text-teal-800"
                  : "text-gray-800"
              }`}
            >
              Study Time by Day
            </h4>
            <div className="flex justify-center space-x-2 mt-4">
              {days.map((day, index) => (
                <div key={day + index} className="flex flex-col items-center">
                  <div
                    className={`w-4 h-${Math.round(
                      studyHoursHeights[index]
                    )} rounded-t-sm ${
                      isDarkMode
                        ? "bg-indigo-500"
                        : isOceanBreeze
                        ? "bg-teal-500"
                        : "bg-indigo-600"
                    }`}
                  ></div>
                  <span
                    className={`text-xs mt-1 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                        ? "text-blue-600"
                        : "text-gray-800"
                    }`}
                  >
                    {day}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div
          className={`mt-8 p-4 rounded-lg ${
            isDarkMode
              ? "bg-indigo-900"
              : isOceanBreeze
              ? "bg-teal-100 bg-opacity-70 backdrop-blur-sm"
              : "bg-indigo-50"
          } text-left`}
        >
          <h4
            className={`font-medium mb-2 ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            AI Insights
          </h4>
          <ul className="space-y-2 text-sm">
            <li className="flex items-start">
              <div className="w-4 h-4 rounded-full bg-green-500 mt-1 mr-2"></div>
              <p>You're most productive on Thursday afternoons between 2-4pm</p>
            </li>
            <li className="flex items-start">
              <div className="w-4 h-4 rounded-full bg-yellow-500 mt-1 mr-2"></div>
              <p>Math assignments take you 30% longer than other subjects</p>
            </li>
            <li className="flex items-start">
              <div className="w-4 h-4 rounded-full bg-blue-500 mt-1 mr-2"></div>
              <p>
                You complete tasks more efficiently when you break them into
                25-minute focused sessions
              </p>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Analytics;
