import React, { useState } from "react";
import { Calendar, Clock, CheckCircle, PlusCircle } from "lucide-react";

function Dashboard({
  tasks,
  studyStats,
  aiSuggestion,
  getStudyTips,
  generateSchedule,
  setActiveTab,
  toggleTaskComplete,
  currentTheme,
  logStudyHours,
  reduceStudyHours,
}) {
  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";
  const isSunsetGlow = currentTheme === "Sunset Glow";
  const isForestWhisper = currentTheme === "Forest Whisper";

  // Filter out deleted tasks
  const nonDeletedTasks = tasks.filter((task) => !task.deleted);
  const upcomingTasks = nonDeletedTasks
    .filter((task) => !task.completed)
    .slice(0, 3);

  // Calculate completed tasks by priority
  const completedTasks = nonDeletedTasks.filter((task) => task.completed);
  const highPriorityCompleted = completedTasks.filter(
    (task) => task.priority === "High"
  ).length;
  const mediumPriorityCompleted = completedTasks.filter(
    (task) => task.priority === "Medium"
  ).length;
  const lowPriorityCompleted = completedTasks.filter(
    (task) => task.priority === "Low"
  ).length;

  const [hoursInput, setHoursInput] = useState("");

  const handleLogHours = () => {
    const hours = parseFloat(hoursInput);
    if (isNaN(hours) || hours <= 0) {
      return; // Error handling is managed in Layout.jsx
    }
    logStudyHours(hours);
    setHoursInput("");
  };

  const handleReduceHours = () => {
    const hours = parseFloat(hoursInput);
    if (isNaN(hours) || hours <= 0) {
      return; // Error handling is managed in Layout.jsx via reduceStudyHours
    }
    const today = new Date().toISOString().split("T")[0]; // Format as YYYY-MM-DD
    reduceStudyHours(hours, today);
    setHoursInput("");
  };

  // Calculate the dynamic range for the study streak display
  const streak = studyStats.streak || 0;
  const rangeEnd = Math.ceil(streak / 7) * 7;
  const rangeStart = streak > 0 ? rangeEnd - 6 : 1;
  const streakRange = Array.from({ length: 7 }, (_, i) => rangeStart + i);

  return (
    <div className="space-y-8">
      {/* AI Assistant Card */}
      <div
        className={`rounded-xl shadow-lg transition-all duration-300 ${
          isDarkMode
            ? "bg-gray-800 border border-gray-700"
            : isOceanBreeze
              ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
              : isSunsetGlow
                ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                : isForestWhisper
                  ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                  : "bg-white border border-gray-200"
        } p-6`}
      >
        <h2
          className={`text-xl font-bold mb-4 flex items-center tracking-tight ${
            isDarkMode
              ? "text-gray-100"
              : isOceanBreeze
                ? "text-teal-900"
                : isSunsetGlow
                  ? "text-orange-900"
                  : isForestWhisper
                    ? "text-green-900"
                    : "text-gray-900"
          }`}
        >
          <div
            className={`w-8 h-8 rounded-full flex items-center justify-center mr-3 shadow-sm ${
              isDarkMode
                ? "bg-indigo-900"
                : isOceanBreeze
                  ? "bg-teal-100"
                  : isSunsetGlow
                    ? "bg-orange-100"
                    : isForestWhisper
                      ? "bg-green-100"
                      : "bg-indigo-100"
            } transition-transform duration-300 hover:scale-110`}
          >
            <div
              className={`w-4 h-4 rounded-full ${
                isDarkMode
                  ? "bg-indigo-500"
                  : isOceanBreeze
                    ? "bg-teal-500"
                    : isSunsetGlow
                      ? "bg-orange-500"
                      : isForestWhisper
                        ? "bg-green-500"
                        : "bg-indigo-600"
              }`}
            ></div>
          </div>
          AI Study Assistant
        </h2>
        <div
          className={`p-5 rounded-xl border shadow-inner min-h-[120px] transition-all duration-300 ${
            isDarkMode
              ? "bg-indigo-900 border-indigo-800 text-gray-200"
              : isOceanBreeze
                ? "bg-teal-50 bg-opacity-90 backdrop-blur-md border-teal-400 text-teal-800"
                : isSunsetGlow
                  ? "bg-pink-50 bg-opacity-90 backdrop-blur-md border-orange-400 text-orange-800"
                  : isForestWhisper
                    ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md border-green-400 text-green-800"
                    : "bg-indigo-50 border-indigo-200 text-gray-800"
          }`}
        >
          {aiSuggestion ? (
            <div className="whitespace-pre-line text-sm leading-relaxed">
              {aiSuggestion}
            </div>
          ) : (
            <div className="flex items-center justify-center h-16">
              <div className="animate-pulse flex space-x-3">
                <div
                  className={`w-3 h-3 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                        ? "bg-teal-500"
                        : isSunsetGlow
                          ? "bg-orange-500"
                          : isForestWhisper
                            ? "bg-green-500"
                            : "bg-indigo-400"
                  }`}
                ></div>
                <div
                  className={`w-3 h-3 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                        ? "bg-teal-500"
                        : isSunsetGlow
                          ? "bg-orange-500"
                          : isForestWhisper
                            ? "bg-green-500"
                            : "bg-indigo-400"
                  }`}
                ></div>
                <div
                  className={`w-3 h-3 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                        ? "bg-teal-500"
                        : isSunsetGlow
                          ? "bg-orange-500"
                          : isForestWhisper
                            ? "bg-green-500"
                            : "bg-indigo-400"
                  }`}
                ></div>
              </div>
            </div>
          )}
        </div>
        <div className="mt-5 flex space-x-3">
          <button
            onClick={getStudyTips}
            className={`px-5 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
              isDarkMode
                ? "bg-indigo-600 hover:bg-indigo-700"
                : isOceanBreeze
                  ? "bg-teal-600 hover:bg-teal-700"
                  : isSunsetGlow
                    ? "bg-orange-600 hover:bg-orange-700"
                    : isForestWhisper
                      ? "bg-green-600 hover:bg-green-700"
                      : "bg-indigo-600 hover:bg-indigo-700"
            }`}
          >
            Get Study Tips
          </button>
          <button
            onClick={generateSchedule}
            className={`px-5 py-2 rounded-lg text-sm font-semibold shadow-md border transition-all duration-200 transform hover:scale-105 ${
              isDarkMode
                ? "bg-gray-700 border-gray-600 text-gray-200 hover:bg-gray-600"
                : isOceanBreeze
                  ? "bg-amber-50 bg-opacity-90 backdrop-blur-md border-teal-400 text-teal-800 hover:bg-amber-100"
                  : isSunsetGlow
                    ? "bg-pink-50 bg-opacity-90 backdrop-blur-md border-orange-400 text-orange-800 hover:bg-pink-100"
                    : isForestWhisper
                      ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md border-green-400 text-green-800 hover:bg-emerald-100"
                      : "bg-white border-gray-300 text-gray-700 hover:bg-gray-50"
            }`}
          >
            Generate Report
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div
          className={`rounded-xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl ${
            isDarkMode
              ? "bg-gray-800 border border-gray-700"
              : isOceanBreeze
                ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
                : isSunsetGlow
                  ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                  : isForestWhisper
                    ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                    : "bg-white border border-gray-200"
          }`}
        >
          <div className="flex justify-between items-center mb-4">
            <h3
              className={`text-sm font-medium tracking-wide ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              Study Hours
            </h3>
            <Clock
              className={`h-6 w-6 transition-transform duration-300 hover:scale-110 ${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                    ? "text-teal-600"
                    : isSunsetGlow
                      ? "text-orange-600"
                      : isForestWhisper
                        ? "text-green-600"
                        : "text-indigo-600"
              }`}
            />
          </div>
          <p
            className={`text-4xl font-extrabold tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            {studyStats.totalHours}h
          </p>
          <div className="flex mt-5 space-x-3">
            <input
              type="number"
              placeholder="Hours"
              className={`p-2 border rounded-lg w-24 text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
                isDarkMode
                  ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                  : isOceanBreeze
                    ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                    : isSunsetGlow
                      ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                      : isForestWhisper
                        ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                        : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
              }`}
              value={hoursInput}
              onChange={(e) => setHoursInput(e.target.value)}
              min="0"
              step="0.5"
            />
            <button
              onClick={handleLogHours}
              className={`px-5 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                isDarkMode
                  ? "bg-indigo-600 hover:bg-indigo-700"
                  : isOceanBreeze
                    ? "bg-teal-600 hover:bg-teal-700"
                    : isSunsetGlow
                      ? "bg-orange-600 hover:bg-orange-700"
                      : isForestWhisper
                        ? "bg-green-600 hover:bg-green-700"
                        : "bg-indigo-600 hover:bg-indigo-700"
              }`}
            >
              Log Hours
            </button>
            <button
              onClick={handleReduceHours}
              className={`px-5 py-2 rounded-lg text-sm font-semibold shadow-md border transition-all duration-200 transform hover:scale-105 ${
                isDarkMode
                  ? "bg-gray-700 border-gray-600 text-gray-200 hover:bg-gray-600"
                  : isOceanBreeze
                    ? "bg-amber-50 bg-opacity-90 backdrop-blur-md border-teal-400 text-teal-800 hover:bg-amber-100"
                    : isSunsetGlow
                      ? "bg-pink-50 bg-opacity-90 backdrop-blur-md border-orange-400 text-orange-800 hover:bg-pink-100"
                      : isForestWhisper
                        ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md border-green-400 text-green-800 hover:bg-emerald-100"
                        : "bg-white border-gray-300 text-gray-700 hover:bg-gray-50"
              }`}
            >
              Reduce Hours
            </button>
          </div>
        </div>

        <div
          className={`rounded-xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl ${
            isDarkMode
              ? "bg-gray-800 border border-gray-700"
              : isOceanBreeze
                ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
                : isSunsetGlow
                  ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                  : isForestWhisper
                    ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                    : "bg-white border border-gray-200"
          }`}
        >
          <div className="flex justify-between items-center mb-4">
            <h3
              className={`text-sm font-medium tracking-wide ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              Tasks Completed
            </h3>
            <CheckCircle
              className={`h-6 w-6 transition-transform duration-300 hover:scale-110 ${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                    ? "text-teal-600"
                    : isSunsetGlow
                      ? "text-orange-600"
                      : isForestWhisper
                        ? "text-green-600"
                        : "text-indigo-600"
              }`}
            />
          </div>
          <p
            className={`text-4xl font-extrabold tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            {studyStats.completedTasks}
          </p>
          <div className="mt-4 flex justify-center space-x-6">
            {/* High Priority */}
            <div className="flex flex-col items-center">
              <div
                className={`w-14 h-14 rounded-full flex items-center justify-center shadow-md transition-transform duration-300 hover:scale-105 ${
                  isDarkMode
                    ? "bg-gradient-to-br from-red-600 to-orange-600 text-white"
                    : "bg-gradient-to-br from-red-500 to-orange-500 text-white"
                }`}
              >
                <span className="text-xs font-semibold">High</span>
              </div>
              <span
                className={`text-lg font-semibold mt-1 ${
                  isDarkMode
                    ? "text-gray-100"
                    : isOceanBreeze
                      ? "text-teal-800"
                      : isSunsetGlow
                        ? "text-orange-800"
                        : isForestWhisper
                          ? "text-green-800"
                          : "text-gray-800"
                }`}
              >
                {highPriorityCompleted}
              </span>
            </div>
            {/* Medium Priority */}
            <div className="flex flex-col items-center">
              <div
                className={`w-14 h-14 rounded-full flex items-center justify-center shadow-md transition-transform duration-300 hover:scale-105 ${
                  isDarkMode
                    ? "bg-gradient-to-br from-yellow-600 to-orange-600 text-white"
                    : "bg-gradient-to-br from-yellow-500 to-orange-500 text-white"
                }`}
              >
                <span className="text-xs font-semibold">Medium</span>
              </div>
              <span
                className={`text-sm font-semibold mt-1 ${
                  isDarkMode
                    ? "text-gray-100"
                    : isOceanBreeze
                      ? "text-teal-800"
                      : isSunsetGlow
                        ? "text-orange-800"
                        : isForestWhisper
                          ? "text-green-800"
                          : "text-gray-800"
                }`}
              >
                {mediumPriorityCompleted}
              </span>
            </div>
            {/* Low Priority */}
            <div className="flex flex-col items-center">
              <div
                className={`w-14 h-14 rounded-full flex items-center justify-center shadow-md transition-transform duration-300 hover:scale-105 ${
                  isDarkMode
                    ? "bg-gradient-to-br from-green-600 to-emerald-600 text-white"
                    : "bg-gradient-to-br from-green-500 to-emerald-500 text-white"
                }`}
              >
                <span className="text-xs font-semibold">Low</span>
              </div>
              <span
                className={`text-sm font-semibold mt-1 ${
                  isDarkMode
                    ? "text-gray-100"
                    : isOceanBreeze
                      ? "text-teal-800"
                      : isSunsetGlow
                        ? "text-orange-800"
                        : isForestWhisper
                          ? "text-green-800"
                          : "text-gray-800"
                }`}
              >
                {lowPriorityCompleted}
              </span>
            </div>
          </div>
        </div>

        <div
          className={`rounded-xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl ${
            isDarkMode
              ? "bg-gray-800 border border-gray-700"
              : isOceanBreeze
                ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
                : isSunsetGlow
                  ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                  : isForestWhisper
                    ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                    : "bg-white border border-gray-200"
          }`}
        >
          <div className="flex justify-between items-center mb-4">
            <h3
              className={`text-sm font-medium tracking-wide ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              Study Streak
            </h3>
            <Calendar
              className={`h-6 w-6 transition-transform duration-300 hover:scale-110 ${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                    ? "text-teal-600"
                    : isSunsetGlow
                      ? "text-orange-600"
                      : isForestWhisper
                        ? "text-green-600"
                        : "text-indigo-600"
              }`}
            />
          </div>
          <p
            className={`text-4xl font-extrabold tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            {studyStats.streak} days
          </p>
          <div className="flex space-x-2 mt-4">
            {streakRange.map((day) => (
              <div
                key={day}
                className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium shadow-sm transition-transform duration-300 hover:scale-110 ${
                  day <= studyStats.streak
                    ? isDarkMode
                      ? "bg-indigo-500 text-white"
                      : isOceanBreeze
                        ? "bg-teal-500 text-white"
                        : isSunsetGlow
                          ? "bg-orange-500 text-white"
                          : isForestWhisper
                            ? "bg-green-500 text-white"
                            : "bg-indigo-600 text-white"
                    : isDarkMode
                      ? "bg-gray-600 text-gray-400"
                      : isOceanBreeze
                        ? "bg-blue-200 text-blue-600"
                        : isSunsetGlow
                          ? "bg-orange-200 text-orange-600"
                          : isForestWhisper
                            ? "bg-green-200 text-green-600"
                            : "bg-gray-100 text-gray-500"
                }`}
              >
                {day}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Upcoming Tasks */}
      <div
        className={`rounded-xl shadow-lg transition-all duration-300 ${
          isDarkMode
            ? "bg-gray-800 border border-gray-700"
            : isOceanBreeze
              ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
              : isSunsetGlow
                ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                : isForestWhisper
                  ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                  : "bg-white border border-gray-200"
        }`}
      >
        <div
          className={`p-5 border-b ${
            isDarkMode
              ? "border-gray-600"
              : isOceanBreeze
                ? "border-teal-400"
                : isSunsetGlow
                  ? "border-orange-400"
                  : isForestWhisper
                    ? "border-green-400"
                    : "border-gray-300"
          }`}
        >
          <h2
            className={`text-xl font-bold tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-900"
                  : isSunsetGlow
                    ? "text-orange-900"
                    : isForestWhisper
                      ? "text-green-900"
                      : "text-gray-900"
            }`}
          >
            Upcoming Tasks
          </h2>
        </div>
        <div className="p-3 space-y-3">
          {upcomingTasks.map((task) => (
            <div
              key={task.id}
              className={`p-3 rounded-lg flex items-center shadow-sm transition-all duration-300 hover:shadow-md hover:scale-[1.02] ${
                isDarkMode
                  ? "hover:bg-gray-700"
                  : isOceanBreeze
                    ? "hover:bg-teal-50"
                    : isSunsetGlow
                      ? "hover:bg-orange-50"
                      : isForestWhisper
                        ? "hover:bg-green-50"
                        : "hover:bg-gray-50"
              }`}
            >
              <div className="flex items-center space-x-3">
                <button
                  onClick={() => toggleTaskComplete(task.id)}
                  className={`p-1 rounded-full transition-all duration-200 ${
                    task.completed
                      ? isDarkMode
                        ? "text-green-400 hover:text-green-500"
                        : isOceanBreeze
                          ? "text-teal-600 hover:text-teal-700"
                          : isSunsetGlow
                            ? "text-orange-600 hover:text-orange-700"
                            : isForestWhisper
                              ? "text-green-600 hover:text-green-700"
                              : "text-indigo-600 hover:text-indigo-700"
                      : isDarkMode
                        ? "text-gray-500 hover:text-gray-400"
                        : isOceanBreeze
                          ? "text-blue-400 hover:text-blue-500"
                          : isSunsetGlow
                            ? "text-orange-400 hover:text-orange-500"
                            : isForestWhisper
                              ? "text-green-400 hover:text-green-500"
                              : "text-gray-400 hover:text-gray-500"
                  }`}
                >
                  <CheckCircle
                    className={`w-5 h-5 ${task.completed ? "fill-current" : ""}`}
                  />
                </button>
                <div>
                  <div className="flex items-center space-x-2">
                    <h3
                      className={`text-base font-semibold ${
                        isDarkMode
                          ? "text-gray-100"
                          : isOceanBreeze
                            ? "text-teal-800"
                            : isSunsetGlow
                              ? "text-orange-800"
                              : isForestWhisper
                                ? "text-green-800"
                                : "text-gray-800"
                      }`}
                    >
                      {task.title}
                    </h3>
                    <span
                      className={`px-2 py-0.5 rounded-full text-xs font-medium ${
                        task.priority === "High"
                          ? isDarkMode
                            ? "bg-red-600 text-white"
                            : "bg-red-500 text-white"
                          : task.priority === "Medium"
                            ? isDarkMode
                              ? "bg-orange-600 text-white"
                              : "bg-orange-500 text-white"
                            : isDarkMode
                              ? "bg-green-600 text-white"
                              : "bg-green-500 text-white"
                      }`}
                    >
                      {task.priority}
                    </span>
                  </div>
                  <p
                    className={`text-sm flex items-center space-x-1 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                          ? "text-blue-600"
                          : isSunsetGlow
                            ? "text-orange-500"
                            : isForestWhisper
                              ? "text-green-500"
                              : "text-gray-500"
                    }`}
                  >
                    <Calendar className="w-4 h-4" />
                    <span>Due: {task.dueDate}</span>
                  </p>
                </div>
              </div>
            </div>
          ))}
          {upcomingTasks.length === 0 && (
            <div
              className={`py-10 text-center text-sm font-medium ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              No upcoming tasks. Add one below!
            </div>
          )}
        </div>
        <div
          className={`p-5 border-t ${
            isDarkMode
              ? "border-gray-600"
              : isOceanBreeze
                ? "border-teal-400"
                : isSunsetGlow
                  ? "border-orange-400"
                  : isForestWhisper
                    ? "border-green-400"
                    : "border-gray-300"
          }`}
        >
          <button
            onClick={() => setActiveTab("tasks")}
            className={`text-sm font-semibold flex items-center transition-all duration-200 transform hover:scale-105 ${
              isDarkMode
                ? "text-indigo-400 hover:text-indigo-300"
                : isOceanBreeze
                  ? "text-teal-600 hover:text-teal-500"
                  : isSunsetGlow
                    ? "text-orange-600 hover:text-orange-500"
                    : isForestWhisper
                      ? "text-green-600 hover:text-green-500"
                      : "text-indigo-600 hover:text-indigo-500"
            }`}
          >
            <PlusCircle className="w-5 h-5 mr-2" />
            Add New Task
          </button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
