import React, { useState } from "react";
import {
  Calendar,
  Clock,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
} from "lucide-react";
import { toast } from "react-toastify";

function Dashboard({
  tasks,
  studyStats,
  aiSuggestion,
  getStudyTips,
  generateSchedule,
  setActiveTab,
  toggleTaskComplete,
  deleteTask,
  currentTheme,
  logStudyHours,
}) {
  const [hoursInput, setHoursInput] = useState("");
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(null);

  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";
  const isSunsetGlow = currentTheme === "Sunset Glow";
  const isForestWhisper = currentTheme === "Forest Whisper";

  // Helper function to get local date in YYYY-MM-DD format
  const getLocalDateString = () => {
    const date = new Date();
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0"); // Months are 0-based
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  const today = getLocalDateString();

  const handleLogHours = () => {
    const hours = parseFloat(hoursInput);
    if (isNaN(hours) || hours <= 0) {
      toast.error("Please enter a valid number of hours (greater than 0).");
      return;
    }
    logStudyHours(hours);
    setHoursInput("");
    toast.success(`Logged ${hours} hours of study time!`);
  };

  const confirmDeleteTask = (id) => {
    setShowDeleteConfirm(id);
  };

  const handleDeleteTask = (id) => {
    deleteTask(id);
    setShowDeleteConfirm(null);
    toast.success("Task deleted successfully!");
  };

  const cancelDeleteTask = () => {
    setShowDeleteConfirm(null);
  };

  const incompleteTasks = tasks.filter((task) => !task.completed);
  const tasksDueToday = incompleteTasks.filter(
    (task) => task.dueDate === today
  );
  const overdueTasks = incompleteTasks.filter((task) => task.dueDate < today);
  const upcomingTasks = incompleteTasks
    .filter((task) => task.dueDate > today)
    .slice(0, 3);

  return (
    <div className="space-y-8">
      {/* AI Assistant Card */}
      <div
        className={`rounded-xl shadow-lg transition-all duration-300 ${
          isDarkMode
            ? "bg-gray-800 border border-gray-700"
            : isOceanBreeze
              ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
              : isSunsetGlow
                ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                : isForestWhisper
                  ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                  : "bg-white border border-gray-200"
        } p-6`}
      >
        <h2
          className={`text-xl font-bold mb-4 flex items-center tracking-tight ${
            isDarkMode
              ? "text-gray-100"
              : isOceanBreeze
                ? "text-teal-900"
                : isSunsetGlow
                  ? "text-orange-900"
                  : isForestWhisper
                    ? "text-green-900"
                    : "text-gray-900"
          }`}
        >
          <div
            className={`w-8 h-8 rounded-full flex items-center justify-center mr-3 shadow-sm ${
              isDarkMode
                ? "bg-indigo-900"
                : isOceanBreeze
                  ? "bg-teal-100"
                  : isSunsetGlow
                    ? "bg-orange-100"
                    : isForestWhisper
                      ? "bg-green-100"
                      : "bg-indigo-100"
            } transition-transform duration-300 hover:scale-110`}
          >
            <div
              className={`w-4 h-4 rounded-full ${
                isDarkMode
                  ? "bg-indigo-500"
                  : isOceanBreeze
                    ? "bg-teal-500"
                    : isSunsetGlow
                      ? "bg-orange-500"
                      : isForestWhisper
                        ? "bg-green-500"
                        : "bg-indigo-600"
              }`}
            ></div>
          </div>
          AI Study Assistant
        </h2>
        <div
          className={`p-5 rounded-xl border shadow-inner min-h-[120px] transition-all duration-300 ${
            isDarkMode
              ? "bg-indigo-900 border-indigo-800 text-gray-200"
              : isOceanBreeze
                ? "bg-teal-50 bg-opacity-90 backdrop-blur-md border-teal-400 text-teal-800"
                : isSunsetGlow
                  ? "bg-pink-50 bg-opacity-90 backdrop-blur-md border-orange-400 text-orange-800"
                  : isForestWhisper
                    ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md border-green-400 text-green-800"
                    : "bg-indigo-50 border-indigo-200 text-gray-800"
          }`}
        >
          {aiSuggestion ? (
            <div className="whitespace-pre-line text-sm leading-relaxed">
              {aiSuggestion}
            </div>
          ) : (
            <div className="flex items-center justify-center h-16">
              <div className="animate-pulse flex space-x-3">
                <div
                  className={`w-3 h-3 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                        ? "bg-teal-500"
                        : isSunsetGlow
                          ? "bg-orange-500"
                          : isForestWhisper
                            ? "bg-green-500"
                            : "bg-indigo-400"
                  }`}
                ></div>
                <div
                  className={`w-3 h-3 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                        ? "bg-teal-500"
                        : isSunsetGlow
                          ? "bg-orange-500"
                          : isForestWhisper
                            ? "bg-green-500"
                            : "bg-indigo-400"
                  }`}
                ></div>
                <div
                  className={`w-3 h-3 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                        ? "bg-teal-500"
                        : isSunsetGlow
                          ? "bg-orange-500"
                          : isForestWhisper
                            ? "bg-green-500"
                            : "bg-indigo-400"
                  }`}
                ></div>
              </div>
            </div>
          )}
        </div>
        <div className="mt-5 flex space-x-3">
          <button
            onClick={getStudyTips}
            className={`px-5 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
              isDarkMode
                ? "bg-indigo-600 hover:bg-indigo-700"
                : isOceanBreeze
                  ? "bg-teal-600 hover:bg-teal-700"
                  : isSunsetGlow
                    ? "bg-orange-600 hover:bg-orange-700"
                    : isForestWhisper
                      ? "bg-green-600 hover:bg-green-700"
                      : "bg-indigo-600 hover:bg-indigo-700"
            }`}
          >
            Get Study Tips
          </button>
          <button
            onClick={generateSchedule}
            className={`px-5 py-2 rounded-lg text-sm font-semibold shadow-md border transition-all duration-200 transform hover:scale-105 ${
              isDarkMode
                ? "bg-gray-700 border-gray-600 text-gray-200 hover:bg-gray-600"
                : isOceanBreeze
                  ? "bg-amber-50 bg-opacity-90 backdrop-blur-md border-teal-400 text-teal-800 hover:bg-amber-100"
                  : isSunsetGlow
                    ? "bg-pink-50 bg-opacity-90 backdrop-blur-md border-orange-400 text-orange-800 hover:bg-pink-100"
                    : isForestWhisper
                      ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md border-green-400 text-green-800 hover:bg-emerald-100"
                      : "bg-white border-gray-300 text-gray-700 hover:bg-gray-50"
            }`}
          >
            Generate Schedule
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div
          className={`rounded-xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl ${
            isDarkMode
              ? "bg-gray-800 border border-gray-700"
              : isOceanBreeze
                ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
                : isSunsetGlow
                  ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                  : isForestWhisper
                    ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                    : "bg-white border border-gray-200"
          }`}
        >
          <div className="flex justify-between items-center mb-4">
            <h3
              className={`text-sm font-medium tracking-wide ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              Study Hours
            </h3>
            <Clock
              className={`h-6 w-6 transition-transform duration-300 hover:scale-110 ${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                    ? "text-teal-600"
                    : isSunsetGlow
                      ? "text-orange-600"
                      : isForestWhisper
                        ? "text-green-600"
                        : "text-indigo-600"
              }`}
            />
          </div>
          <p
            className={`text-4xl font-extrabold tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            {studyStats.totalHours}h
          </p>
          <div className="flex mt-5 space-x-3">
            <input
              type="number"
              placeholder="Hours"
              className={`p-2 border rounded-lg w-24 text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
                isDarkMode
                  ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                  : isOceanBreeze
                    ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                    : isSunsetGlow
                      ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                      : isForestWhisper
                        ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                        : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
              }`}
              value={hoursInput}
              onChange={(e) => setHoursInput(e.target.value)}
              min="0"
              step="0.5"
            />
            <button
              onClick={handleLogHours}
              className={`px-5 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                isDarkMode
                  ? "bg-indigo-600 hover:bg-indigo-700"
                  : isOceanBreeze
                    ? "bg-teal-600 hover:bg-teal-700"
                    : isSunsetGlow
                      ? "bg-orange-600 hover:bg-orange-700"
                      : isForestWhisper
                        ? "bg-green-600 hover:bg-green-700"
                        : "bg-indigo-600 hover:bg-indigo-700"
              }`}
            >
              Log Hours
            </button>
          </div>
        </div>

        <div
          className={`rounded-xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl ${
            isDarkMode
              ? "bg-gray-800 border border-gray-700"
              : isOceanBreeze
                ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
                : isSunsetGlow
                  ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                  : isForestWhisper
                    ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                    : "bg-white border border-gray-200"
          }`}
        >
          <div className="flex justify-between items-center mb-4">
            <h3
              className={`text-sm font-medium tracking-wide ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              Tasks Completed
            </h3>
            <CheckCircle
              className={`h-6 w-6 transition-transform duration-300 hover:scale-110 ${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                    ? "text-teal-600"
                    : isSunsetGlow
                      ? "text-orange-600"
                      : isForestWhisper
                        ? "text-green-600"
                        : "text-indigo-600"
              }`}
            />
          </div>
          <p
            className={`text-4xl font-extrabold tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            {studyStats.completedTasks}
          </p>
        </div>

        <div
          className={`rounded-xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl ${
            isDarkMode
              ? "bg-gray-800 border border-gray-700"
              : isOceanBreeze
                ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
                : isSunsetGlow
                  ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                  : isForestWhisper
                    ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                    : "bg-white border border-gray-200"
          }`}
        >
          <div className="flex justify-between items-center mb-4">
            <h3
              className={`text-sm font-medium tracking-wide ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              Study Streak
            </h3>
            <Calendar
              className={`h-6 w-6 transition-transform duration-300 hover:scale-110 ${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                    ? "text-teal-600"
                    : isSunsetGlow
                      ? "text-orange-600"
                      : isForestWhisper
                        ? "text-green-600"
                        : "text-indigo-600"
              }`}
            />
          </div>
          <p
            className={`text-4xl font-extrabold tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            {studyStats.streak} days
          </p>
          <div className="flex space-x-2 mt-4">
            {[1, 2, 3, 4, 5, 6, 7].map((day) => (
              <div
                key={day}
                className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium shadow-sm transition-transform duration-300 hover:scale-110 ${
                  day <= studyStats.streak
                    ? isDarkMode
                      ? "bg-indigo-500 text-white"
                      : isOceanBreeze
                        ? "bg-teal-500 text-white"
                        : isSunsetGlow
                          ? "bg-orange-500 text-white"
                          : isForestWhisper
                            ? "bg-green-500 text-white"
                            : "bg-indigo-600 text-white"
                    : isDarkMode
                      ? "bg-gray-600 text-gray-400"
                      : isOceanBreeze
                        ? "bg-blue-200 text-blue-600"
                        : isSunsetGlow
                          ? "bg-orange-200 text-orange-600"
                          : isForestWhisper
                            ? "bg-green-200 text-green-600"
                            : "bg-gray-100 text-gray-500"
                }`}
              >
                {day}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Tasks Overview Section */}
      <div
        className={`rounded-xl shadow-lg transition-all duration-300 ${
          isDarkMode
            ? "bg-gray-800 border border-gray-700"
            : isOceanBreeze
              ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
              : isSunsetGlow
                ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
                : isForestWhisper
                  ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                  : "bg-white border border-gray-200"
        }`}
      >
        <div
          className={`p-5 border-b ${
            isDarkMode
              ? "border-gray-600"
              : isOceanBreeze
                ? "border-teal-400"
                : isSunsetGlow
                  ? "border-orange-400"
                  : isForestWhisper
                    ? "border-green-400"
                    : "border-gray-300"
          }`}
        >
          <h2
            className={`text-xl font-bold tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-900"
                  : isSunsetGlow
                    ? "text-orange-900"
                    : isForestWhisper
                      ? "text-green-900"
                      : "text-gray-900"
            }`}
          >
            Tasks Overview
          </h2>
        </div>

        {/* Overdue Tasks */}
        <div className="p-3 space-y-3">
          <h3
            className={`text-lg font-semibold mb-2 ${
              isDarkMode
                ? "text-red-400"
                : isOceanBreeze
                  ? "text-red-600"
                  : isSunsetGlow
                    ? "text-red-600"
                    : isForestWhisper
                      ? "text-red-600"
                      : "text-red-600"
            }`}
          >
            Overdue Tasks ({overdueTasks.length})
          </h3>
          {overdueTasks.length === 0 ? (
            <div
              className={`py-4 text-center text-sm font-medium ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              No overdue tasks.
            </div>
          ) : (
            overdueTasks.map((task) => (
              <div
                key={task.id}
                className={`p-3 rounded-lg flex justify-between items-center shadow-sm transition-all duration-300 hover:shadow-md hover:scale-[1.02] ${
                  isDarkMode
                    ? "hover:bg-gray-700"
                    : isOceanBreeze
                      ? "hover:bg-teal-50"
                      : isSunsetGlow
                        ? "hover:bg-orange-50"
                        : isForestWhisper
                          ? "hover:bg-green-50"
                          : "hover:bg-gray-50"
                } ${isDarkMode ? "" : "bg-red-50 bg-opacity-50"}`}
              >
                <div className="flex items-center">
                  <button
                    onClick={() => toggleTaskComplete(task.id)}
                    className={`w-6 h-6 rounded-full border mr-4 flex items-center justify-center shadow-sm transition-all duration-200 hover:scale-110 ${
                      task.priority === "High"
                        ? "border-red-500 hover:border-red-600"
                        : "border-yellow-500 hover:border-yellow-600"
                    }`}
                  >
                    {task.completed && (
                      <CheckCircle className="w-5 h-5 text-green-500" />
                    )}
                  </button>
                  <div>
                    <h3
                      className={`text-sm font-semibold ${
                        isDarkMode
                          ? "text-red-400"
                          : isOceanBreeze
                            ? "text-red-600"
                            : isSunsetGlow
                              ? "text-red-600"
                              : isForestWhisper
                                ? "text-red-600"
                                : "text-red-600"
                      }`}
                    >
                      {task.title}
                    </h3>
                    <span
                      className={`text-xs font-medium ${
                        isDarkMode
                          ? "text-gray-400"
                          : isOceanBreeze
                            ? "text-blue-600"
                            : isSunsetGlow
                              ? "text-orange-500"
                              : isForestWhisper
                                ? "text-green-500"
                                : "text-gray-500"
                      }`}
                    >
                      Due: {task.dueDate}
                    </span>
                  </div>
                </div>
                <div className="flex space-x-3">
                  <button
                    onClick={() => setActiveTab("tasks")}
                    className={`transition-all duration-200 transform hover:scale-110 ${
                      isDarkMode
                        ? "text-gray-400 hover:text-gray-200"
                        : isOceanBreeze
                          ? "text-blue-600 hover:text-teal-600"
                          : isSunsetGlow
                            ? "text-orange-500 hover:text-orange-600"
                            : isForestWhisper
                              ? "text-green-500 hover:text-green-600"
                              : "text-gray-400 hover:text-gray-600"
                    }`}
                  >
                    <Edit3 className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => confirmDeleteTask(task.id)}
                    className={`transition-all duration-200 transform hover:scale-110 ${
                      isDarkMode
                        ? "text-red-400 hover:text-red-500"
                        : isOceanBreeze
                          ? "text-red-600 hover:text-red-700"
                          : isSunsetGlow
                            ? "text-red-600 hover:text-red-700"
                            : isForestWhisper
                              ? "text-red-600 hover:text-red-700"
                              : "text-red-500 hover:text-red-600"
                    }`}
                  >
                    <Trash2 className="w-5 h-5" />
                  </button>
                </div>
                {showDeleteConfirm === task.id && (
                  <div
                    className={`mt-2 p-2 border-t ${
                      isDarkMode
                        ? "border-gray-600"
                        : isOceanBreeze
                          ? "border-teal-400"
                          : isSunsetGlow
                            ? "border-orange-400"
                            : isForestWhisper
                              ? "border-green-400"
                              : "border-gray-300"
                    }`}
                  >
                    <p
                      className={`text-sm ${
                        isDarkMode
                          ? "text-gray-300"
                          : isOceanBreeze
                            ? "text-teal-800"
                            : isSunsetGlow
                              ? "text-orange-800"
                              : isForestWhisper
                                ? "text-green-800"
                                : "text-gray-800"
                      }`}
                    >
                      Are you sure you want to delete this task?
                    </p>
                    <div className="mt-2 flex space-x-2">
                      <button
                        onClick={() => handleDeleteTask(task.id)}
                        className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                          isDarkMode
                            ? "bg-red-600 hover:bg-red-700"
                            : isOceanBreeze
                              ? "bg-red-600 hover:bg-red-700"
                              : isSunsetGlow
                                ? "bg-red-600 hover:bg-red-700"
                                : isForestWhisper
                                  ? "bg-red-600 hover:bg-red-700"
                                  : "bg-red-600 hover:bg-red-700"
                        }`}
                      >
                        Yes, Delete
                      </button>
                      <button
                        onClick={cancelDeleteTask}
                        className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                          isDarkMode
                            ? "bg-gray-600 hover:bg-gray-700"
                            : isOceanBreeze
                              ? "bg-blue-500 hover:bg-blue-600"
                              : isSunsetGlow
                                ? "bg-pink-500 hover:bg-pink-600"
                                : isForestWhisper
                                  ? "bg-emerald-500 hover:bg-emerald-600"
                                  : "bg-gray-500 hover:bg-gray-600"
                        }`}
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ))
          )}
        </div>

        {/* Tasks Due Today */}
        <div className="p-3 space-y-3">
          <h3
            className={`text-lg font-semibold mb-2 ${
              isDarkMode
                ? "text-orange-400"
                : isOceanBreeze
                  ? "text-orange-500"
                  : isSunsetGlow
                    ? "text-orange-600"
                    : isForestWhisper
                      ? "text-orange-500"
                      : "text-orange-600"
            }`}
          >
            Due Today ({tasksDueToday.length})
          </h3>
          {tasksDueToday.length === 0 ? (
            <div
              className={`py-4 text-center text-sm font-medium ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              No tasks due today.
            </div>
          ) : (
            tasksDueToday.map((task) => (
              <div
                key={task.id}
                className={`p-3 rounded-lg flex justify-between items-center shadow-sm transition-all duration-300 hover:shadow-md hover:scale-[1.02] ${
                  isDarkMode
                    ? "hover:bg-gray-700"
                    : isOceanBreeze
                      ? "hover:bg-teal-50"
                      : isSunsetGlow
                        ? "hover:bg-orange-50"
                        : isForestWhisper
                          ? "hover:bg-green-50"
                          : "hover:bg-gray-50"
                } ${isDarkMode ? "" : "bg-orange-50 bg-opacity-50"}`}
              >
                <div className="flex items-center">
                  <button
                    onClick={() => toggleTaskComplete(task.id)}
                    className={`w-6 h-6 rounded-full border mr-4 flex items-center justify-center shadow-sm transition-all duration-200 hover:scale-110 ${
                      task.priority === "High"
                        ? "border-red-500 hover:border-red-600"
                        : "border-yellow-500 hover:border-yellow-600"
                    }`}
                  >
                    {task.completed && (
                      <CheckCircle className="w-5 h-5 text-green-500" />
                    )}
                  </button>
                  <div>
                    <h3
                      className={`text-sm font-semibold ${
                        isDarkMode
                          ? "text-orange-400"
                          : isOceanBreeze
                            ? "text-orange-500"
                            : isSunsetGlow
                              ? "text-orange-600"
                              : isForestWhisper
                                ? "text-orange-500"
                                : "text-orange-600"
                      }`}
                    >
                      {task.title}
                    </h3>
                    <span
                      className={`text-xs font-medium ${
                        isDarkMode
                          ? "text-gray-400"
                          : isOceanBreeze
                            ? "text-blue-600"
                            : isSunsetGlow
                              ? "text-orange-500"
                              : isForestWhisper
                                ? "text-green-500"
                                : "text-gray-500"
                      }`}
                    >
                      Due: {task.dueDate}
                    </span>
                  </div>
                </div>
                <div className="flex space-x-3">
                  <button
                    onClick={() => setActiveTab("tasks")}
                    className={`transition-all duration-200 transform hover:scale-110 ${
                      isDarkMode
                        ? "text-gray-400 hover:text-gray-200"
                        : isOceanBreeze
                          ? "text-blue-600 hover:text-teal-600"
                          : isSunsetGlow
                            ? "text-orange-500 hover:text-orange-600"
                            : isForestWhisper
                              ? "text-green-500 hover:text-green-600"
                              : "text-gray-400 hover:text-gray-600"
                    }`}
                  >
                    <Edit3 className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => confirmDeleteTask(task.id)}
                    className={`transition-all duration-200 transform hover:scale-110 ${
                      isDarkMode
                        ? "text-red-400 hover:text-red-500"
                        : isOceanBreeze
                          ? "text-red-600 hover:text-red-700"
                          : isSunsetGlow
                            ? "text-red-600 hover:text-red-700"
                            : isForestWhisper
                              ? "text-red-600 hover:text-red-700"
                              : "text-red-500 hover:text-red-600"
                    }`}
                  >
                    <Trash2 className="w-5 h-5" />
                  </button>
                </div>
                {showDeleteConfirm === task.id && (
                  <div
                    className={`mt-2 p-2 border-t ${
                      isDarkMode
                        ? "border-gray-600"
                        : isOceanBreeze
                          ? "border-teal-400"
                          : isSunsetGlow
                            ? "border-orange-400"
                            : isForestWhisper
                              ? "border-green-400"
                              : "border-gray-300"
                    }`}
                  >
                    <p
                      className={`text-sm ${
                        isDarkMode
                          ? "text-gray-300"
                          : isOceanBreeze
                            ? "text-teal-800"
                            : isSunsetGlow
                              ? "text-orange-800"
                              : isForestWhisper
                                ? "text-green-800"
                                : "text-gray-800"
                      }`}
                    >
                      Are you sure you want to delete this task?
                    </p>
                    <div className="mt-2 flex space-x-2">
                      <button
                        onClick={() => handleDeleteTask(task.id)}
                        className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                          isDarkMode
                            ? "bg-red-600 hover:bg-red-700"
                            : isOceanBreeze
                              ? "bg-red-600 hover:bg-red-700"
                              : isSunsetGlow
                                ? "bg-red-600 hover:bg-red-700"
                                : isForestWhisper
                                  ? "bg-red-600 hover:bg-red-700"
                                  : "bg-red-600 hover:bg-red-700"
                        }`}
                      >
                        Yes, Delete
                      </button>
                      <button
                        onClick={cancelDeleteTask}
                        className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                          isDarkMode
                            ? "bg-gray-600 hover:bg-gray-700"
                            : isOceanBreeze
                              ? "bg-blue-500 hover:bg-blue-600"
                              : isSunsetGlow
                                ? "bg-pink-500 hover:bg-pink-600"
                                : isForestWhisper
                                  ? "bg-emerald-500 hover:bg-emerald-600"
                                  : "bg-gray-500 hover:bg-gray-600"
                        }`}
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ))
          )}
        </div>

        {/* Upcoming Tasks */}
        <div className="p-3 space-y-3">
          <h3
            className={`text-lg font-semibold mb-2 ${
              isDarkMode
                ? "text-green-400"
                : isOceanBreeze
                  ? "text-green-600"
                  : isSunsetGlow
                    ? "text-green-600"
                    : isForestWhisper
                      ? "text-green-500"
                      : "text-green-600"
            }`}
          >
            Upcoming Tasks ({upcomingTasks.length})
          </h3>
          {upcomingTasks.length === 0 ? (
            <div
              className={`py-4 text-center text-sm font-medium ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              No upcoming tasks. Add one below!
            </div>
          ) : (
            upcomingTasks.map((task) => (
              <div
                key={task.id}
                className={`p-3 rounded-lg flex justify-between items-center shadow-sm transition-all duration-300 hover:shadow-md hover:scale-[1.02] ${
                  isDarkMode
                    ? "hover:bg-gray-700"
                    : isOceanBreeze
                      ? "hover:bg-teal-50"
                      : isSunsetGlow
                        ? "hover:bg-orange-50"
                        : isForestWhisper
                          ? "hover:bg-green-50"
                          : "hover:bg-gray-50"
                } ${isDarkMode ? "" : "bg-green-50 bg-opacity-50"}`}
              >
                <div className="flex items-center">
                  <button
                    onClick={() => toggleTaskComplete(task.id)}
                    className={`w-6 h-6 rounded-full border mr-4 flex items-center justify-center shadow-sm transition-all duration-200 hover:scale-110 ${
                      task.priority === "High"
                        ? "border-red-500 hover:border-red-600"
                        : "border-yellow-500 hover:border-yellow-600"
                    }`}
                  >
                    {task.completed && (
                      <CheckCircle className="w-5 h-5 text-green-500" />
                    )}
                  </button>
                  <div>
                    <h3
                      className={`text-sm font-semibold ${
                        isDarkMode
                          ? "text-green-400"
                          : isOceanBreeze
                            ? "text-green-600"
                            : isSunsetGlow
                              ? "text-green-600"
                              : isForestWhisper
                                ? "text-green-500"
                                : "text-green-600"
                      }`}
                    >
                      {task.title}
                    </h3>
                    <span
                      className={`text-xs font-medium ${
                        isDarkMode
                          ? "text-gray-400"
                          : isOceanBreeze
                            ? "text-blue-600"
                            : isSunsetGlow
                              ? "text-orange-500"
                              : isForestWhisper
                                ? "text-green-500"
                                : "text-gray-500"
                      }`}
                    >
                      Due: {task.dueDate}
                    </span>
                  </div>
                </div>
                <div className="flex space-x-3">
                  <button
                    onClick={() => setActiveTab("tasks")}
                    className={`transition-all duration-200 transform hover:scale-110 ${
                      isDarkMode
                        ? "text-gray-400 hover:text-gray-200"
                        : isOceanBreeze
                          ? "text-blue-600 hover:text-teal-600"
                          : isSunsetGlow
                            ? "text-orange-500 hover:text-orange-600"
                            : isForestWhisper
                              ? "text-green-500 hover:text-green-600"
                              : "text-gray-400 hover:text-gray-600"
                    }`}
                  >
                    <Edit3 className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => confirmDeleteTask(task.id)}
                    className={`transition-all duration-200 transform hover:scale-110 ${
                      isDarkMode
                        ? "text-red-400 hover:text-red-500"
                        : isOceanBreeze
                          ? "text-red-600 hover:text-red-700"
                          : isSunsetGlow
                            ? "text-red-600 hover:text-red-700"
                            : isForestWhisper
                              ? "text-red-600 hover:text-red-700"
                              : "text-red-500 hover:text-red-600"
                    }`}
                  >
                    <Trash2 className="w-5 h-5" />
                  </button>
                </div>
                {showDeleteConfirm === task.id && (
                  <div
                    className={`mt-2 p-2 border-t ${
                      isDarkMode
                        ? "border-gray-600"
                        : isOceanBreeze
                          ? "border-teal-400"
                          : isSunsetGlow
                            ? "border-orange-400"
                            : isForestWhisper
                              ? "border-green-400"
                              : "border-gray-300"
                    }`}
                  >
                    <p
                      className={`text-sm ${
                        isDarkMode
                          ? "text-gray-300"
                          : isOceanBreeze
                            ? "text-teal-800"
                            : isSunsetGlow
                              ? "text-orange-800"
                              : isForestWhisper
                                ? "text-green-800"
                                : "text-gray-800"
                      }`}
                    >
                      Are you sure you want to delete this task?
                    </p>
                    <div className="mt-2 flex space-x-2">
                      <button
                        onClick={() => handleDeleteTask(task.id)}
                        className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                          isDarkMode
                            ? "bg-red-600 hover:bg-red-700"
                            : isOceanBreeze
                              ? "bg-red-600 hover:bg-red-700"
                              : isSunsetGlow
                                ? "bg-red-600 hover:bg-red-700"
                                : isForestWhisper
                                  ? "bg-red-600 hover:bg-red-700"
                                  : "bg-red-600 hover:bg-red-700"
                        }`}
                      >
                        Yes, Delete
                      </button>
                      <button
                        onClick={cancelDeleteTask}
                        className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                          isDarkMode
                            ? "bg-gray-600 hover:bg-gray-700"
                            : isOceanBreeze
                              ? "bg-blue-500 hover:bg-blue-600"
                              : isSunsetGlow
                                ? "bg-pink-500 hover:bg-pink-600"
                                : isForestWhisper
                                  ? "bg-emerald-500 hover:bg-emerald-600"
                                  : "bg-gray-500 hover:bg-gray-600"
                        }`}
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                )}
              </div>
            ))
          )}
        </div>

        <div
          className={`p-5 border-t ${
            isDarkMode
              ? "border-gray-600"
              : isOceanBreeze
                ? "border-teal-400"
                : isSunsetGlow
                  ? "border-orange-400"
                  : isForestWhisper
                    ? "border-green-400"
                    : "border-gray-300"
          }`}
        >
          <button
            onClick={() => setActiveTab("tasks")}
            className={`text-sm font-semibold flex items-center transition-all duration-200 transform hover:scale-105 ${
              isDarkMode
                ? "text-indigo-400 hover:text-indigo-300"
                : isOceanBreeze
                  ? "text-teal-600 hover:text-teal-500"
                  : isSunsetGlow
                    ? "text-orange-600 hover:text-orange-500"
                    : isForestWhisper
                      ? "text-green-600 hover:text-green-500"
                      : "text-indigo-600 hover:text-indigo-500"
            }`}
          >
            <PlusCircle className="w-5 h-5 mr-2" />
            Add New Task
          </button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
