import React, { useState, useEffect } from "react";
import { BookOpen } from "lucide-react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSun,
  faMoon,
  faWater,
  faFire,
  faLeaf,
} from "@fortawesome/free-solid-svg-icons";
import Dashboard from "./Dashboard";
import TaskManager from "./TaskManager";
import Analytics from "./Analytics";
import Rewards from "./Rewards";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Layout() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [tasks, setTasks] = useState([]);
  const [studyStats, setStudyStats] = useState({
    totalHours: 0,
    completedTasks: 0,
    streak: 0,
    lastActiveDate: null,
    lastStreakUpdate: null,
    studyHoursLog: [],
  });
  const [aiSuggestion, setAiSuggestion] = useState("");
  const [points, setPoints] = useState(0);
  const [badges, setBadges] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("Light Mode");
  const [unlockedThemes, setUnlockedThemes] = useState(["Light Mode"]);
  const [loading, setLoading] = useState(true);

  const userId = "user123";

  // Helper function to format the local date as YYYY-MM-DD
  const getLocalDateString = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0"); // getMonth() is 0-based
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  // Theme styles for all themes
  const themeStyles = {
    "Light Mode": "bg-gray-100 text-gray-900",
    "Dark Mode": "bg-gray-900 text-gray-100",
    "Ocean Breeze": "bg-gradient-to-br from-blue-50 to-teal-100 text-blue-900",
    "Sunset Glow":
      "bg-gradient-to-br from-orange-50 to-pink-100 text-orange-900 backdrop-blur-sm",
    "Forest Whisper":
      "bg-gradient-to-br from-green-50 to-emerald-100 text-green-900 backdrop-blur-sm",
  };

  const headerStyles = {
    "Light Mode": "bg-indigo-600 text-white shadow-lg",
    "Dark Mode": "bg-indigo-900 text-gray-100 shadow-lg",
    "Ocean Breeze":
      "bg-gradient-to-r from-blue-600 to-teal-600 text-white shadow-lg",
    "Sunset Glow":
      "bg-gradient-to-r from-orange-600 to-pink-600 text-white shadow-lg",
    "Forest Whisper":
      "bg-gradient-to-r from-green-600 to-teal-600 text-white shadow-lg",
  };

  const navStyles = {
    "Light Mode": "bg-white text-gray-600 border-b border-gray-200",
    "Dark Mode": "bg-gray-800 text-gray-300 border-b border-gray-700",
    "Ocean Breeze": "bg-blue-100 text-blue-800 border-b border-teal-300",
    "Sunset Glow": "bg-orange-50 text-orange-800 border-b border-orange-300",
    "Forest Whisper": "bg-green-50 text-green-800 border-b border-green-300",
  };

  // Map themes to their FontAwesome icons
  const themeIcons = {
    "Light Mode": faSun,
    "Dark Mode": faMoon,
    "Ocean Breeze": faWater,
    "Sunset Glow": faFire,
    "Forest Whisper": faLeaf,
  };

  const getNextTheme = () => {
    if (unlockedThemes.length === 1) {
      return "Light Mode";
    }
    const currentIndex = unlockedThemes.indexOf(currentTheme);
    const nextIndex = (currentIndex + 1) % unlockedThemes.length;
    return unlockedThemes[nextIndex];
  };

  const handleThemeChange = () => {
    const nextTheme = getNextTheme();
    setCurrentTheme(nextTheme);
    toast.info(`ðŸŽ¨ Switched to ${nextTheme}!`);
  };

  const handlePointsUpdate = (newPoints) => {
    setPoints(newPoints);
  };

  const handleThemeUnlock = (themeName) => {
    if (!unlockedThemes.includes(themeName)) {
      setUnlockedThemes([...unlockedThemes, themeName]);
    }
  };

  const updateStreak = (today) => {
    setStudyStats((prev) => {
      const lastActive = prev.lastActiveDate
        ? new Date(prev.lastActiveDate)
        : null;
      const lastStreakUpdate = prev.lastStreakUpdate
        ? new Date(prev.lastStreakUpdate)
        : null;
      const todayDate = new Date(today);

      let newStreak = prev.streak;
      let newLastActiveDate = today;
      let newLastStreakUpdate = prev.lastStreakUpdate;

      if (!lastActive || lastActive.toISOString().split("T")[0] !== today) {
        if (lastActive) {
          const diffTime = todayDate - lastActive;
          const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
          if (diffDays === 1) {
            newStreak = prev.streak + 1;
            newLastStreakUpdate = today;
          } else if (diffDays > 1) {
            newStreak = 1;
            newLastStreakUpdate = today;
          }
        } else {
          newStreak = 1;
          newLastStreakUpdate = today;
        }
      }

      if (
        lastStreakUpdate &&
        new Date(lastStreakUpdate).toISOString().split("T")[0] !== today
      ) {
        const diffTime = todayDate - new Date(lastStreakUpdate);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
        if (diffDays > 1) {
          newStreak = 1;
          newLastStreakUpdate = today;
        }
      }

      const newStats = {
        ...prev,
        streak: newStreak,
        lastActiveDate: newLastActiveDate,
        lastStreakUpdate: newLastStreakUpdate,
        studyHoursLog: prev.studyHoursLog || [],
      };
      console.log("Updated studyStats (updateStreak):", newStats);
      return newStats;
    });
  };

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/user/${userId}`
        );
        if (!response.ok) {
          throw new Error(`Failed to fetch user data: ${response.status}`);
        }
        const data = await response.json();
        console.log("Fetched data:", data);

        // Ensure tasks have pointsAwarded and hours properties
        const fetchedTasks = (data.tasks || []).map((task) => ({
          ...task,
          pointsAwarded: task.pointsAwarded ?? false,
          hours: task.hours ?? 0, // Default to 0 if hours is not present
        }));
        setTasks(fetchedTasks);

        const fetchedStats = {
          totalHours: data.studyStats?.totalHours || 0,
          completedTasks: data.studyStats?.completedTasks || 0,
          streak: data.studyStats?.streak || 0,
          lastActiveDate: data.studyStats?.lastActiveDate || null,
          lastStreakUpdate: data.studyStats?.lastStreakUpdate || null,
          studyHoursLog: data.studyStats?.studyHoursLog || [],
        };
        console.log("Setting initial studyStats:", fetchedStats);
        setStudyStats(fetchedStats);
        setPoints(data.points || 0);
        setBadges(data.badges || []);
        setCurrentTheme(data.currentTheme || "Light Mode");
        setUnlockedThemes(data.unlockedThemes || ["Light Mode"]);

        const today = getLocalDateString(new Date());
        console.log("Today's date:", today);
        const { lastActiveDate, streak, lastStreakUpdate } = fetchedStats;

        let newStats = { ...fetchedStats };
        if (lastStreakUpdate !== today) {
          if (!lastActiveDate) {
            console.log("No lastActiveDate, initializing with today's date...");
            newStats = {
              ...fetchedStats,
              lastActiveDate: today,
              lastStreakUpdate: today,
              studyHoursLog: fetchedStats.studyHoursLog || [],
            };
          } else {
            const lastDate = new Date(lastActiveDate);
            const currentDate = new Date(today);
            const diffDays = (currentDate - lastDate) / (1000 * 60 * 60 * 24);
            console.log("Days difference:", diffDays);

            if (diffDays === 1) {
              newStats = {
                ...fetchedStats,
                streak: fetchedStats.streak + 1,
                lastActiveDate: today,
                lastStreakUpdate: today,
                studyHoursLog: fetchedStats.studyHoursLog || [],
              };
              console.log("Updated studyStats (increment streak):", newStats);
            } else if (diffDays > 1) {
              newStats = {
                ...fetchedStats,
                streak: 0,
                lastActiveDate: today,
                lastStreakUpdate: today,
                studyHoursLog: fetchedStats.studyHoursLog || [],
              };
              console.log("Updated studyStats (reset streak):", newStats);
            } else {
              newStats = {
                ...fetchedStats,
                lastStreakUpdate: today,
                studyHoursLog: fetchedStats.studyHoursLog || [],
              };
              console.log("Updated studyStats (same day):", newStats);
            }
          }
          setStudyStats(newStats);
        } else {
          console.log("Streak already updated today, skipping...");
        }

        const lastFetchTimestamp = localStorage.getItem("lastFetchTimestamp");
        const currentTimestamp = Date.now();
        localStorage.setItem("lastFetchTimestamp", currentTimestamp.toString());

        const lastActive = fetchedStats.lastActiveDate
          ? new Date(fetchedStats.lastActiveDate).getTime()
          : 0;
        const timeSinceLastFetch = lastFetchTimestamp
          ? currentTimestamp - parseInt(lastFetchTimestamp)
          : Infinity;
        const timeSinceLastActive = lastActive
          ? currentTimestamp - lastActive
          : Infinity;

        setTimeout(() => {
          const incompleteTasks = fetchedTasks.filter(
            (task) => !task.completed
          );
          if (incompleteTasks.length === 0) {
            setAiSuggestion(
              "You have no incomplete tasks! Add a new task to keep progressing."
            );
            return;
          }

          const todayStr = getLocalDateString(new Date());
          const priorityValue = { High: 3, Medium: 2, Low: 1 };

          const tasksDueToday = [];
          const pastDueTasks = [];
          const futureTasks = [];

          incompleteTasks.forEach((task) => {
            const taskDueDateStr = task.dueDate;
            const diffDays =
              (new Date(taskDueDateStr) - new Date(todayStr)) /
              (1000 * 60 * 60 * 24);

            if (taskDueDateStr === todayStr) {
              tasksDueToday.push(task);
            } else if (diffDays < 0) {
              pastDueTasks.push({ ...task, dueDateDiff: Math.abs(diffDays) });
            } else {
              futureTasks.push({ ...task, dueDateDiff: diffDays });
            }
          });

          tasksDueToday.sort(
            (a, b) => priorityValue[b.priority] - priorityValue[a.priority]
          );
          pastDueTasks.sort((a, b) => {
            if (a.dueDateDiff !== b.dueDateDiff) {
              return a.dueDateDiff - b.dueDateDiff;
            }
            return priorityValue[b.priority] - priorityValue[a.priority];
          });
          futureTasks.sort((a, b) => {
            if (a.dueDateDiff !== b.dueDateDiff) {
              return a.dueDateDiff - b.dueDateDiff;
            }
            return priorityValue[b.priority] - priorityValue[a.priority];
          });

          let recommendedTask = null;

          if (tasksDueToday.length > 0) {
            recommendedTask = tasksDueToday[0];
          } else if (pastDueTasks.length > 0) {
            recommendedTask = pastDueTasks[0];
          } else if (futureTasks.length > 0) {
            recommendedTask = futureTasks[0];
          }

          if (recommendedTask) {
            setAiSuggestion(
              `Based on your schedule and learning patterns, I recommend focusing on your "${recommendedTask.title}" today.`
            );
          } else {
            setAiSuggestion(
              "You have no incomplete tasks! Add a new task to keep progressing."
            );
          }
        }, 1000);

        setLoading(false);
      } catch (err) {
        console.error("Error fetching user data:", err);
        toast.error("Failed to load user data. Please try again later.");
        setAiSuggestion("Add a new task to get started!");
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    console.log("Current studyStats (after update):", studyStats);
  }, [studyStats]);

  useEffect(() => {
    const completedCount = tasks.filter((task) => task.completed).length;
    setStudyStats((prevStats) => {
      const newStats = {
        ...prevStats,
        completedTasks: completedCount,
        studyHoursLog: prevStats.studyHoursLog || [],
      };
      console.log("Updated studyStats (completedTasks):", newStats);
      return newStats;
    });
  }, [tasks]);

  useEffect(() => {
    const saveUserData = async () => {
      try {
        const serializedStudyStats = {
          totalHours: studyStats.totalHours,
          completedTasks: studyStats.completedTasks,
          streak: studyStats.streak,
          lastActiveDate: studyStats.lastActiveDate || null,
          lastStreakUpdate: studyStats.lastStreakUpdate || null,
          studyHoursLog: studyStats.studyHoursLog || [],
        };

        const dataToSend = {
          tasks,
          studyStats: serializedStudyStats,
          points,
          badges,
          themes: [
            "Light Mode",
            "Dark Mode",
            "Ocean Breeze",
            "Sunset Glow",
            "Forest Whisper",
          ],
          currentTheme,
          unlockedThemes,
        };

        console.log(
          "Sending data to backend:",
          JSON.stringify(dataToSend, null, 2)
        );

        const response = await fetch(
          `http://localhost:5000/api/user/${userId}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(dataToSend),
          }
        );

        console.log("Response status:", response.status);
        const responseBody = await response.text();
        console.log("Response body:", responseBody);

        if (!response.ok) {
          throw new Error(
            `Failed to save user data: ${response.status} - ${responseBody}`
          );
        }

        console.log("Data saved successfully");
      } catch (err) {
        console.error("Error saving user data:", err);
        toast.error(`Failed to save progress: ${err.message}`);
      }
    };

    const debounceSave = setTimeout(() => {
      saveUserData();
    }, 1000);

    return () => clearTimeout(debounceSave);
  }, [tasks, studyStats, points, badges, currentTheme, unlockedThemes]);

  const logStudyHours = (hours) => {
    const parsedHours = parseFloat(hours);
    if (isNaN(parsedHours) || parsedHours <= 0) {
      toast.error("Please enter a valid number of hours greater than 0.");
      return;
    }
    const roundedHours = Math.round(parsedHours * 100) / 100;
    const today = getLocalDateString(new Date());
    setStudyStats((prev) => {
      const studyHoursLog = prev.studyHoursLog || [];
      const existingLog = studyHoursLog.find((log) => log.date === today);
      let updatedLog;
      if (existingLog) {
        updatedLog = studyHoursLog.map((log) =>
          log.date === today
            ? {
                ...log,
                hours: Math.round((log.hours + roundedHours) * 100) / 100,
              }
            : log
        );
      } else {
        updatedLog = [...studyHoursLog, { date: today, hours: roundedHours }];
      }
      const newStats = {
        ...prev,
        totalHours: Math.round((prev.totalHours + roundedHours) * 100) / 100,
        studyHoursLog: updatedLog,
      };
      console.log("Updated studyStats (logStudyHours):", newStats);
      return newStats;
    });
    updateStreak(today);
  };

  const deductStudyHours = (hours, completedDate) => {
    const parsedHours = parseFloat(hours);
    if (isNaN(parsedHours) || parsedHours <= 0) return;
    const roundedHours = Math.round(parsedHours * 100) / 100;
    setStudyStats((prev) => {
      const studyHoursLog = prev.studyHoursLog || [];
      const logDate = completedDate; // Use the task's completedDate
      const existingLog = studyHoursLog.find((log) => log.date === logDate);
      let updatedLog;
      if (existingLog) {
        const newHours = Math.max(0, existingLog.hours - roundedHours);
        updatedLog = studyHoursLog
          .map((log) =>
            log.date === logDate
              ? { ...log, hours: Math.round(newHours * 100) / 100 }
              : log
          )
          .filter((log) => log.hours > 0); // Remove entries with 0 hours
      } else {
        updatedLog = studyHoursLog;
      }
      const newTotalHours = Math.max(0, prev.totalHours - roundedHours);
      const newStats = {
        ...prev,
        totalHours: Math.round(newTotalHours * 100) / 100,
        studyHoursLog: updatedLog,
      };
      console.log("Updated studyStats (deductStudyHours):", newStats);
      return newStats;
    });
  };

  const getStudyTips = () => {
    setAiSuggestion("Loading new suggestions...");
    setTimeout(() => {
      const tips = [
        "Try using the Pomodoro technique: 25 minutes of focused study followed by a 5-minute break.",
        "For difficult subjects, try teaching the material to an imaginary student.",
        "Create visual mind maps to connect key concepts in your subjects.",
        "Review your notes within 24 hours of taking them to improve retention.",
        "Study in short, frequent sessions rather than long cramming sessions.",
      ];
      const randomTip = tips[Math.floor(Math.random() * tips.length)];
      setAiSuggestion(randomTip);
    }, 1500);
  };

  const generateSchedule = () => {
    setAiSuggestion("Generating personalized study schedule...");
    setTimeout(() => {
      const today = new Date();
      const todayStr = getLocalDateString(today);
      const days = [
        "Sunday",
        "Monday",
        "Tuesday",
        "Wednesday",
        "Thursday",
        "Friday",
        "Saturday",
      ];
      const todayDay = days[today.getDay()];

      const incompleteTasks = tasks.filter((task) => !task.completed);
      if (incompleteTasks.length === 0) {
        setAiSuggestion(
          "You have no incomplete tasks to schedule. Add a new task to get started!"
        );
        return;
      }

      const tasksDueToday = [];
      const pastDueTasks = [];
      const futureTasksDueTomorrow = [];
      const otherFutureTasks = [];

      incompleteTasks.forEach((task) => {
        const taskDueDate = new Date(task.dueDate);
        const taskDueDateStr = task.dueDate;
        const diffDays =
          (new Date(taskDueDateStr) - new Date(todayStr)) /
          (1000 * 60 * 60 * 24);

        if (taskDueDateStr === todayStr) {
          tasksDueToday.push(task);
        } else if (diffDays < 0) {
          pastDueTasks.push({ ...task, dueDateDiff: Math.abs(diffDays) });
        } else {
          if (diffDays === 1) {
            futureTasksDueTomorrow.push({ ...task, dueDateDiff: diffDays });
          } else {
            otherFutureTasks.push({ ...task, dueDateDiff: diffDays });
          }
        }
      });

      const priorityValue = { High: 3, Medium: 2, Low: 1 };

      tasksDueToday.sort(
        (a, b) => priorityValue[b.priority] - priorityValue[a.priority]
      );

      pastDueTasks.sort((a, b) => {
        if (a.dueDateDiff !== b.dueDateDiff) {
          return a.dueDateDiff - b.dueDateDiff;
        }
        return priorityValue[b.priority] - priorityValue[a.priority];
      });

      futureTasksDueTomorrow.sort(
        (a, b) => priorityValue[b.priority] - priorityValue[a.priority]
      );

      otherFutureTasks.sort((a, b) => {
        if (a.dueDateDiff !== b.dueDateDiff) {
          return a.dueDateDiff - b.dueDateDiff;
        }
        return priorityValue[b.priority] - priorityValue[a.priority];
      });

      const schedule = {
        today: [],
        futureDays: [],
      };

      const getHours = (priority) => {
        return priority === "High" ? 2 : priority === "Medium" ? 1.5 : 1;
      };

      tasksDueToday.forEach((task) => {
        const hours = task.hours || getHours(task.priority); // Use task.hours if available
        schedule.today.push(`${task.title} (${hours} hours)`);
      });

      pastDueTasks.forEach((task) => {
        const hours = task.hours || getHours(task.priority);
        schedule.today.push(`${task.title} (${hours} hours)`);
      });

      futureTasksDueTomorrow.forEach((task) => {
        const hours = task.hours || getHours(task.priority);
        schedule.today.push(`${task.title} (${hours} hours)`);
      });

      otherFutureTasks.forEach((task) => {
        const taskDueDate = new Date(task.dueDate);
        const diffDays = task.dueDateDiff;
        const hours = task.hours || getHours(task.priority);

        const scheduleDate = new Date(taskDueDate);
        scheduleDate.setDate(taskDueDate.getDate() - 1);
        const scheduleDateStr = scheduleDate.toISOString().split("T")[0];
        const scheduleDay = days[scheduleDate.getDay()];

        let dayEntry = schedule.futureDays.find(
          (entry) =>
            entry.dayName === scheduleDay && entry.date === scheduleDateStr
        );
        if (!dayEntry) {
          dayEntry = { dayName: scheduleDay, date: scheduleDateStr, tasks: [] };
          schedule.futureDays.push(dayEntry);
        }
        dayEntry.tasks.push(`${task.title} (${hours} hours)`);
      });

      schedule.futureDays.sort((a, b) => new Date(a.date) - new Date(b.date));

      const scheduleMessage = [
        "Based on your upcoming tasks and past performance, here's a suggested schedule:\n",
      ];

      if (schedule.today.length > 0) {
        scheduleMessage.push(`- ${todayDay} (Today):`);
        schedule.today.forEach((taskEntry) => {
          scheduleMessage.push(`  - ${taskEntry}`);
        });
      }

      schedule.futureDays.forEach((day) => {
        scheduleMessage.push(`- ${day.dayName} (${day.date}):`);
        day.tasks.forEach((taskEntry) => {
          scheduleMessage.push(`  - ${taskEntry}`);
        });
      });

      if (schedule.today.length === 0 && schedule.futureDays.length === 0) {
        scheduleMessage.push("No tasks to schedule for the immediate future.");
      }

      setAiSuggestion(scheduleMessage.join("\n"));
    }, 2000);
  };

  const addTask = (newTask) => {
    const task = {
      id: Date.now(),
      ...newTask,
      completedDate: null,
      pointsAwarded: false,
      hours: newTask.hours || 0, // Ensure hours is included
    };
    setTasks([...tasks, task]);
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const toggleTaskComplete = (id) => {
    const task = tasks.find((task) => task.id === id);
    if (!task) return;

    const today = getLocalDateString(new Date());
    let newPoints = points;
    let newBadges = [...badges];
    let notifications = [];

    const updatedTasks = tasks.map((t) =>
      t.id === id
        ? {
            ...t,
            completed: !t.completed,
            completedDate: !t.completed ? today : null,
            pointsAwarded: t.pointsAwarded,
          }
        : t
    );

    const updatedTask = updatedTasks.find((t) => t.id === id);
    const pointsToAddOrDeduct =
      task.priority === "High" ? 30 : task.priority === "Medium" ? 20 : 10;

    if (!task.completed) {
      // Task is being marked as completed
      if (!updatedTask.pointsAwarded) {
        // Only award points if they haven't been awarded yet
        newPoints = points + pointsToAddOrDeduct;
        notifications.push(`ðŸŽ‰ +${pointsToAddOrDeduct} Points! Keep it up!`);
        updatedTask.pointsAwarded = true; // Mark points as awarded
      }

      // Log the task's hours when completed
      if (task.hours > 0) {
        logStudyHours(task.hours);
      }

      const highPriorityTasks = updatedTasks.filter(
        (t) => t.priority === "High" && t.completed
      ).length;
      if (highPriorityTasks >= 5 && !badges.includes("Priority Master")) {
        newBadges.push("Priority Master");
        notifications.push("ðŸ† Badge Earned: Priority Master!");
      }

      const totalCompletedTasks = updatedTasks.filter(
        (t) => t.completed
      ).length;
      if (totalCompletedTasks >= 10 && !badges.includes("Task Titan")) {
        newBadges.push("Task Titan");
        notifications.push("ðŸ† Badge Earned: Task Titan!");
      }

      const dueDate = new Date(task.dueDate);
      const todayDate = new Date();
      if (dueDate > todayDate) {
        const earlyTasks = updatedTasks.filter(
          (t) => new Date(t.dueDate) > todayDate && t.completed
        ).length;
        if (earlyTasks >= 3 && !badges.includes("Early Bird")) {
          newBadges.push("Early Bird");
          notifications.push("ðŸ† Badge Earned: Early Bird!");
        }
      }

      updateStreak(today);
    } else {
      // Task is being unmarked as completed
      if (updatedTask.pointsAwarded) {
        // Only deduct points if they were previously awarded
        newPoints = points - pointsToAddOrDeduct;
        notifications.push(
          `â†©ï¸ -${pointsToAddOrDeduct} Points. Task unmarked as completed.`
        );
        updatedTask.pointsAwarded = false; // Reset pointsAwarded
      }

      // Deduct the task's hours when uncompleted
      if (task.hours > 0 && task.completedDate) {
        deductStudyHours(task.hours, task.completedDate);
      }
    }

    setTasks(updatedTasks);
    setPoints(newPoints);
    setBadges(newBadges);
    notifications.forEach((msg) => toast.success(msg));
  };

  const updateTask = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task.id === updatedTask.id ? updatedTask : task))
    );
  };

  useEffect(() => {
    if (studyStats.streak >= 7 && !badges.includes("Streak Star")) {
      let newBadges = [...badges, "Streak Star"];
      let newPoints = points + 50;

      setBadges(newBadges);
      setPoints(newPoints);

      toast.success("ðŸ† Badge Earned: Streak Star!");
      toast.success("ðŸŽ‰ +50 Points for 7-Day Streak!");
    }
  }, [studyStats.streak]);

  const nextTheme = getNextTheme();

  return (
    <div className={`min-h-screen flex flex-col ${themeStyles[currentTheme]}`}>
      <ToastContainer />
      <header className={`p-4 ${headerStyles[currentTheme]}`}>
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <h1 className="text-2xl font-bold flex items-center">
              <BookOpen className="w-8 h-8 mr-3" />
              StudyBuddy AI Planner
            </h1>
            <button
              onClick={handleThemeChange}
              className="text-2xl p-2 rounded-full transition-all duration-200 hover:scale-110"
            >
              <FontAwesomeIcon icon={themeIcons[nextTheme]} />
            </button>
          </div>
          <div
            className={`px-4 py-2 rounded-lg text-sm font-medium ${
              currentTheme === "Dark Mode"
                ? "bg-indigo-800 text-gray-200"
                : "bg-white bg-opacity-20 text-white"
            }`}
          >
            {new Date().toLocaleDateString("en-US", {
              weekday: "long",
              year: "numeric",
              month: "long",
              day: "numeric",
            })}
          </div>
        </div>
      </header>

      <nav className={`sticky top-0 z-10 ${navStyles[currentTheme]}`}>
        <div className="container mx-auto">
          <div className="flex justify-around md:justify-start md:space-x-8">
            {["dashboard", "tasks", "analytics", "rewards"].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`px-4 py-3 font-semibold text-sm transition-all duration-300 relative ${
                  activeTab === tab
                    ? currentTheme === "Dark Mode"
                      ? "text-indigo-400 border-b-2 border-indigo-400"
                      : currentTheme === "Ocean Breeze"
                        ? "text-teal-600 border-b-2 border-teal-600"
                        : currentTheme === "Sunset Glow"
                          ? "text-orange-600 border-b-2 border-orange-600"
                          : currentTheme === "Forest Whisper"
                            ? "text-green-600 border-b-2 border-green-600"
                            : "text-indigo-600 border-b-2 border-indigo-600"
                    : currentTheme === "Dark Mode"
                      ? "text-gray-400 hover:text-gray-200"
                      : currentTheme === "Ocean Breeze"
                        ? "text-blue-700 hover:text-teal-600"
                        : currentTheme === "Sunset Glow"
                          ? "text-orange-700 hover:text-orange-600"
                          : currentTheme === "Forest Whisper"
                            ? "text-green-700 hover:text-green-600"
                            : "text-gray-600 hover:text-gray-800"
                }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
                {activeTab === tab && (
                  <span className="absolute inset-x-0 bottom-0 h-0.5 bg-current transform scale-x-100 transition-transform duration-300" />
                )}
              </button>
            ))}
          </div>
        </div>
      </nav>

      <main className="flex-grow container mx-auto p-4">
        {loading ? (
          <div className="text-center py-8">
            <div className="animate-pulse flex justify-center space-x-3">
              <div
                className={`w-5 h-5 rounded-full ${
                  currentTheme === "Dark Mode"
                    ? "bg-indigo-500"
                    : currentTheme === "Ocean Breeze"
                      ? "bg-teal-500"
                      : currentTheme === "Sunset Glow"
                        ? "bg-orange-500"
                        : currentTheme === "Forest Whisper"
                          ? "bg-green-500"
                          : "bg-indigo-600"
                }`}
              ></div>
              <div
                className={`w-5 h-5 rounded-full ${
                  currentTheme === "Dark Mode"
                    ? "bg-indigo-500"
                    : currentTheme === "Ocean Breeze"
                      ? "bg-teal-500"
                      : currentTheme === "Sunset Glow"
                        ? "bg-orange-500"
                        : currentTheme === "Forest Whisper"
                          ? "bg-green-500"
                          : "bg-indigo-600"
                }`}
              ></div>
              <div
                className={`w-5 h-5 rounded-full ${
                  currentTheme === "Dark Mode"
                    ? "bg-indigo-500"
                    : currentTheme === "Ocean Breeze"
                      ? "bg-teal-500"
                      : currentTheme === "Sunset Glow"
                        ? "bg-orange-500"
                        : currentTheme === "Forest Whisper"
                          ? "bg-green-500"
                          : "bg-indigo-600"
                }`}
              ></div>
            </div>
            <p
              className={`mt-3 text-sm font-medium ${
                currentTheme === "Dark Mode"
                  ? "text-gray-400"
                  : currentTheme === "Ocean Breeze"
                    ? "text-blue-700"
                    : currentTheme === "Sunset Glow"
                      ? "text-orange-700"
                      : currentTheme === "Forest Whisper"
                        ? "text-green-700"
                        : "text-gray-600"
              }`}
            >
              Loading your study data...
            </p>
          </div>
        ) : (
          <div className="space-y-6">
            {activeTab === "dashboard" && (
              <Dashboard
                tasks={tasks}
                studyStats={studyStats}
                aiSuggestion={aiSuggestion}
                getStudyTips={getStudyTips}
                generateSchedule={generateSchedule}
                setActiveTab={setActiveTab}
                toggleTaskComplete={toggleTaskComplete}
                deleteTask={deleteTask}
                currentTheme={currentTheme}
                logStudyHours={logStudyHours}
              />
            )}

            {activeTab === "tasks" && (
              <TaskManager
                tasks={tasks}
                addTask={addTask}
                updateTask={updateTask}
                deleteTask={deleteTask}
                toggleTaskComplete={toggleTaskComplete}
                currentTheme={currentTheme}
              />
            )}

            {activeTab === "analytics" && (
              <Analytics tasks={tasks} currentTheme={currentTheme} />
            )}

            {activeTab === "rewards" && (
              <Rewards
                points={points}
                badges={badges}
                studyStats={studyStats}
                currentTheme={currentTheme}
                onPointsUpdate={handlePointsUpdate}
                onThemeUnlock={handleThemeUnlock}
                unlockedThemes={unlockedThemes}
                tasks={tasks}
              />
            )}
          </div>
        )}
      </main>

      <footer className={`py-4 ${themeStyles[currentTheme]}`}>
        <div className="container mx-auto text-center">
          <p
            className={`text-sm ${
              currentTheme === "Dark Mode"
                ? "text-gray-500"
                : currentTheme === "Ocean Breeze"
                  ? "text-blue-700"
                  : currentTheme === "Sunset Glow"
                    ? "text-orange-700"
                    : currentTheme === "Forest Whisper"
                      ? "text-green-700"
                      : "text-gray-600"
            }`}
          >
            Â© 2025 StudyBuddy AI Planner. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
}

export default Layout;
