import React, { useEffect, useRef } from "react";
import Chart from "chart.js/auto";

function Analytics({ tasks, currentTheme }) {
  const priorityChartRef = useRef(null);
  const studyHoursChartRef = useRef(null);
  let priorityChartInstance = useRef(null);
  let studyHoursChartInstance = useRef(null);

  // Calculate analytics data
  const completedTasks = tasks.filter((task) => task.completed).length;
  const totalStudyHours = tasks.reduce(
    (total, task) =>
      task.completed
        ? total +
          (task.priority === "High" ? 2 : task.priority === "Medium" ? 1.5 : 1)
        : total,
    0
  );

  const priorityData = {
    High: tasks.filter((task) => task.priority === "High" && task.completed)
      .length,
    Medium: tasks.filter((task) => task.priority === "Medium" && task.completed)
      .length,
    Low: tasks.filter((task) => task.priority === "Low" && task.completed)
      .length,
  };

  // Mock study hours data for the past 7 days (since we don't have a study log yet)
  const today = new Date();
  const studyHoursData = Array(7)
    .fill(0)
    .map((_, index) => {
      const date = new Date(today);
      date.setDate(today.getDate() - (6 - index));
      const tasksOnDate = tasks.filter((task) => {
        const taskDate = new Date(task.dueDate);
        return (
          task.completed &&
          taskDate.toISOString().split("T")[0] ===
            date.toISOString().split("T")[0]
        );
      });
      return tasksOnDate.reduce(
        (total, task) =>
          total +
          (task.priority === "High" ? 2 : task.priority === "Medium" ? 1.5 : 1),
        0
      );
    });

  const labels = Array(7)
    .fill(0)
    .map((_, index) => {
      const date = new Date(today);
      date.setDate(today.getDate() - (6 - index));
      return date.toISOString().split("T")[0];
    });

  useEffect(() => {
    // Destroy existing charts if they exist
    if (priorityChartInstance.current) {
      priorityChartInstance.current.destroy();
    }
    if (studyHoursChartInstance.current) {
      studyHoursChartInstance.current.destroy();
    }

    // Priority Chart
    const priorityCtx = priorityChartRef.current.getContext("2d");
    priorityChartInstance.current = new Chart(priorityCtx, {
      type: "bar",
      data: {
        labels: ["High", "Medium", "Low"],
        datasets: [
          {
            label: "Completed Tasks by Priority",
            data: [priorityData.High, priorityData.Medium, priorityData.Low],
            backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
            borderColor: ["#FF6384", "#36A2EB", "#FFCE56"],
            borderWidth: 1,
          },
        ],
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
            title: {
              display: true,
              text: "Number of Tasks",
            },
          },
        },
        plugins: {
          legend: {
            display: false,
          },
        },
      },
    });

    // Study Hours Chart
    const studyHoursCtx = studyHoursChartRef.current.getContext("2d");
    studyHoursChartInstance.current = new Chart(studyHoursCtx, {
      type: "line",
      data: {
        labels: labels,
        datasets: [
          {
            label: "Study Hours",
            data: studyHoursData,
            fill: false,
            borderColor: "#36A2EB",
            tension: 0.1,
          },
        ],
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
            title: {
              display: true,
              text: "Hours",
            },
          },
          x: {
            title: {
              display: true,
              text: "Date",
            },
          },
        },
      },
    });

    return () => {
      if (priorityChartInstance.current) {
        priorityChartInstance.current.destroy();
      }
      if (studyHoursChartInstance.current) {
        studyHoursChartInstance.current.destroy();
      }
    };
  }, [tasks, priorityData, studyHoursData, labels]);

  const themeStyles = {
    "Light Mode": "bg-white text-gray-800",
    "Dark Mode": "bg-gray-800 text-gray-200",
    "Ocean Breeze": "bg-blue-100 text-blue-900",
  };

  return (
    <div className={`p-6 rounded-lg shadow-lg ${themeStyles[currentTheme]}`}>
      <h2 className="text-2xl font-bold mb-6">Analytics</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <div className="p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold">Total Completed Tasks</h3>
          <p className="text-3xl">{completedTasks}</p>
        </div>
        <div className="p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold">Total Study Hours</h3>
          <p className="text-3xl">{totalStudyHours}</p>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-4">
            Completed Tasks by Priority
          </h3>
          <canvas ref={priorityChartRef}></canvas>
        </div>
        <div className="p-4 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-4">
            Study Hours (Last 7 Days)
          </h3>
          <canvas ref={studyHoursChartRef}></canvas>
        </div>
      </div>
    </div>
  );
}

export default Analytics;
