import React, { useState } from "react";
import {
  Calendar,
  Clock,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
} from "lucide-react";

function Dashboard({
  tasks,
  studyStats,
  aiSuggestion,
  getStudyTips,
  generateSchedule,
  setActiveTab,
  toggleTaskComplete,
  deleteTask,
  currentTheme,
  logStudyHours, // New prop
}) {
  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";
  const upcomingTasks = tasks.filter((task) => !task.completed).slice(0, 3);

  const [hoursInput, setHoursInput] = useState("");

  const handleLogHours = () => {
    const hours = parseFloat(hoursInput);
    if (isNaN(hours) || hours <= 0) {
      return; // Error handling is managed in Layout.jsx
    }
    logStudyHours(hours);
    setHoursInput("");
  };

  return (
    <div className="space-y-6">
      {/* AI Assistant Card */}
      <div
        className={`rounded-lg shadow ${
          isDarkMode
            ? "bg-gray-700"
            : isOceanBreeze
            ? "bg-opacity-70 backdrop-blur-lg"
            : "bg-white"
        } p-6`}
      >
        <h2
          className={`text-lg font-semibold mb-4 flex items-center ${
            isDarkMode
              ? "text-gray-200"
              : isOceanBreeze
              ? "text-teal-800"
              : "text-gray-800"
          }`}
        >
          <div
            className={`w-8 h-8 rounded-full flex items-center justify-center mr-2 ${
              isDarkMode
                ? "bg-indigo-900"
                : isOceanBreeze
                ? "bg-teal-100"
                : "bg-indigo-100"
            }`}
          >
            <div
              className={`w-4 h-4 rounded-full ${
                isDarkMode
                  ? "bg-indigo-500"
                  : isOceanBreeze
                  ? "bg-teal-500"
                  : "bg-indigo-600"
              }`}
            ></div>
          </div>
          AI Study Assistant
        </h2>
        <div
          className={`p-4 rounded-lg border min-h-[100px] ${
            isDarkMode
              ? "bg-indigo-900 border-indigo-800"
              : isOceanBreeze
              ? "bg-teal-100 bg-opacity-70 backdrop-blur-sm border-teal-300"
              : "bg-indigo-50 border-indigo-100"
          }`}
        >
          {aiSuggestion ? (
            <div className="whitespace-pre-line">{aiSuggestion}</div>
          ) : (
            <div className="flex items-center justify-center h-16">
              <div className="animate-pulse flex space-x-2">
                <div
                  className={`w-2 h-2 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                      ? "bg-teal-500"
                      : "bg-indigo-400"
                  }`}
                ></div>
                <div
                  className={`w-2 h-2 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                      ? "bg-teal-500"
                      : "bg-indigo-400"
                  }`}
                ></div>
                <div
                  className={`w-2 h-2 rounded-full ${
                    isDarkMode
                      ? "bg-indigo-500"
                      : isOceanBreeze
                      ? "bg-teal-500"
                      : "bg-indigo-400"
                  }`}
                ></div>
              </div>
            </div>
          )}
        </div>
        <div className="mt-4 flex space-x-2">
          <button
            onClick={getStudyTips}
            className={`px-4 py-2 rounded-lg text-sm ${
              isDarkMode
                ? "bg-indigo-500 text-white"
                : isOceanBreeze
                ? "bg-teal-500 text-white"
                : "bg-indigo-600 text-white"
            }`}
          >
            Get Study Tips
          </button>
          <button
            onClick={generateSchedule}
            className={`px-4 py-2 rounded-lg text-sm ${
              isDarkMode
                ? "bg-gray-600 border-gray-500 text-gray-200"
                : isOceanBreeze
                ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm border-teal-300 text-teal-800"
                : "bg-white border border-gray-300 text-gray-700"
            }`}
          >
            Generate Schedule
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div
          className={`rounded-lg shadow p-6 ${
            isDarkMode
              ? "bg-gray-700"
              : isOceanBreeze
              ? "bg-opacity-70 backdrop-blur-lg"
              : "bg-white"
          }`}
        >
          <div className="flex justify-between items-center">
            <h3
              className={`${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                  ? "text-blue-600"
                  : "text-gray-500"
              } text-sm`}
            >
              Study Hours
            </h3>
            <Clock
              className={`${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                  ? "text-teal-600"
                  : "text-indigo-600"
              } h-5 w-5`}
            />
          </div>
          <p
            className={`text-3xl font-bold mt-2 ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            {studyStats.totalHours}h
          </p>
          <div className="flex mt-4 space-x-2">
            <input
              type="number"
              placeholder="Hours"
              className={`p-2 border rounded w-24 ${
                isDarkMode
                  ? "bg-gray-800 border-gray-500 text-gray-200"
                  : isOceanBreeze
                  ? "bg-blue-100 border-teal-300 text-teal-800"
                  : "bg-white border-gray-300 text-gray-800"
              }`}
              value={hoursInput}
              onChange={(e) => setHoursInput(e.target.value)}
              min="0"
              step="0.5"
            />
            <button
              onClick={handleLogHours}
              className={`px-4 py-2 rounded-lg text-sm ${
                isDarkMode
                  ? "bg-indigo-500 text-white"
                  : isOceanBreeze
                  ? "bg-teal-500 text-white"
                  : "bg-indigo-600 text-white"
              }`}
            >
              Log Hours
            </button>
          </div>
        </div>

        <div
          className={`rounded-lg shadow p-6 ${
            isDarkMode
              ? "bg-gray-700"
              : isOceanBreeze
              ? "bg-opacity-70 backdrop-blur-lg"
              : "bg-white"
          }`}
        >
          <div className="flex justify-between items-center">
            <h3
              className={`${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                  ? "text-blue-600"
                  : "text-gray-500"
              } text-sm`}
            >
              Tasks Completed
            </h3>
            <CheckCircle
              className={`${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                  ? "text-teal-600"
                  : "text-indigo-600"
              } h-5 w-5`}
            />
          </div>
          <p
            className={`text-3xl font-bold mt-2 ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            {studyStats.completedTasks}
          </p>
          <p className="text-green-600 text-sm mt-2">â†‘ 3 from last week</p>
        </div>

        <div
          className={`rounded-lg shadow p-6 ${
            isDarkMode
              ? "bg-gray-700"
              : isOceanBreeze
              ? "bg-opacity-70 backdrop-blur-lg"
              : "bg-white"
          }`}
        >
          <div className="flex justify-between items-center">
            <h3
              className={`${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                  ? "text-blue-600"
                  : "text-gray-500"
              } text-sm`}
            >
              Study Streak
            </h3>
            <Calendar
              className={`${
                isDarkMode
                  ? "text-indigo-400"
                  : isOceanBreeze
                  ? "text-teal-600"
                  : "text-indigo-600"
              } h-5 w-5`}
            />
          </div>
          <p
            className={`text-3xl font-bold mt-2 ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            {studyStats.streak} days
          </p>
          <div className="flex space-x-1 mt-2">
            {[1, 2, 3, 4, 5, 6, 7].map((day) => (
              <div
                key={day}
                className={`w-6 h-6 rounded-full flex items-center justify-center text-xs
                ${
                  day <= studyStats.streak
                    ? isDarkMode
                      ? "bg-indigo-500 text-white"
                      : isOceanBreeze
                      ? "bg-teal-500 text-white"
                      : "bg-indigo-600 text-white"
                    : isDarkMode
                    ? "bg-gray-600"
                    : isOceanBreeze
                    ? "bg-blue-200"
                    : "bg-gray-100"
                }`}
              >
                {day}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Upcoming Tasks */}
      <div
        className={`rounded-lg shadow ${
          isDarkMode
            ? "bg-gray-700"
            : isOceanBreeze
            ? "bg-opacity-70 backdrop-blur-lg"
            : "bg-white"
        }`}
      >
        <div
          className={`p-4 border-b ${
            isDarkMode
              ? "border-gray-600"
              : isOceanBreeze
              ? "border-teal-300"
              : "border-gray-200"
          }`}
        >
          <h2
            className={`font-semibold ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            Upcoming Tasks
          </h2>
        </div>
        <div className="p-2">
          {upcomingTasks.map((task) => (
            <div
              key={task.id}
              className={`p-2 rounded-md flex justify-between items-center ${
                isDarkMode
                  ? "hover:bg-gray-600"
                  : isOceanBreeze
                  ? "hover:bg-teal-100"
                  : "hover:bg-gray-50"
              }`}
            >
              <div className="flex items-center">
                <button
                  onClick={() => toggleTaskComplete(task.id)}
                  className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center
                  ${
                    task.priority === "High"
                      ? "border-red-500"
                      : "border-yellow-500"
                  }`}
                >
                  {task.completed && (
                    <CheckCircle className="w-4 h-4 text-green-600" />
                  )}
                </button>
                <div>
                  <h3
                    className={`text-sm font-medium ${
                      isDarkMode
                        ? "text-gray-200"
                        : isOceanBreeze
                        ? "text-teal-800"
                        : "text-gray-800"
                    }`}
                  >
                    {task.title}
                  </h3>
                  <span
                    className={`${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                        ? "text-blue-600"
                        : "text-gray-500"
                    } text-xs`}
                  >
                    Due: {task.dueDate}
                  </span>
                </div>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => setActiveTab("tasks")}
                  className={`${
                    isDarkMode
                      ? "text-gray-500 hover:text-gray-300"
                      : isOceanBreeze
                      ? "text-blue-600 hover:text-teal-600"
                      : "text-gray-400 hover:text-gray-600"
                  }`}
                >
                  <Edit3 className="w-4 h-4" />
                </button>
                <button
                  onClick={() => deleteTask(task.id)}
                  className={`${
                    isDarkMode
                      ? "text-gray-500 hover:text-gray-300"
                      : isOceanBreeze
                      ? "text-blue-600 hover:text-teal-600"
                      : "text-gray-400 hover:text-gray-600"
                  }`}
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            </div>
          ))}
          {upcomingTasks.length === 0 && (
            <div
              className={`py-8 text-center ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                  ? "text-blue-600"
                  : "text-gray-500"
              }`}
            >
              No upcoming tasks. Add one below!
            </div>
          )}
        </div>
        <div
          className={`p-4 border-t ${
            isDarkMode
              ? "border-gray-600"
              : isOceanBreeze
              ? "border-teal-300"
              : "border-gray-200"
          }`}
        >
          <button
            onClick={() => setActiveTab("tasks")}
            className={`${
              isDarkMode
                ? "text-indigo-400"
                : isOceanBreeze
                ? "text-teal-600"
                : "text-indigo-600"
            } text-sm font-medium flex items-center`}
          >
            <PlusCircle className="w-4 h-4 mr-1" />
            Add New Task
          </button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
