import React from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";

// Setup the localizer for react-big-calendar
const localizer = momentLocalizer(moment);

function CalendarView({ tasks, scheduleEvents, handleEventDrop }) {
  // Map tasks to calendar events
  const taskEvents = tasks.map((task) => ({
    id: task.id,
    title: task.title,
    start: new Date(task.dueDate),
    end: new Date(task.dueDate),
    allDay: true,
    priority: task.priority,
    completed: task.completed,
  }));

  // Combine task events and AI-scheduled events
  const events = [...taskEvents, ...scheduleEvents];

  // Custom event styling
  const eventStyleGetter = (event) => {
    let backgroundColor = "#3174ad"; // Default blue
    if (event.isSchedule) {
      backgroundColor = "#10b981"; // Green for AI-scheduled events
    } else if (event.priority === "High") {
      backgroundColor = "#ef4444"; // Red for high priority
    } else if (event.priority === "Medium") {
      backgroundColor = "#f59e0b"; // Yellow for medium priority
    }

    const style = {
      backgroundColor,
      borderRadius: "5px",
      opacity: event.completed ? 0.5 : 1,
      color: "white",
      border: "0px",
      display: "block",
    };
    return { style };
  };

  return (
    <div className="bg-white rounded-lg shadow">
      <div className="p-4 border-b border-gray-200">
        <h2 className="font-semibold">Task Calendar</h2>
      </div>
      <div className="p-4">
        <Calendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 600 }}
          eventPropGetter={eventStyleGetter}
          onEventDrop={handleEventDrop}
          draggableAccessor={(event) => !event.isSchedule} // Only allow dragging task events
          defaultView="month"
          views={["month", "week", "day"]}
          popup
        />
      </div>
    </div>
  );
}

export default CalendarView;
