import React, { useState, useEffect } from "react";
import { BookOpen } from "lucide-react";
import Dashboard from "./Dashboard";
import TaskManager from "./TaskManager";
import Analytics from "./Analytics";
import Login from "./Login";

function Layout() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [tasks, setTasks] = useState([]);
  const [studyStats, setStudyStats] = useState({
    totalHours: 0,
    completedTasks: 0,
    streak: 0,
  });
  const [aiSuggestion, setAiSuggestion] = useState("");
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // Check if user is logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      fetch("http://localhost:5000/api/user/profile", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((data) => {
          if (data.email) {
            setUser(data);
            setStudyStats(data.studyStats);
          } else {
            localStorage.removeItem("token");
            setUser(null);
          }
        })
        .catch(() => {
          localStorage.removeItem("token");
          setUser(null);
        })
        .finally(() => setIsLoading(false));
    } else {
      setIsLoading(false);
    }
  }, []);

  // Fetch tasks when user is logged in
  useEffect(() => {
    if (user) {
      const token = localStorage.getItem("token");
      fetch("http://localhost:5000/api/tasks", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((data) => setTasks(data))
        .catch((err) => console.error("Error fetching tasks:", err));
    }
  }, [user]);

  // Simulate AI suggestion
  useEffect(() => {
    if (user) {
      setTimeout(() => {
        setAiSuggestion(
          "Based on your schedule and learning patterns, I recommend focusing on your Math Assignment today. You tend to work best on math problems in the afternoon."
        );
      }, 1000);
    }
  }, [user]);

  // Get AI study tips
  const getStudyTips = () => {
    setAiSuggestion("Loading new suggestions...");
    setTimeout(() => {
      const tips = [
        "Try using the Pomodoro technique: 25 minutes of focused study followed by a 5-minute break.",
        "For difficult subjects, try teaching the material to an imaginary student to improve your understanding.",
        "Create visual mind maps to connect key concepts in your subjects.",
        "Review your notes within 24 hours of taking them to improve retention.",
        "Study in short, frequent sessions rather than long cramming sessions.",
      ];
      const randomTip = tips[Math.floor(Math.random() * tips.length)];
      setAiSuggestion(randomTip);
    }, 1500);
  };

  // Generate AI schedule
  const generateSchedule = () => {
    setAiSuggestion("Generating personalized study schedule...");
    setTimeout(() => {
      const schedule =
        "Based on your upcoming tasks and past performance, here's a suggested schedule:\n\n" +
        "- Tuesday (Today): Math Assignment (2 hours) in the afternoon\n" +
        "- Wednesday: Chemistry Notes review (1 hour) in the morning\n" +
        "- Thursday: Start Research Paper Outline (1.5 hours)\n" +
        "- Friday: Complete remaining tasks and review progress";
      setAiSuggestion(schedule);
    }, 2000);
  };

  // Task CRUD operations
  const addTask = async (newTask) => {
    const token = localStorage.getItem("token");
    const res = await fetch("http://localhost:5000/api/tasks", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(newTask),
    });
    const task = await res.json();
    setTasks([...tasks, task]);
  };

  const deleteTask = async (id) => {
    const token = localStorage.getItem("token");
    await fetch(`http://localhost:5000/api/tasks/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter((task) => task._id !== id));
  };

  const toggleTaskComplete = async (id) => {
    const task = tasks.find((t) => t._id === id);
    const updatedTask = { ...task, completed: !task.completed };
    const token = localStorage.getItem("token");
    await fetch(`http://localhost:5000/api/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(updatedTask),
    });
    setTasks(tasks.map((t) => (t._id === id ? updatedTask : t)));
  };

  const updateTask = async (updatedTask) => {
    const token = localStorage.getItem("token");
    await fetch(`http://localhost:5000/api/tasks/${updatedTask._id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(updatedTask),
    });
    setTasks(
      tasks.map((task) => (task._id === updatedTask._id ? updatedTask : task))
    );
  };

  // Update study stats
  useEffect(() => {
    if (user) {
      const completedCount = tasks.filter((task) => task.completed).length;
      const updatedStats = { ...studyStats, completedTasks: completedCount };
      setStudyStats(updatedStats);
      // Update stats in backend
      fetch("http://localhost:5000/api/user/profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ studyStats: updatedStats }),
      });
    }
  }, [tasks, user]);

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        Loading...
      </div>
    );
  }

  if (!user) {
    return <Login onLogin={(userData) => setUser(userData)} />;
  }

  return (
    <div className="min-h-screen bg-slate-50 text-gray-800">
      {/* Header */}
      <header className="bg-indigo-600 text-white p-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold flex items-center">
            <BookOpen className="mr-2" />
            StudyBuddy AI Planner
          </h1>
          <div className="flex items-center space-x-4">
            <div className="bg-indigo-700 px-4 py-2 rounded-lg text-sm">
              {new Date().toLocaleDateString("en-US", {
                weekday: "long",
                year: "numeric",
                month: "long",
                day: "numeric",
              })}
            </div>
            <button
              onClick={() => {
                localStorage.removeItem("token");
                setUser(null);
              }}
              className="bg-red-500 px-4 py-2 rounded-lg text-sm"
            >
              Logout
            </button>
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white shadow">
        <div className="container mx-auto">
          <div className="flex">
            <button
              onClick={() => setActiveTab("dashboard")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "dashboard"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Dashboard
            </button>
            <button
              onClick={() => setActiveTab("tasks")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "tasks"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Tasks
            </button>
            <button
              onClick={() => setActiveTab("analytics")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "analytics"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Analytics
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="container mx-auto p-4">
        {activeTab === "dashboard" && (
          <Dashboard
            tasks={tasks}
            studyStats={studyStats}
            aiSuggestion={aiSuggestion}
            getStudyTips={getStudyTips}
            generateSchedule={generateSchedule}
            setActiveTab={setActiveTab}
            toggleTaskComplete={toggleTaskComplete}
            deleteTask={deleteTask}
          />
        )}

        {activeTab === "tasks" && (
          <TaskManager
            tasks={tasks}
            addTask={addTask}
            updateTask={updateTask}
            deleteTask={deleteTask}
            toggleTaskComplete={toggleTaskComplete}
          />
        )}

        {activeTab === "analytics" && <Analytics tasks={tasks} />}
      </main>
    </div>
  );
}

export default Layout;
