import React, { useState } from "react";
import { Calendar } from "lucide-react";

function Dashboard({
  tasks,
  studyStats,
  aiSuggestion,
  getStudyTips,
  generateSchedule,
  setActiveTab,
  toggleTaskComplete,
  deleteTask,
  themeConfig,
  logStudyHours,
}) {
  const [isLoadingTips, setIsLoadingTips] = useState(false);
  const [isLoadingSchedule, setIsLoadingSchedule] = useState(false);
  const [hoursInput, setHoursInput] = useState("");
  const [hoursError, setHoursError] = useState("");

  const today = new Date().toISOString().split("T")[0];

  const priorityValue = { High: 3, Medium: 2, Low: 1 };
  const upcomingTasks = tasks
    .filter((task) => !task.completed)
    .sort((a, b) => {
      const dateDiff = new Date(a.dueDate) - new Date(b.dueDate);
      if (dateDiff !== 0) return dateDiff;
      return priorityValue[b.priority] - priorityValue[a.priority];
    })
    .slice(0, 3);

  const handleGetStudyTips = () => {
    setIsLoadingTips(true);
    getStudyTips();
    setTimeout(() => setIsLoadingTips(false), 1500);
  };

  const handleGenerateSchedule = () => {
    setIsLoadingSchedule(true);
    generateSchedule();
    setTimeout(() => setIsLoadingSchedule(false), 2000);
  };

  const handleLogHours = () => {
    const hours = parseFloat(hoursInput);
    if (isNaN(hours) || hours <= 0) {
      setHoursError("Please enter a valid number of hours greater than 0.");
      return;
    }
    setHoursError("");
    logStudyHours(hours);
    setHoursInput("");
  };

  return (
    <div className="space-y-6">
      <div className={`rounded-lg shadow ${themeConfig.background} p-6`}>
        <h2
          className={`text-lg font-semibold mb-4 flex items-center ${themeConfig.text}`}
        >
          <div
            className={`w-8 h-8 rounded-full flex items-center justify-center mr-2 ${themeConfig.secondaryBg}`}
          >
            <div
              className={`w-4 h-4 rounded-full ${themeConfig.primary}`}
            ></div>
          </div>
          AI Study Assistant
        </h2>
        <div
          className={`p-4 rounded-lg border min-h-[100px] ${themeConfig.secondaryBg} ${themeConfig.border}`}
        >
          {aiSuggestion ? (
            <div className="whitespace-pre-line">{aiSuggestion}</div>
          ) : (
            <div className="flex items-center justify-center h-16">
              <div className="animate-pulse flex space-x-2">
                <div
                  className={`w-2 h-2 rounded-full ${themeConfig.primary}`}
                ></div>
                <div
                  className={`w-2 h-2 rounded-full ${themeConfig.primary}`}
                ></div>
                <div
                  className={`w-2 h-2 rounded-full ${themeConfig.primary}`}
                ></div>
              </div>
            </div>
          )}
        </div>
        <div className="mt-4 flex space-x-2">
          <button
            onClick={handleGetStudyTips}
            disabled={isLoadingTips}
            className={`px-4 py-2 rounded-lg text-sm flex items-center ${
              isLoadingTips
                ? "bg-gray-400 cursor-not-allowed"
                : `${themeConfig.primary} ${themeConfig.primaryText} ${themeConfig.primaryHover}`
            } transition-colors duration-200`}
          >
            {isLoadingTips ? (
              <>
                <svg
                  className="animate-spin h-5 w-5 mr-2 text-white"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
                Loading...
              </>
            ) : (
              "Get Study Tips"
            )}
          </button>
          <button
            onClick={handleGenerateSchedule}
            disabled={isLoadingSchedule}
            className={`px-4 py-2 rounded-lg text-sm flex items-center ${
              isLoadingSchedule
                ? "bg-gray-400 cursor-not-allowed"
                : `${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.hoverBg}`
            } transition-colors duration-200`}
          >
            {isLoadingSchedule ? (
              <>
                <svg
                  className="animate-spin h-5 w-5 mr-2 text-gray-700"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
                Loading...
              </>
            ) : (
              "Generate Schedule"
            )}
          </button>
        </div>
      </div>

      <div className={`rounded-lg shadow ${themeConfig.background}`}>
        <div className={`p-4 border-b ${themeConfig.border}`}>
          <h2 className={`font-semibold ${themeConfig.text}`}>Study Stats</h2>
        </div>
        <div className="p-8">
          <div
            className={`p-4 rounded-lg text-center ${themeConfig.secondaryBg}`}
          >
            <h3 className={`text-3xl font-bold mb-2 ${themeConfig.primary}`}>
              {studyStats.totalHours}
            </h3>
            <p className={`${themeConfig.secondaryText}`}>Total Study Hours</p>
          </div>
          <div className="flex mt-4 space-x-2">
            <div className="relative">
              <input
                type="number"
                placeholder="Hours"
                className={`p-2 border rounded w-24 ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
                value={hoursInput}
                onChange={(e) => setHoursInput(e.target.value)}
                min="0"
                step="0.5"
              />
              {hoursError && (
                <p className="text-red-500 text-xs mt-1 absolute">
                  {hoursError}
                </p>
              )}
            </div>
            <button
              onClick={handleLogHours}
              className={`px-4 py-2 rounded-lg text-sm ${themeConfig.primary} ${themeConfig.primaryText} ${themeConfig.primaryHover} transition-colors duration-200`}
            >
              Log Hours
            </button>
          </div>
        </div>
      </div>

      <div className={`rounded-lg shadow ${themeConfig.background}`}>
        <div className={`p-4 border-b ${themeConfig.border}`}>
          <h2 className={`font-semibold ${themeConfig.text}`}>
            Upcoming Tasks
          </h2>
        </div>
        <div className={`divide-y ${themeConfig.border}`}>
          {upcomingTasks.length > 0 ? (
            upcomingTasks.map((task) => {
              const isOverdue = task.dueDate < today;
              const isDueToday = task.dueDate === today;
              const isFuture = task.dueDate > today;

              return (
                <div
                  key={task.id}
                  className={`p-4 ${themeConfig.hoverBg} ${
                    isOverdue
                      ? "bg-red-50 bg-opacity-50"
                      : isDueToday
                      ? "bg-orange-50 bg-opacity-50"
                      : isFuture
                      ? "bg-green-50 bg-opacity-50"
                      : ""
                  } transition-colors duration-200`}
                >
                  <div className="flex justify-between items-center">
                    <div className="flex items-center">
                      <button
                        onClick={() => toggleTaskComplete(task.id)}
                        className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center ${
                          task.priority === "High"
                            ? "border-red-500"
                            : task.priority === "Medium"
                            ? "border-yellow-500"
                            : "border-blue-400"
                        } hover:shadow-sm transition-shadow duration-200`}
                      ></button>
                      <div>
                        <h3
                          className={`${themeConfig.text} ${
                            isOverdue
                              ? themeConfig.overdueColor
                              : isDueToday
                              ? themeConfig.dueTodayColor
                              : isFuture
                              ? themeConfig.futureColor
                              : ""
                          } font-medium`}
                        >
                          {task.title}
                          {isOverdue && " (Overdue)"}
                          {isDueToday && " (Due Today)"}
                          {isFuture && " (Future)"}
                        </h3>
                        <div
                          className={`flex items-center text-sm ${themeConfig.secondaryText} mt-1`}
                        >
                          <Calendar
                            className={`w-4 h-4 mr-1 ${themeConfig.secondaryText}`}
                          />
                          Due: {task.dueDate}
                          {task.subject && (
                            <span className="ml-3">
                              Subject: {task.subject}
                            </span>
                          )}
                          <span
                            className={`ml-3 px-2 py-1 rounded-full text-xs ${
                              task.priority === "High"
                                ? "bg-red-100 text-red-800"
                                : task.priority === "Medium"
                                ? "bg-yellow-100 text-yellow-800"
                                : "bg-blue-100 text-blue-800"
                            }`}
                          >
                            {task.priority}
                          </span>
                        </div>
                      </div>
                    </div>
                    <div>
                      <button
                        onClick={() => setActiveTab("tasks")}
                        className={`text-sm ${themeConfig.primary} ${themeConfig.primaryText} px-3 py-1 rounded hover:${themeConfig.primaryHover} transition-colors duration-200`}
                      >
                        View All Tasks
                      </button>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="p-8 text-center">
              <p className={`${themeConfig.secondaryText}`}>
                No upcoming tasks. Add some tasks to get started!
              </p>
              <button
                onClick={() => setActiveTab("tasks")}
                className={`mt-4 px-4 py-2 rounded-lg text-sm ${themeConfig.primary} ${themeConfig.primaryText} ${themeConfig.primaryHover} transition-colors duration-200`}
              >
                Add Task
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
