import React from "react";
import { Award, Star, Flame } from "lucide-react";

function Rewards({
  points,
  badges,
  studyStats,
  themes,
  currentTheme,
  unlockTheme,
}) {
  const isDarkMode = currentTheme === "Dark Mode";

  const badgeDetails = [
    {
      name: "Priority Master",
      description: "Complete 5 high-priority tasks",
      icon: <Star className="w-6 h-6 text-yellow-500" />,
    },
    {
      name: "Streak Star",
      description: "Maintain a 7-day study streak",
      icon: <Flame className="w-6 h-6 text-orange-500" />,
    },
    {
      name: "Task Titan",
      description: "Complete 10 tasks",
      icon: <Award className="w-6 h-6 text-blue-500" />,
    },
    {
      name: "Early Bird",
      description: "Complete 3 tasks before their due date",
      icon: <Star className="w-6 h-6 text-green-500" />,
    },
  ];

  const availableThemes = [
    { name: "Dark Mode", cost: 100 },
    { name: "Ocean Breeze", cost: 150 },
  ];

  return (
    <div
      className={`rounded-lg shadow ${isDarkMode ? "bg-gray-700" : "bg-white"}`}
    >
      <div
        className={`p-4 border-b ${
          isDarkMode ? "border-gray-600" : "border-gray-200"
        }`}
      >
        <h2
          className={`font-semibold ${
            isDarkMode ? "text-gray-200" : "text-gray-800"
          }`}
        >
          Your Rewards
        </h2>
      </div>
      <div className="p-8">
        {/* Points Display */}
        <div className="text-center mb-8">
          <div
            className={`inline-block p-12 rounded-full mb-4 ${
              isDarkMode ? "bg-indigo-900" : "bg-indigo-100"
            }`}
          >
            <div
              className={`text-4xl font-bold ${
                isDarkMode ? "text-indigo-400" : "text-indigo-600"
              }`}
            >
              {points}
            </div>
          </div>
          <h3
            className={`text-lg font-medium ${
              isDarkMode ? "text-gray-200" : "text-gray-800"
            }`}
          >
            Total Points
          </h3>
          <p
            className={`mt-2 ${isDarkMode ? "text-gray-400" : "text-gray-500"}`}
          >
            Earn points by completing tasks and maintaining streaks!
          </p>
        </div>

        {/* Streak Progress */}
        <div
          className={`p-4 rounded-lg mb-8 ${
            isDarkMode ? "bg-gray-600" : "bg-gray-50"
          }`}
        >
          <h4
            className={`font-medium mb-2 ${
              isDarkMode ? "text-gray-200" : "text-gray-800"
            }`}
          >
            Study Streak Progress
          </h4>
          <div className="flex justify-center space-x-2 mt-4">
            {[1, 2, 3, 4, 5, 6, 7].map((day) => (
              <div
                key={day}
                className={`w-8 h-8 rounded-full flex items-center justify-center text-sm
                ${
                  day <= studyStats.streak
                    ? isDarkMode
                      ? "bg-indigo-500 text-white"
                      : "bg-indigo-600 text-white"
                    : isDarkMode
                    ? "bg-gray-500 text-gray-300"
                    : "bg-gray-200 text-gray-500"
                }`}
              >
                {day}
              </div>
            ))}
          </div>
          <p
            className={`text-center text-sm mt-2 ${
              isDarkMode ? "text-gray-400" : "text-gray-500"
            }`}
          >
            {studyStats.streak >= 7
              ? "Great job! You've earned the Streak Star badge!"
              : `Keep going! ${
                  7 - studyStats.streak
                } more days to earn the Streak Star badge.`}
          </p>
        </div>

        {/* Badges Display */}
        <div
          className={`p-4 rounded-lg mb-8 ${
            isDarkMode ? "bg-gray-600" : "bg-gray-50"
          }`}
        >
          <h4
            className={`font-medium mb-4 ${
              isDarkMode ? "text-gray-200" : "text-gray-800"
            }`}
          >
            Your Badges
          </h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {badgeDetails.map((badge) => (
              <div
                key={badge.name}
                className={`p-4 rounded-lg flex items-center space-x-3 ${
                  badges.includes(badge.name)
                    ? isDarkMode
                      ? "bg-indigo-800"
                      : "bg-indigo-100"
                    : isDarkMode
                    ? "bg-gray-500 opacity-50"
                    : "bg-gray-100 opacity-50"
                }`}
              >
                <div>{badge.icon}</div>
                <div>
                  <h5
                    className={`font-medium ${
                      isDarkMode ? "text-gray-200" : "text-gray-800"
                    }`}
                  >
                    {badge.name}
                  </h5>
                  <p
                    className={`text-sm ${
                      isDarkMode ? "text-gray-400" : "text-gray-500"
                    }`}
                  >
                    {badge.description}
                  </p>
                </div>
              </div>
            ))}
          </div>
          {badges.length === 0 && (
            <p
              className={`text-center mt-4 ${
                isDarkMode ? "text-gray-400" : "text-gray-500"
              }`}
            >
              Complete tasks and maintain streaks to earn badges!
            </p>
          )}
        </div>

        {/* Store Section for Themes */}
        <div
          className={`p-4 rounded-lg ${
            isDarkMode ? "bg-gray-600" : "bg-gray-50"
          }`}
        >
          <h4
            className={`font-medium mb-4 ${
              isDarkMode ? "text-gray-200" : "text-gray-800"
            }`}
          >
            Store
          </h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {availableThemes.map((theme) => (
              <div
                key={theme.name}
                className={`p-4 rounded-lg flex justify-between items-center ${
                  isDarkMode ? "bg-indigo-800" : "bg-indigo-50"
                }`}
              >
                <div>
                  <h5
                    className={`font-medium ${
                      isDarkMode ? "text-gray-200" : "text-gray-800"
                    }`}
                  >
                    {theme.name}
                  </h5>
                  <p
                    className={`text-sm ${
                      isDarkMode ? "text-gray-400" : "text-gray-500"
                    }`}
                  >
                    {theme.cost} points
                  </p>
                </div>
                <button
                  onClick={() => unlockTheme(theme.name, theme.cost)}
                  className={`px-4 py-2 rounded-lg text-sm ${
                    points >= theme.cost || themes.includes(theme.name)
                      ? isDarkMode
                        ? "bg-indigo-500 text-white"
                        : "bg-indigo-600 text-white"
                      : "bg-gray-300 text-gray-500"
                  }`}
                >
                  {themes.includes(theme.name)
                    ? currentTheme === theme.name
                      ? "Selected"
                      : "Switch"
                    : "Unlock"}
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Rewards;
