import React from "react";
import { Trophy, Medal, Flame, Moon, Waves, Sun, Leaf } from "lucide-react";
import { toast } from "react-toastify";

const unlockableThemes = [
  {
    name: "Dark Mode",
    cost: 50,
    description: "A sleek dark theme for night owls.",
    icon: (
      <Moon className="w-8 h-8 text-indigo-500 transition-transform duration-300 hover:scale-110" />
    ),
  },
  {
    name: "Ocean Breeze",
    cost: 50,
    description: "A refreshing blue and teal gradient theme.",
    icon: (
      <Waves className="w-8 h-8 text-teal-500 transition-transform duration-300 hover:scale-110" />
    ),
  },
  {
    name: "Sunset Glow",
    cost: 50,
    description: "A warm theme with hues of orange and pink.",
    icon: (
      <Sun className="w-8 h-8 text-orange-500 transition-transform duration-300 hover:scale-110" />
    ),
  },
  {
    name: "Forest Whisper",
    cost: 50,
    description: "A calming green theme inspired by nature.",
    icon: (
      <Leaf className="w-8 h-8 text-green-500 transition-transform duration-300 hover:scale-110" />
    ),
  },
];

function Rewards({
  points,
  badges,
  studyStats,
  currentTheme,
  onPointsUpdate,
  onThemeUnlock,
  unlockedThemes,
  tasks = [], // Default to empty array if tasks is undefined
}) {
  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";
  const isSunsetGlow = currentTheme === "Sunset Glow";
  const isForestWhisper = currentTheme === "Forest Whisper";

  // Helper function to format the local date as YYYY-MM-DD
  const getLocalDateString = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0"); // getMonth() is 0-based
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  const today = getLocalDateString(new Date());

  // Calculate badge progress
  const completedHighPriorityTasks = tasks.filter(
    (task) => task.completed && task.priority === "High"
  ).length;
  const totalCompletedTasks = tasks.filter((task) => task.completed).length;
  const earlyCompletedTasks = tasks.filter(
    (task) => task.completed && task.completedDate < task.dueDate
  ).length;
  const currentStreak = studyStats.streak || 0;

  const availableBadges = [
    {
      name: "Priority Master",
      description: "Complete 5 high-priority tasks",
      icon: (
        <Trophy className="w-10 h-10 text-yellow-500 transition-transform duration-300 hover:scale-110" />
      ),
      goal: 5,
      progress: completedHighPriorityTasks,
    },
    {
      name: "Task Titan",
      description: "Complete 10 tasks",
      icon: (
        <Medal className="w-10 h-10 text-yellow-500 transition-transform duration-300 hover:scale-110" />
      ),
      goal: 10,
      progress: totalCompletedTasks,
    },
    {
      name: "Early Bird",
      description: "Complete 3 tasks before their due date",
      icon: (
        <Trophy className="w-10 h-10 text-yellow-500 transition-transform duration-300 hover:scale-110" />
      ),
      goal: 3,
      progress: earlyCompletedTasks,
    },
    {
      name: "Streak Star",
      description: "Maintain a 7-day study streak",
      icon: (
        <Flame className="w-10 h-10 text-yellow-500 transition-transform duration-300 hover:scale-110" />
      ),
      goal: 7,
      progress: currentStreak,
    },
  ];

  const handleRedeemTheme = (theme) => {
    if (points < theme.cost) {
      toast.error("Not enough points to redeem this theme!", {
        position: "top-right",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        theme: isDarkMode ? "dark" : "light",
      });
      return;
    }

    const newPoints = points - theme.cost;
    onPointsUpdate(newPoints);
    onThemeUnlock(theme.name);
    toast.success(`${theme.name} theme unlocked!`, {
      position: "top-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      theme: isDarkMode ? "dark" : "light",
    });
  };

  return (
    <div
      className={`rounded-xl shadow-lg transition-all duration-300 ${
        isDarkMode
          ? "bg-gray-800 border border-gray-700"
          : isOceanBreeze
            ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
            : isSunsetGlow
              ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
              : isForestWhisper
                ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                : "bg-white border border-gray-200"
      }`}
    >
      <div
        className={`p-6 border-b ${
          isDarkMode
            ? "border-gray-600"
            : isOceanBreeze
              ? "border-teal-400"
              : isSunsetGlow
                ? "border-orange-400"
                : isForestWhisper
                  ? "border-green-400"
                  : "border-gray-300"
        }`}
      >
        <h2
          className={`text-2xl font-bold tracking-tight ${
            isDarkMode
              ? "text-gray-100"
              : isOceanBreeze
                ? "text-teal-900"
                : isSunsetGlow
                  ? "text-orange-900"
                  : isForestWhisper
                    ? "text-green-900"
                    : "text-gray-900"
          }`}
        >
          Rewards & Achievements
        </h2>
      </div>
      <div className="p-8 space-y-10">
        <div
          className={`p-6 rounded-xl text-center shadow-md transition-all duration-300 hover:shadow-lg ${
            isDarkMode
              ? "bg-gray-700"
              : isOceanBreeze
                ? "bg-amber-50 bg-opacity-80 backdrop-blur-md"
                : isSunsetGlow
                  ? "bg-pink-50 bg-opacity-80 backdrop-blur-md"
                  : isForestWhisper
                    ? "bg-emerald-50 bg-opacity-80 backdrop-blur-md"
                    : "bg-gray-50"
          }`}
        >
          <h3
            className={`text-4xl font-extrabold mb-3 tracking-wide ${
              points < 0
                ? "text-red-600"
                : isDarkMode
                  ? "text-indigo-300"
                  : isOceanBreeze
                    ? "text-teal-600"
                    : isSunsetGlow
                      ? "text-orange-600"
                      : isForestWhisper
                        ? "text-green-600"
                        : "text-indigo-600"
            }`}
          >
            {points}
          </h3>
          <p
            className={`text-lg font-medium ${
              isDarkMode
                ? "text-gray-300"
                : isOceanBreeze
                  ? "text-blue-700"
                  : isSunsetGlow
                    ? "text-orange-600"
                    : isForestWhisper
                      ? "text-green-600"
                      : "text-gray-600"
            }`}
          >
            Total Points Earned
          </p>
        </div>

        <div>
          <h3
            className={`text-xl font-semibold mb-6 tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-900"
                  : isSunsetGlow
                    ? "text-orange-900"
                    : isForestWhisper
                      ? "text-green-900"
                      : "text-gray-900"
            }`}
          >
            Your Badges
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {availableBadges.map((badge) => {
              const isEarned = badges.includes(badge.name);
              const remaining = badge.goal - badge.progress;
              return (
                <div
                  key={badge.name}
                  className={`p-6 rounded-xl text-center shadow-md transition-all duration-300 hover:shadow-xl hover:scale-105 ${
                    isEarned
                      ? isDarkMode
                        ? "bg-indigo-900 border border-indigo-500"
                        : isOceanBreeze
                          ? "bg-teal-50 bg-opacity-90 backdrop-blur-md border border-teal-400"
                          : isSunsetGlow
                            ? "bg-orange-50 bg-opacity-90 backdrop-blur-md border border-orange-400"
                            : isForestWhisper
                              ? "bg-green-50 bg-opacity-90 backdrop-blur-md border border-green-400"
                              : "bg-indigo-100 border border-indigo-300"
                      : isDarkMode
                        ? "bg-gray-700 opacity-60 border border-gray-600"
                        : isOceanBreeze
                          ? "bg-gray-200 opacity-60 backdrop-blur-md border border-gray-300"
                          : isSunsetGlow
                            ? "bg-gray-200 opacity-60 backdrop-blur-md border border-gray-300"
                            : isForestWhisper
                              ? "bg-gray-200 opacity-60 backdrop-blur-md border border-gray-300"
                              : "bg-gray-100 opacity-60 border border-gray-200"
                  }`}
                >
                  <div className="flex justify-center">{badge.icon}</div>
                  <h4
                    className={`font-semibold mt-3 text-lg ${
                      isDarkMode
                        ? "text-gray-100"
                        : isOceanBreeze
                          ? "text-teal-800"
                          : isSunsetGlow
                            ? "text-orange-800"
                            : isForestWhisper
                              ? "text-green-800"
                              : "text-gray-800"
                    }`}
                  >
                    {badge.name}
                  </h4>
                  <p
                    className={`text-sm mt-1 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                          ? "text-blue-600"
                          : isSunsetGlow
                            ? "text-orange-500"
                            : isForestWhisper
                              ? "text-green-500"
                              : "text-gray-500"
                    }`}
                  >
                    {badge.description}
                  </p>
                  <p
                    className={`text-sm font-medium mt-2 ${
                      isEarned
                        ? "text-green-500"
                        : isDarkMode
                          ? "text-gray-500"
                          : isOceanBreeze
                            ? "text-blue-500"
                            : isSunsetGlow
                              ? "text-orange-400"
                              : isForestWhisper
                                ? "text-green-400"
                                : "text-gray-400"
                    }`}
                  >
                    {isEarned ? "Earned!" : "Not yet earned"}
                  </p>
                  {!isEarned && (
                    <p
                      className={`text-sm font-medium mt-1 ${
                        isDarkMode
                          ? "text-gray-300"
                          : isOceanBreeze
                            ? "text-blue-600"
                            : isSunsetGlow
                              ? "text-orange-500"
                              : isForestWhisper
                                ? "text-green-500"
                                : "text-gray-500"
                      }`}
                    >
                      {badge.name === "Streak Star"
                        ? `Days remaining: ${remaining > 0 ? remaining : 0}`
                        : `Tasks remaining: ${remaining > 0 ? remaining : 0}`}
                    </p>
                  )}
                </div>
              );
            })}
          </div>
        </div>

        <div>
          <h3
            className={`text-xl font-semibold mb-6 tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-900"
                  : isSunsetGlow
                    ? "text-orange-900"
                    : isForestWhisper
                      ? "text-green-900"
                      : "text-gray-900"
            }`}
          >
            Study Streaks
          </h3>
          <div
            className={`p-6 rounded-xl text-center shadow-md transition-all duration-300 hover:shadow-lg ${
              isDarkMode
                ? "bg-gray-700"
                : isOceanBreeze
                  ? "bg-amber-50 bg-opacity-80 backdrop-blur-md"
                  : isSunsetGlow
                    ? "bg-pink-50 bg-opacity-80 backdrop-blur-md"
                    : isForestWhisper
                      ? "bg-emerald-50 bg-opacity-80 backdrop-blur-md"
                      : "bg-gray-50"
            }`}
          >
            <Flame
              className={`inline-block w-10 h-10 mb-3 transition-transform duration-300 hover:scale-110 ${
                isDarkMode
                  ? "text-orange-400"
                  : isOceanBreeze
                    ? "text-orange-500"
                    : isSunsetGlow
                      ? "text-orange-600"
                      : isForestWhisper
                        ? "text-green-600"
                        : "text-orange-600"
              }`}
            />
            <h4
              className={`text-xl font-semibold ${
                isDarkMode
                  ? "text-gray-100"
                  : isOceanBreeze
                    ? "text-teal-800"
                    : isSunsetGlow
                      ? "text-orange-800"
                      : isForestWhisper
                        ? "text-green-800"
                        : "text-gray-800"
              }`}
            >
              Current Streak: {studyStats.streak} Days
            </h4>
            <p
              className={`text-sm mt-1 ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              Keep studying daily to maintain your streak!
            </p>
          </div>
        </div>

        <div>
          <h3
            className={`text-xl font-semibold mb-6 tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-900"
                  : isSunsetGlow
                    ? "text-orange-900"
                    : isForestWhisper
                      ? "text-green-900"
                      : "text-gray-900"
            }`}
          >
            Redeem Themes
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {unlockableThemes.map((theme) => {
              const isUnlocked = unlockedThemes.includes(theme.name);
              const canRedeem = points >= theme.cost && !isUnlocked;
              return (
                <div
                  key={theme.name}
                  className={`p-6 rounded-xl text-center shadow-md transition-all duration-300 hover:shadow-xl hover:scale-105 ${
                    isUnlocked
                      ? isDarkMode
                        ? "bg-indigo-900 border border-indigo-500 opacity-80"
                        : isOceanBreeze
                          ? "bg-teal-50 bg-opacity-90 backdrop-blur-md border border-teal-400 opacity-80"
                          : isSunsetGlow
                            ? "bg-orange-50 bg-opacity-90 backdrop-blur-md border border-orange-400 opacity-80"
                            : isForestWhisper
                              ? "bg-green-50 bg-opacity-90 backdrop-blur-md border border-green-400 opacity-80"
                              : "bg-indigo-100 border border-indigo-300 opacity-80"
                      : isDarkMode
                        ? "bg-gray-700 border border-gray-600"
                        : isOceanBreeze
                          ? "bg-gray-200 backdrop-blur-md border border-gray-300"
                          : isSunsetGlow
                            ? "bg-gray-200 backdrop-blur-md border border-gray-300"
                            : isForestWhisper
                              ? "bg-gray-200 backdrop-blur-md border border-gray-300"
                              : "bg-gray-100 border border-gray-200"
                  }`}
                >
                  <div className="flex justify-center mb-3">{theme.icon}</div>
                  <h4
                    className={`font-semibold text-lg ${
                      isDarkMode
                        ? "text-gray-100"
                        : isOceanBreeze
                          ? "text-teal-800"
                          : isSunsetGlow
                            ? "text-orange-800"
                            : isForestWhisper
                              ? "text-green-800"
                              : "text-gray-800"
                    }`}
                  >
                    {theme.name}
                  </h4>
                  <p
                    className={`text-sm mt-1 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                          ? "text-blue-600"
                          : isSunsetGlow
                            ? "text-orange-500"
                            : isForestWhisper
                              ? "text-green-500"
                              : "text-gray-500"
                    }`}
                  >
                    {theme.description}
                  </p>
                  <p
                    className={`text-sm font-medium mt-2 ${
                      isDarkMode
                        ? "text-gray-300"
                        : isOceanBreeze
                          ? "text-blue-700"
                          : isSunsetGlow
                            ? "text-orange-600"
                            : isForestWhisper
                              ? "text-green-600"
                              : "text-gray-600"
                    }`}
                  >
                    Cost: {theme.cost} Points
                  </p>
                  <p
                    className={`text-sm font-medium mt-2 ${
                      isUnlocked
                        ? "text-green-500"
                        : isDarkMode
                          ? "text-gray-500"
                          : isOceanBreeze
                            ? "text-blue-500"
                            : isSunsetGlow
                              ? "text-orange-400"
                              : isForestWhisper
                                ? "text-green-400"
                                : "text-gray-400"
                    }`}
                  >
                    {isUnlocked ? "Unlocked!" : "Not yet unlocked"}
                  </p>
                  {!isUnlocked && (
                    <button
                      onClick={() => handleRedeemTheme(theme)}
                      className={`mt-4 px-5 py-2 rounded-lg text-white text-sm font-semibold transition-all duration-200 transform hover:scale-105 ${
                        canRedeem
                          ? isDarkMode
                            ? "bg-indigo-600 hover:bg-indigo-700"
                            : isOceanBreeze
                              ? "bg-teal-600 hover:bg-teal-700"
                              : isSunsetGlow
                                ? "bg-orange-600 hover:bg-orange-700"
                                : isForestWhisper
                                  ? "bg-green-600 hover:bg-green-700"
                                  : "bg-indigo-600 hover:bg-indigo-700"
                          : "bg-gray-400 cursor-not-allowed opacity-70"
                      }`}
                      disabled={!canRedeem}
                    >
                      Redeem
                    </button>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Rewards;
