import React, { useMemo } from "react";
import { parse, isBefore, format } from "date-fns";

// Theme configuration for consistent styling
const themeStyles = {
  "Light Mode": {
    bg: "bg-white",
    border: "border-gray-200",
    text: "text-gray-800",
    subText: "text-gray-500",
    accent: "bg-indigo-600",
    cardBg: "bg-gray-100",
    insightBg: "bg-indigo-50",
    priorityColors: {
      High: "bg-red-500",
      Medium: "bg-yellow-500",
      Low: "bg-blue-500",
    },
  },
  "Dark Mode": {
    bg: "bg-gray-700",
    border: "border-gray-600",
    text: "text-gray-200",
    subText: "text-gray-400",
    accent: "bg-indigo-500",
    cardBg: "bg-gray-600",
    insightBg: "bg-indigo-900",
    priorityColors: {
      High: "bg-red-400",
      Medium: "bg-yellow-400",
      Low: "bg-blue-400",
    },
  },
  "Ocean Breeze": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-teal-300",
    text: "text-teal-800",
    subText: "text-blue-600",
    accent: "bg-teal-500",
    cardBg: "bg-amber-100 bg-opacity-70 backdrop-blur-sm",
    insightBg: "bg-teal-100 bg-opacity-70 backdrop-blur-sm",
    priorityColors: {
      High: "bg-red-600",
      Medium: "bg-yellow-600",
      Low: "bg-blue-600",
    },
  },
  "Sunset Glow": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-orange-300",
    text: "text-orange-800",
    subText: "text-orange-500",
    accent: "bg-orange-500",
    cardBg: "bg-pink-100 bg-opacity-70 backdrop-blur-sm",
    insightBg: "bg-pink-100 bg-opacity-70 backdrop-blur-sm",
    priorityColors: {
      High: "bg-red-600",
      Medium: "bg-yellow-600",
      Low: "bg-blue-600",
    },
  },
  "Forest Whisper": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-green-300",
    text: "text-green-800",
    subText: "text-green-500",
    accent: "bg-green-500",
    cardBg: "bg-emerald-100 bg-opacity-70 backdrop-blur-sm",
    insightBg: "bg-emerald-100 bg-opacity-70 backdrop-blur-sm",
    priorityColors: {
      High: "bg-red-600",
      Medium: "bg-yellow-600",
      Low: "bg-blue-600",
    },
  },
};

function Analytics({ tasks, currentTheme }) {
  // Select theme styles, default to Light Mode if theme not found
  const currentThemeStyles =
    themeStyles[currentTheme] || themeStyles["Light Mode"];

  // Memoize task-related calculations to improve performance
  const completionStats = useMemo(() => {
    const completedTasks = tasks.filter((task) => task.completed).length;
    const completionRate = tasks.length
      ? Math.round((completedTasks / tasks.length) * 100)
      : 0;
    return { completedTasks, completionRate };
  }, [tasks]);

  const priorityStats = useMemo(() => {
    const counts = {
      High: tasks.filter((task) => task.priority === "High").length,
      Medium: tasks.filter((task) => task.priority === "Medium").length,
      Low: tasks.filter((task) => task.priority === "Low").length,
    };
    const total = tasks.length || 1;
    return {
      counts,
      percentages: {
        High: (counts.High / total) * 100,
        Medium: (counts.Medium / total) * 100,
        Low: (counts.Low / total) * 100,
      },
    };
  }, [tasks]);

  const today = new Date();
  const todayStr = format(today, "yyyy-MM-dd");
  const daysOfWeek = ["S", "M", "T", "W", "T", "F", "S"];
  const past7Days = Array(7)
    .fill(0)
    .map((_, index) => {
      const date = new Date(today);
      date.setDate(today.getDate() - (6 - index));
      return {
        date: format(date, "yyyy-MM-dd"),
        day: daysOfWeek[date.getDay()],
      };
    });

  const completionData = past7Days.map(
    (dayObj) =>
      tasks.filter(
        (task) => task.completed && task.completedDate === dayObj.date
      ).length
  );
  const maxCompletions = Math.max(...completionData, 1);
  const completionHeights = completionData.map(
    (count) => (count / maxCompletions) * 20
  );

  const overdueTasks = tasks.filter((task) => {
    if (task.completed || !task.dueDate) return false;
    const dueDate = parse(task.dueDate, "yyyy-MM-dd", new Date());
    return isBefore(dueDate, today);
  });

  const completedTasksByDay = past7Days.map((dayObj) => ({
    day: dayObj.day,
    date: dayObj.date,
    count: tasks.filter(
      (task) => task.completed && task.completedDate === dayObj.date
    ).length,
  }));
  const mostProductiveDay = completedTasksByDay.reduce(
    (max, day) => (day.count > max.count ? day : max),
    { day: "", date: "", count: 0 }
  );

  // AI Insights
  const insights = [
    {
      color: "bg-green-500",
      label: "Productivity Insight",
      text:
        mostProductiveDay.count > 0
          ? `You're most productive on ${mostProductiveDay.day}s, with ${
              mostProductiveDay.count
            } task${mostProductiveDay.count !== 1 ? "s" : ""} completed on ${
              mostProductiveDay.date
            }.`
          : "You're just getting started—complete your first task today to build your streak!",
    },
    {
      color: "bg-yellow-500",
      label: "Overdue Tasks",
      text:
        overdueTasks.length > 0
          ? `You have ${overdueTasks.length} overdue task${
              overdueTasks.length !== 1 ? "s" : ""
            }. Focus on completing these to stay on track!`
          : "Great job! You have no overdue tasks.",
    },
    {
      color: "bg-blue-500",
      label: "Priority Reminder",
      text: (() => {
        const incompleteHighPriorityTasks = tasks.filter(
          (task) => !task.completed && task.priority === "High"
        ).length;
        return incompleteHighPriorityTasks > 0
          ? `You have ${incompleteHighPriorityTasks} incomplete high-priority task${
              incompleteHighPriorityTasks !== 1 ? "s" : ""
            }. Prioritize these to maximize your productivity.`
          : "No high-priority tasks pending. Keep up the good work!";
      })(),
    },
  ];

  return (
    <div className={`rounded-lg shadow ${currentThemeStyles.bg}`}>
      <div className={`p-4 border-b ${currentThemeStyles.border}`}>
        <h2 className={`font-semibold ${currentThemeStyles.text}`}>
          Study Analytics
        </h2>
      </div>
      <div className="p-4 sm:p-8 text-center">
        <div
          className={`inline-block p-8 sm:p-12 rounded-full mb-4 ${currentThemeStyles.cardBg} shadow-sm`}
        >
          <div className={`text-4xl font-bold ${currentThemeStyles.accent}`}>
            {completionStats.completionRate}%
          </div>
        </div>
        <h3 className={`text-lg font-medium ${currentThemeStyles.text}`}>
          Task Completion Rate
        </h3>
        <p className={`mt-2 ${currentThemeStyles.subText}`}>
          You've completed {completionStats.completedTasks} out of{" "}
          {tasks.length} tasks
        </p>

        <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* Task Completion Timeline */}
          <div
            className={`p-4 rounded-lg ${currentThemeStyles.cardBg} shadow-sm`}
          >
            <h4 className={`font-medium mb-2 ${currentThemeStyles.text}`}>
              Task Completion Timeline
            </h4>
            <div className="flex justify-center space-x-2 mt-4">
              {past7Days.map((dayObj, index) => (
                <div key={dayObj.date} className="flex flex-col items-center">
                  <div
                    className={`w-4 rounded-t-sm ${currentThemeStyles.accent}`}
                    style={{ height: `${completionHeights[index]}rem` }}
                    aria-label={`Tasks completed on ${dayObj.day}: ${completionData[index]}`}
                  />
                  <span
                    className={`text-xs mt-1 ${currentThemeStyles.subText}`}
                  >
                    {dayObj.day}
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Priority Breakdown */}
          <div
            className={`p-4 rounded-lg ${currentThemeStyles.cardBg} shadow-sm`}
          >
            <h4 className={`font-medium mb-2 ${currentThemeStyles.text}`}>
              Priority Breakdown
            </h4>
            <div className="flex flex-col items-center space-y-2 mt-4">
              {["High", "Medium", "Low"].map((priority) => (
                <div
                  key={priority}
                  className="flex items-center w-full max-w-xs"
                >
                  <div
                    className={`h-4 rounded ${currentThemeStyles.priorityColors[priority]}`}
                    style={{ width: `${priorityStats.percentages[priority]}%` }}
                    aria-label={`${priority} priority tasks: ${Math.round(
                      priorityStats.percentages[priority]
                    )}%`}
                  />
                  <span
                    className={`text-xs ml-2 ${currentThemeStyles.subText}`}
                  >
                    {priority}:{" "}
                    {Math.round(priorityStats.percentages[priority])}%
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Overdue Tasks Indicator */}
          <div
            className={`p-4 rounded-lg ${currentThemeStyles.cardBg} shadow-sm`}
          >
            <h4 className={`font-medium mb-2 ${currentThemeStyles.text}`}>
              Overdue Tasks ({overdueTasks.length})
            </h4>
            {overdueTasks.length > 0 ? (
              <ul className="space-y-2">
                {overdueTasks.map((task) => (
                  <li
                    key={task.id}
                    className={`text-sm ${currentThemeStyles.subText}`}
                  >
                    {task.title} (Due: {task.dueDate}, Priority: {task.priority}
                    )
                  </li>
                ))}
              </ul>
            ) : (
              <p className={`text-sm ${currentThemeStyles.subText}`}>
                No overdue tasks! You're on track.
              </p>
            )}
          </div>
        </div>

        {/* AI Insights */}
        <div
          className={`mt-8 p-4 rounded-lg ${currentThemeStyles.insightBg} shadow-sm text-left`}
        >
          <h4 className={`font-medium mb-2 ${currentThemeStyles.text}`}>
            AI Insights
          </h4>
          <ul className="space-y-2 text-sm">
            {insights.map((insight, index) => (
              <li key={index} className="flex items-start">
                <div
                  className={`w-4 h-4 rounded-full ${insight.color} mt-1 mr-2`}
                  aria-hidden="true"
                />
                <div>
                  <span className={`font-medium ${currentThemeStyles.subText}`}>
                    {insight.label}:
                  </span>{" "}
                  <span className={currentThemeStyles.subText}>
                    {insight.text}
                  </span>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Analytics;
