import React, { useState, useMemo } from "react";
import {
  Calendar,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
  Save,
  X,
  ArrowUpDown,
  Filter,
} from "lucide-react";

function TaskManager({
  tasks,
  addTask,
  updateTask,
  deleteTask,
  toggleTaskComplete,
  currentTheme,
}) {
  const [newTask, setNewTask] = useState({
    title: "",
    dueDate: "",
    priority: "Medium",
    completed: false,
  });
  const [editingTask, setEditingTask] = useState(null);
  const [sortBy, setSortBy] = useState("dueDateAsc");
  const [filterBy, setFilterBy] = useState("all");
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(null);

  const today = new Date().toISOString().split("T")[0];

  const handleAddTask = () => {
    if (!newTask.title) return;
    addTask(newTask);
    setNewTask({
      title: "",
      dueDate: "",
      priority: "Medium",
      completed: false,
    });
  };

  const handleStartEdit = (task) => {
    setEditingTask({ ...task });
  };

  const handleSaveEdit = () => {
    if (!editingTask) return;
    updateTask(editingTask);
    setEditingTask(null);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const confirmDeleteTask = (id) => {
    setShowDeleteConfirm(id);
  };

  const handleDeleteTask = (id) => {
    deleteTask(id);
    setShowDeleteConfirm(null);
  };

  const cancelDeleteTask = () => {
    setShowDeleteConfirm(null);
  };

  const priorityValue = { High: 3, Medium: 2, Low: 1 };

  const sortedAndFilteredTasks = useMemo(() => {
    return [...tasks]
      .filter((task) => {
        if (filterBy === "completed") return task.completed;
        if (filterBy === "incomplete") return !task.completed;
        return true;
      })
      .sort((a, b) => {
        if (sortBy === "dueDateAsc") {
          return new Date(a.dueDate) - new Date(b.dueDate);
        } else if (sortBy === "dueDateDesc") {
          return new Date(b.dueDate) - new Date(a.dueDate);
        } else if (sortBy === "priorityAsc") {
          return priorityValue[a.priority] - priorityValue[b.priority];
        } else if (sortBy === "priorityDesc") {
          return priorityValue[b.priority] - priorityValue[a.priority];
        }
        return 0;
      });
  }, [tasks, filterBy, sortBy, currentTheme]);

  const renderTask = (task) => {
    const isDarkMode = currentTheme === "Dark Mode";
    const isOceanBreeze = currentTheme === "Ocean Breeze";
    const isLightMode = currentTheme === "Light Mode";
    const isSunsetGlow = currentTheme === "Sunset Glow";
    const isForestWhisper = currentTheme === "Forest Whisper";

    const isOverdue = !task.completed && task.dueDate < today;
    const isDueToday = !task.completed && task.dueDate === today;
    const isFuture = !task.completed && task.dueDate > today;

    return (
      <div
        key={task.id}
        className={`p-4 ${
          isDarkMode
            ? "hover:bg-gray-600"
            : isOceanBreeze
            ? "hover:bg-teal-100"
            : isSunsetGlow
            ? "hover:bg-orange-100"
            : isForestWhisper
            ? "hover:bg-green-100"
            : "hover:bg-gray-50"
        } ${
          isDarkMode
            ? ""
            : isOverdue
            ? "bg-red-50 bg-opacity-50"
            : isDueToday
            ? "bg-orange-50 bg-opacity-50"
            : isFuture
            ? "bg-green-50 bg-opacity-50"
            : ""
        } transition-colors duration-200`}
      >
        {editingTask && editingTask.id === task.id ? (
          <div className="flex flex-wrap gap-2">
            <input
              type="text"
              className={`flex-grow p-2 border rounded ${
                isDarkMode
                  ? "bg-gray-800 border-gray-500 text-gray-200"
                  : isOceanBreeze
                  ? "bg-blue-100 border-teal-300 text-teal-800"
                  : isSunsetGlow
                  ? "bg-orange-100 border-orange-300 text-orange-800"
                  : isForestWhisper
                  ? "bg-green-100 border-green-300 text-green-800"
                  : "bg-white border-gray-300 text-gray-800"
              } focus:outline-none focus:ring-2 ${
                isSunsetGlow
                  ? "focus:ring-orange-400"
                  : isForestWhisper
                  ? "focus:ring-green-400"
                  : "focus:ring-indigo-400"
              } transition-all duration-200`}
              value={editingTask.title}
              onChange={(e) =>
                setEditingTask({ ...editingTask, title: e.target.value })
              }
            />
            <input
              type="date"
              className={`p-2 border rounded ${
                isDarkMode
                  ? "bg-gray-800 border-gray-500 text-gray-200"
                  : isOceanBreeze
                  ? "bg-blue-100 border-teal-300 text-teal-800"
                  : isSunsetGlow
                  ? "bg-orange-100 border-orange-300 text-orange-800"
                  : isForestWhisper
                  ? "bg-green-100 border-green-300 text-green-800"
                  : "bg-white border-gray-300 text-gray-800"
              } focus:outline-none focus:ring-2 ${
                isSunsetGlow
                  ? "focus:ring-orange-400"
                  : isForestWhisper
                  ? "focus:ring-green-400"
                  : "focus:ring-indigo-400"
              } transition-all duration-200`}
              value={editingTask.dueDate}
              onChange={(e) =>
                setEditingTask({
                  ...editingTask,
                  dueDate: e.target.value,
                })
              }
            />
            <select
              className={`p-2 border rounded ${
                isDarkMode
                  ? "bg-gray-800 border-gray-500 text-gray-200"
                  : isOceanBreeze
                  ? "bg-blue-100 border-teal-300 text-teal-800"
                  : isSunsetGlow
                  ? "bg-orange-100 border-orange-300 text-orange-800"
                  : isForestWhisper
                  ? "bg-green-100 border-green-300 text-green-800"
                  : "bg-white border-gray-300 text-gray-800"
              } focus:outline-none focus:ring-2 ${
                isSunsetGlow
                  ? "focus:ring-orange-400"
                  : isForestWhisper
                  ? "focus:ring-green-400"
                  : "focus:ring-indigo-400"
              } transition-all duration-200`}
              value={editingTask.priority}
              onChange={(e) =>
                setEditingTask({
                  ...editingTask,
                  priority: e.target.value,
                })
              }
            >
              <option value="Low">Low Priority</option>
              <option value="Medium">Medium Priority</option>
              <option value="High">High Priority</option>
            </select>
            <div className="flex space-x-2">
              <button
                onClick={handleSaveEdit}
                className={`p-2 rounded flex items-center ${
                  isDarkMode
                    ? "bg-green-500 text-white hover:bg-green-600"
                    : isOceanBreeze
                    ? "bg-teal-500 text-white hover:bg-teal-600"
                    : isSunsetGlow
                    ? "bg-orange-500 text-white hover:bg-orange-600"
                    : isForestWhisper
                    ? "bg-green-500 text-white hover:bg-green-600"
                    : "bg-green-600 text-white hover:bg-green-700"
                } transition-colors duration-200`}
              >
                <Save className="w-4 h-4 mr-1" /> Save
              </button>
              <button
                onClick={handleCancelEdit}
                className={`p-2 rounded flex items-center ${
                  isDarkMode
                    ? "bg-gray-500 text-gray-200 hover:bg-gray-600"
                    : isOceanBreeze
                    ? "bg-blue-200 text-blue-700 hover:bg-blue-300"
                    : isSunsetGlow
                    ? "bg-orange-200 text-orange-700 hover:bg-orange-300"
                    : isForestWhisper
                    ? "bg-green-200 text-green-700 hover:bg-green-300"
                    : "bg-gray-300 text-gray-700 hover:bg-gray-400"
                } transition-colors duration-200`}
              >
                <X className="w-4 h-4 mr-1" /> Cancel
              </button>
            </div>
          </div>
        ) : (
          <div className="flex justify-between items-center">
            <div className="flex items-center">
              <button
                onClick={() => toggleTaskComplete(task.id)}
                className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center ${
                  task.completed
                    ? isDarkMode
                      ? "bg-green-900 border-green-600"
                      : isOceanBreeze
                      ? "bg-teal-100 border-teal-500"
                      : isSunsetGlow
                      ? "bg-orange-100 border-orange-500"
                      : isForestWhisper
                      ? "bg-green-100 border-green-500"
                      : "bg-green-50 border-green-500"
                    : task.priority === "High"
                    ? "border-red-500"
                    : task.priority === "Medium"
                    ? "border-yellow-500"
                    : "border-blue-400"
                } hover:shadow-sm transition-shadow duration-200`}
              >
                {task.completed && (
                  <CheckCircle className="w-4 h-4 text-green-600" />
                )}
              </button>
              <div
                className={task.completed ? "line-through text-gray-400" : ""}
              >
                <h3
                  className={`font-medium ${
                    isDarkMode
                      ? "text-gray-200"
                      : isOceanBreeze
                      ? "text-teal-800"
                      : isSunsetGlow
                      ? "text-orange-800"
                      : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
                  } ${
                    isOverdue
                      ? "text-red-600"
                      : isDueToday
                      ? "text-orange-600"
                      : isFuture
                      ? "text-green-600"
                      : ""
                  }`}
                >
                  {task.title}
                  {isOverdue && " (Overdue)"}
                  {isDueToday && " (Due Today)"}
                  {isFuture && " (Future)"}
                </h3>
                <div
                  className={`flex items-center text-sm ${
                    !task.completed && isDarkMode
                      ? "text-white"
                      : isDarkMode
                      ? "text-gray-400"
                      : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                      ? "text-green-500"
                      : "text-gray-500"
                  } mt-1`}
                >
                  <Calendar
                    className={`w-4 h-4 mr-1 ${
                      isDarkMode
                        ? "text-gray-200"
                        : isOceanBreeze
                        ? "text-blue-600"
                        : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
                    }`}
                  />{" "}
                  Due: {task.dueDate}
                  <span
                    className={`ml-3 px-2 py-1 rounded-full text-xs ${
                      task.priority === "High"
                        ? "bg-red-100 text-red-800"
                        : task.priority === "Medium"
                        ? "bg-yellow-100 text-yellow-800"
                        : "bg-blue-100 text-blue-800"
                    }`}
                  >
                    {task.priority}
                  </span>
                </div>
              </div>
            </div>
            <div className="flex space-x-2">
              <button
                onClick={() => handleStartEdit(task)}
                className={`${
                  isDarkMode
                    ? "text-gray-500 hover:text-gray-300"
                    : isOceanBreeze
                    ? "text-blue-600 hover:text-teal-600"
                    : isSunsetGlow
                    ? "text-orange-500 hover:text-orange-600"
                    : isForestWhisper
                    ? "text-green-500 hover:text-green-600"
                    : "text-gray-400 hover:text-gray-600"
                } transition-colors duration-200`}
              >
                <Edit3 className="w-5 h-5" />
              </button>
              <button
                onClick={() => confirmDeleteTask(task.id)}
                className={`${
                  isDarkMode
                    ? "text-gray-500 hover:text-gray-300"
                    : isOceanBreeze
                    ? "text-blue-600 hover:text-teal-600"
                    : isSunsetGlow
                    ? "text-orange-500 hover:text-orange-600"
                    : isForestWhisper
                    ? "text-green-500 hover:text-green-600"
                    : "text-gray-400 hover:text-gray-600"
                } transition-colors duration-200`}
              >
                <Trash2 className="w-5 h-5" />
              </button>
            </div>
          </div>
        )}
        {/* Delete Confirmation Dialog */}
        {showDeleteConfirm === task.id && (
          <div
            className={`mt-2 p-2 rounded-lg ${
              isDarkMode
                ? "bg-gray-800"
                : isOceanBreeze
                ? "bg-teal-100 bg-opacity-70 backdrop-blur-sm"
                : isSunsetGlow
                ? "bg-orange-100 bg-opacity-70 backdrop-blur-sm"
                : isForestWhisper
                ? "bg-green-100 bg-opacity-70 backdrop-blur-sm"
                : "bg-gray-100"
            }`}
          >
            <p
              className={`text-sm ${
                isDarkMode
                  ? "text-gray-200"
                  : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                  ? "text-orange-800"
                  : isForestWhisper
                  ? "text-green-800"
                  : "text-gray-800"
              }`}
            >
              Are you sure you want to delete "{task.title}"?
            </p>
            <div className="flex space-x-2 mt-2">
              <button
                onClick={() => handleDeleteTask(task.id)}
                className={`p-2 rounded flex items-center ${
                  isDarkMode
                    ? "bg-red-500 text-white hover:bg-red-600"
                    : isOceanBreeze
                    ? "bg-red-500 text-white hover:bg-red-600"
                    : isSunsetGlow
                    ? "bg-orange-500 text-white hover:bg-orange-600"
                    : isForestWhisper
                    ? "bg-green-500 text-white hover:bg-green-600"
                    : "bg-red-600 text-white hover:bg-red-700"
                } transition-colors duration-200`}
              >
                Yes, Delete
              </button>
              <button
                onClick={cancelDeleteTask}
                className={`p-2 rounded flex items-center ${
                  isDarkMode
                    ? "bg-gray-500 text-gray-200 hover:bg-gray-600"
                    : isOceanBreeze
                    ? "bg-blue-200 text-blue-700 hover:bg-blue-300"
                    : isSunsetGlow
                    ? "bg-orange-200 text-orange-700 hover:bg-orange-300"
                    : isForestWhisper
                    ? "bg-green-200 text-green-700 hover:bg-green-300"
                    : "bg-gray-300 text-gray-700 hover:bg-gray-400"
                } transition-colors duration-200`}
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <div
      className={`rounded-lg shadow ${
        currentTheme === "Dark Mode"
          ? "bg-gray-700"
          : currentTheme === "Ocean Breeze"
          ? "bg-opacity-70 backdrop-blur-lg"
          : currentTheme === "Sunset Glow"
          ? "bg-opacity-70 backdrop-blur-lg"
          : currentTheme === "Forest Whisper"
          ? "bg-opacity-70 backdrop-blur-lg"
          : "bg-white"
      }`}
    >
      <div
        className={`p-4 border-b ${
          currentTheme === "Dark Mode"
            ? "border-gray-600"
            : currentTheme === "Ocean Breeze"
            ? "border-teal-300"
            : currentTheme === "Sunset Glow"
            ? "border-orange-300"
            : currentTheme === "Forest Whisper"
            ? "border-green-300"
            : "border-gray-200"
        }`}
      >
        <h2
          className={`font-semibold ${
            currentTheme === "Dark Mode"
              ? "text-gray-200"
              : currentTheme === "Ocean Breeze"
              ? "text-teal-800"
              : currentTheme === "Sunset Glow"
              ? "text-orange-800"
              : currentTheme === "Forest Whisper"
              ? "text-green-800"
              : "text-gray-800"
          }`}
        >
          Manage Tasks
        </h2>
      </div>

      {/* Add Task Form */}
      <div
        className={`p-4 border-b ${
          currentTheme === "Dark Mode"
            ? "border-gray-600 bg-gray-600"
            : currentTheme === "Ocean Breeze"
            ? "border-teal-300 bg-amber-100 bg-opacity-70 backdrop-blur-sm"
            : currentTheme === "Sunset Glow"
            ? "border-orange-300 bg-pink-100 bg-opacity-70 backdrop-blur-sm"
            : currentTheme === "Forest Whisper"
            ? "border-green-300 bg-emerald-100 bg-opacity-70 backdrop-blur-sm"
            : "border-gray-200 bg-gray-50"
        }`}
      >
        <div className="flex flex-wrap md:flex-nowrap gap-3">
          <input
            type="text"
            placeholder="Task title"
            className={`flex-grow p-2 border rounded ${
              currentTheme === "Dark Mode"
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : currentTheme === "Sunset Glow"
                ? "bg-orange-100 border-orange-300 text-orange-800"
                : currentTheme === "Forest Whisper"
                ? "bg-green-100 border-green-300 text-green-800"
                : "bg-white border-gray-300 text-gray-800"
            } focus:outline-none focus:ring-2 ${
              currentTheme === "Sunset Glow"
                ? "focus:ring-orange-400"
                : currentTheme === "Forest Whisper"
                ? "focus:ring-green-400"
                : "focus:ring-indigo-400"
            } transition-all duration-200`}
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          />
          <input
            type="date"
            className={`p-2 border rounded ${
              currentTheme === "Dark Mode"
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : currentTheme === "Sunset Glow"
                ? "bg-orange-100 border-orange-300 text-orange-800"
                : currentTheme === "Forest Whisper"
                ? "bg-green-100 border-green-300 text-green-800"
                : "bg-white border-gray-300 text-gray-800"
            } focus:outline-none focus:ring-2 ${
              currentTheme === "Sunset Glow"
                ? "focus:ring-orange-400"
                : currentTheme === "Forest Whisper"
                ? "focus:ring-green-400"
                : "focus:ring-indigo-400"
            } transition-all duration-200`}
            value={newTask.dueDate}
            onChange={(e) =>
              setNewTask({ ...newTask, dueDate: e.target.value })
            }
          />
          <select
            className={`p-2 border rounded ${
              currentTheme === "Dark Mode"
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : currentTheme === "Sunset Glow"
                ? "bg-orange-100 border-orange-300 text-orange-800"
                : currentTheme === "Forest Whisper"
                ? "bg-green-100 border-green-300 text-green-800"
                : "bg-white border-gray-300 text-gray-800"
            } focus:outline-none focus:ring-2 ${
              currentTheme === "Sunset Glow"
                ? "focus:ring-orange-400"
                : currentTheme === "Forest Whisper"
                ? "focus:ring-green-400"
                : "focus:ring-indigo-400"
            } transition-all duration-200`}
            value={newTask.priority}
            onChange={(e) =>
              setNewTask({ ...newTask, priority: e.target.value })
            }
          >
            <option value="Low">Low Priority</option>
            <option value="Medium">Medium Priority</option>
            <option value="High">High Priority</option>
          </select>
          <button
            className={`p-2 rounded flex items-center ${
              currentTheme === "Dark Mode"
                ? "bg-indigo-500 text-white hover:bg-indigo-600"
                : currentTheme === "Ocean Breeze"
                ? "bg-teal-500 text-white hover:bg-teal-600"
                : currentTheme === "Sunset Glow"
                ? "bg-orange-500 text-white hover:bg-orange-600"
                : currentTheme === "Forest Whisper"
                ? "bg-green-500 text-white hover:bg-green-600"
                : "bg-indigo-600 text-white hover:bg-indigo-700"
            } transition-colors duration-200`}
            onClick={handleAddTask}
          >
            <PlusCircle className="w-4 h-4 mr-1" /> Add Task
          </button>
        </div>
      </div>

      {/* Sort and Filter Controls */}
      <div
        className={`p-4 flex flex-wrap gap-3 border-b ${
          currentTheme === "Dark Mode"
            ? "border-gray-600 bg-gray-600"
            : currentTheme === "Ocean Breeze"
            ? "border-teal-300 bg-amber-100 bg-opacity-70 backdrop-blur-sm"
            : currentTheme === "Sunset Glow"
            ? "border-orange-300 bg-pink-100 bg-opacity-70 backdrop-blur-sm"
            : currentTheme === "Forest Whisper"
            ? "border-green-300 bg-emerald-100 bg-opacity-70 backdrop-blur-sm"
            : "border-gray-200 bg-gray-50"
        }`}
      >
        <div className="flex items-center space-x-2">
          <ArrowUpDown
            className={`w-4 h-4 ${
              currentTheme === "Dark Mode"
                ? "text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "text-teal-600"
                : currentTheme === "Sunset Glow"
                ? "text-orange-600"
                : currentTheme === "Forest Whisper"
                ? "text-green-600"
                : "text-gray-500"
            }`}
          />
          <label
            className={`text-sm ${
              currentTheme === "Dark Mode"
                ? "text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "text-teal-800"
                : currentTheme === "Sunset Glow"
                ? "text-orange-800"
                : currentTheme === "Forest Whisper"
                ? "text-green-800"
                : "text-gray-800"
            }`}
          >
            Sort by:
          </label>
          <select
            className={`p-2 border rounded ${
              currentTheme === "Dark Mode"
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : currentTheme === "Sunset Glow"
                ? "bg-orange-100 border-orange-300 text-orange-800"
                : currentTheme === "Forest Whisper"
                ? "bg-green-100 border-green-300 text-green-800"
                : "bg-white border-gray-300 text-gray-800"
            } focus:outline-none focus:ring-2 ${
              currentTheme === "Sunset Glow"
                ? "focus:ring-orange-400"
                : currentTheme === "Forest Whisper"
                ? "focus:ring-green-400"
                : "focus:ring-indigo-400"
            } transition-all duration-200`}
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="dueDateAsc">Due Date (Earliest First)</option>
            <option value="dueDateDesc">Due Date (Latest First)</option>
            <option value="priorityAsc">Priority (Low to High)</option>
            <option value="priorityDesc">Priority (High to Low)</option>
          </select>
        </div>
        <div className="flex items-center space-x-2">
          <Filter
            className={`w-4 h-4 ${
              currentTheme === "Dark Mode"
                ? "text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "text-teal-600"
                : currentTheme === "Sunset Glow"
                ? "text-orange-600"
                : currentTheme === "Forest Whisper"
                ? "text-green-600"
                : "text-gray-500"
            }`}
          />
          <label
            className={`text-sm ${
              currentTheme === "Dark Mode"
                ? "text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "text-teal-800"
                : currentTheme === "Sunset Glow"
                ? "text-orange-800"
                : currentTheme === "Forest Whisper"
                ? "text-green-800"
                : "text-gray-800"
            }`}
          >
            Filter:
          </label>
          <select
            className={`p-2 border rounded ${
              currentTheme === "Dark Mode"
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : currentTheme === "Ocean Breeze"
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : currentTheme === "Sunset Glow"
                ? "bg-orange-100 border-orange-300 text-orange-800"
                : currentTheme === "Forest Whisper"
                ? "bg-green-100 border-green-300 text-green-800"
                : "bg-white border-gray-300 text-gray-800"
            } focus:outline-none focus:ring-2 ${
              currentTheme === "Sunset Glow"
                ? "focus:ring-orange-400"
                : currentTheme === "Forest Whisper"
                ? "focus:ring-green-400"
                : "focus:ring-indigo-400"
            } transition-all duration-200`}
            value={filterBy}
            onChange={(e) => setFilterBy(e.target.value)}
          >
            <option value="all">All Tasks</option>
            <option value="completed">Completed Tasks</option>
            <option value="incomplete">Incomplete Tasks</option>
          </select>
        </div>
      </div>

      {/* Task List */}
      <div
        className={`divide-y ${
          currentTheme === "Dark Mode"
            ? "divide-gray-600"
            : currentTheme === "Ocean Breeze"
            ? "divide-teal-300"
            : currentTheme === "Sunset Glow"
            ? "divide-orange-300"
            : currentTheme === "Forest Whisper"
            ? "divide-green-300"
            : "divide-gray-200"
        }`}
      >
        {sortedAndFilteredTasks.map((task) => renderTask(task))}
        {sortedAndFilteredTasks.length === 0 && (
          <div
            className={`py-12 text-center ${
              currentTheme === "Dark Mode"
                ? "text-gray-400"
                : currentTheme === "Ocean Breeze"
                ? "text-blue-600"
                : currentTheme === "Sunset Glow"
                ? "text-orange-500"
                : currentTheme === "Forest Whisper"
                ? "text-green-500"
                : "text-gray-500"
            }`}
          >
            {filterBy === "completed"
              ? "No completed tasks yet."
              : filterBy === "incomplete"
              ? "No incomplete tasks yet."
              : "No tasks yet. Add your first task above!"}
          </div>
        )}
      </div>
    </div>
  );
}

export default TaskManager;
