// src/services/aiService.js

export const getStudySuggestion = async (tasks, studyHabits) => {
  try {
    const response = await fetch("http://localhost:5000/api/ai/suggestion", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ tasks, studyHabits }),
    });
    const data = await response.json();
    if (!response.ok) {
      throw new Error(data.error || "Failed to fetch AI suggestion");
    }
    return data.suggestion;
  } catch (error) {
    console.error("Error getting AI suggestion:", error);
    return "I'm having trouble generating suggestions right now. Please try again later.";
  }
};
