import React, { useState, useEffect } from "react";
import {
  Calendar,
  Clock,
  BookOpen,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
  Save,
  X,
} from "lucide-react";

function StudyBuddyPlanner() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    dueDate: "",
    priority: "Medium",
    completed: false,
  });
  const [editingTask, setEditingTask] = useState(null);
  const [studyStats, setStudyStats] = useState({
    totalHours: 0,
    completedTasks: 0,
    streak: 5,
  });
  const [aiSuggestion, setAiSuggestion] = useState("");

  const schedules = [
    {
      name: "Schedule 1",
      content:
        "Based on your profile as a Computer Engineering student, here's a suggested schedule:\n\n" +
        "- Tuesday (Today): Math Assignment (2 hours) in the afternoon\n" +
        "- Wednesday: Data Structures practice (1.5 hours) in the morning, Programming Assignment (1 hour) in the afternoon\n" +
        "- Thursday: Algorithms review (1.5 hours), Database Design (1 hour)\n" +
        "- Friday: Project work (2 hours), Code optimization (1 hour)\n" +
        "- Saturday: System Design study (2 hours), review progress (1 hour)\n" +
        "- Sunday: Rest and prepare for next week",
    },
    {
      name: "Schedule 2",
      content:
        "Tailored for a Computer Engineering student, here's your schedule:\n\n" +
        "- Tuesday (Today): Math Assignment (2 hours) in the afternoon\n" +
        "- Wednesday: Programming Assignment (1.5 hours) in the morning, Data Structures (1 hour) in the afternoon\n" +
        "- Thursday: Algorithms practice (1.5 hours), Project work (1 hour)\n" +
        "- Friday: Database Design (2 hours), Code debugging (1 hour)\n" +
        "- Saturday: Software Engineering concepts (2 hours), review (1 hour)\n" +
        "- Sunday: Plan next week's tasks",
    },
    {
      name: "Schedule 3",
      content:
        "Optimized schedule for a Computer Engineering student:\n\n" +
        "- Tuesday (Today): Math Assignment (2 hours) in the afternoon\n" +
        "- Wednesday: Algorithms revision (1.5 hours) in the morning, Programming (1 hour) in the afternoon\n" +
        "- Thursday: Data Structures exercises (1.5 hours), Project work (1 hour)\n" +
        "- Friday: Database Design (2 hours), System Architecture (1 hour)\n" +
        "- Saturday: Code review (2 hours), study Operating Systems (1 hour)\n" +
        "- Sunday: Reflect and adjust goals",
    },
    {
      name: "Schedule 4",
      content:
        "Custom schedule for a Computer Engineering student:\n\n" +
        "- Tuesday (Today): Math Assignment (2 hours) in the afternoon\n" +
        "- Wednesday: Programming Assignment (1.5 hours) in the morning, Algorithms (1 hour) in the afternoon\n" +
        "- Thursday: Data Structures (1.5 hours), Project work (1 hour)\n" +
        "- Friday: Database Design (2 hours), Networking basics (1 hour)\n" +
        "- Saturday: Software Testing (2 hours), review notes (1 hour)\n" +
        "- Sunday: Prepare for upcoming lectures",
    },
    {
      name: "Schedule 5",
      content:
        "Balanced schedule for a Computer Engineering student:\n\n" +
        "- Tuesday (Today): Math Assignment (2 hours) in the afternoon\n" +
        "- Wednesday: Data Structures (1.5 hours) in the morning, Programming (1 hour) in the afternoon\n" +
        "- Thursday: Algorithms (1.5 hours), Project work (1 hour)\n" +
        "- Friday: Database Design (2 hours), Cloud Computing (1 hour)\n" +
        "- Saturday: System Analysis (2 hours), code practice (1 hour)\n" +
        "- Sunday: Review and relax",
    },
  ];

  useEffect(() => {
    const savedTasks = localStorage.getItem("studyBuddyTasks");
    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    } else {
      setTasks([
        {
          id: 1,
          title: "Complete Math Assignment",
          dueDate: "2025-04-25",
          priority: "High",
          completed: false,
        },
        {
          id: 2,
          title: "Review Notes",
          dueDate: "2025-04-23",
          priority: "Medium",
          completed: true,
        },
        {
          id: 3,
          title: "Research Paper Outline",
          dueDate: "2025-04-30",
          priority: "High",
          completed: false,
        },
      ]);
    }

    const savedStats = localStorage.getItem("studyBuddyStats");
    if (savedStats) {
      setStudyStats(JSON.parse(savedStats));
    }

    setTimeout(() => {
      setAiSuggestion(
        "Based on your schedule and learning patterns, I recommend focusing on your Math Assignment today. You tend to work best on math problems in the afternoon."
      );
    }, 1000);
  }, []);

  useEffect(() => {
    localStorage.setItem("studyBuddyTasks", JSON.stringify(tasks));

    const completedCount = tasks.filter((task) => task.completed).length;
    setStudyStats((prevStats) => {
      const newStats = {
        ...prevStats,
        completedTasks: completedCount,
      };
      localStorage.setItem("studyBuddyStats", JSON.stringify(newStats));
      return newStats;
    });
  }, [tasks]);

  const handleAddTask = () => {
    if (!newTask.title) return;

    const task = {
      id: Date.now(),
      ...newTask,
    };

    setTasks([...tasks, task]);
    setNewTask({
      title: "",
      dueDate: "",
      priority: "Medium",
      completed: false,
    });
  };

  const handleDeleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const handleToggleComplete = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const handleStartEdit = (task) => {
    setEditingTask({ ...task });
  };

  const handleSaveEdit = () => {
    if (!editingTask) return;

    setTasks(
      tasks.map((task) => (task.id === editingTask.id ? editingTask : task))
    );

    setEditingTask(null);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const getStudyTips = () => {
    setAiSuggestion("Loading new suggestions...");

    setTimeout(() => {
      const tips = [
        "Try using the Pomodoro technique: 25 minutes of focused study followed by a 5-minute break.",
        "For difficult subjects, try teaching the material to an imaginary student to improve your understanding.",
        "Create visual mind maps to connect key concepts in your subjects.",
        "Review your notes within 24 hours of taking them to improve retention.",
        "Study in short, frequent sessions rather than long cramming sessions.",
      ];

      const randomTip = tips[Math.floor(Math.random() * tips.length)];
      setAiSuggestion(randomTip);
    }, 1500);
  };

  const generateSchedule = () => {
    setAiSuggestion("Generating personalized study schedule...");
    setTimeout(() => {
      const randomSchedule =
        schedules[Math.floor(Math.random() * schedules.length)].content;
      setAiSuggestion(randomSchedule);
    }, 2000);
  };

  const getTaskCompletionRate = () => {
    if (tasks.length === 0) return 0;
    const completedTasks = tasks.filter((task) => task.completed).length;
    return Math.round((completedTasks / tasks.length) * 100);
  };

  return (
    <div className="min-h-screen bg-slate-50 text-gray-800">
      <header className="bg-indigo-600 text-white p-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold flex items-center">
            <BookOpen className="mr-2" />
            StudyBuddy AI Planner
          </h1>
          <div className="bg-indigo-700 px-4 py-2 rounded-lg text-sm">
            {new Date().toLocaleDateString("en-US", {
              weekday: "long",
              year: "numeric",
              month: "long",
              day: "numeric",
            })}
          </div>
        </div>
      </header>

      <nav className="bg-white shadow">
        <div className="container mx-auto">
          <div className="flex">
            <button
              onClick={() => setActiveTab("dashboard")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "dashboard"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Dashboard
            </button>
            <button
              onClick={() => setActiveTab("tasks")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "tasks"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Tasks
            </button>
            <button
              onClick={() => setActiveTab("analytics")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "analytics"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Analytics
            </button>
          </div>
        </div>
      </nav>

      <main className="container mx-auto p-4">
        {activeTab === "dashboard" && (
          <div className="space-y-6">
            <div className="bg-white rounded-lg shadow p-6">
              <h2 className="text-lg font-semibold mb-4 flex items-center">
                <div className="w-8 h-8 rounded-full bg-indigo-100 flex items-center justify-center mr-2">
                  <div className="w-4 h-4 rounded-full bg-indigo-600"></div>
                </div>
                AI Study Assistant
              </h2>
              <div className="bg-indigo-50 p-4 rounded-lg border border-indigo-100 min-h-[100px]">
                {aiSuggestion ? (
                  <div className="whitespace-pre-line">{aiSuggestion}</div>
                ) : (
                  <div className="flex items-center justify-center h-16">
                    <div className="animate-pulse flex space-x-2">
                      <div className="w-2 h-2 bg-indigo-400 rounded-full"></div>
                      <div className="w-2 h-2 bg-indigo-400 rounded-full"></div>
                      <div className="w-2 h-2 bg-indigo-400 rounded-full"></div>
                    </div>
                  </div>
                )}
              </div>
              <div className="mt-4 flex space-x-2">
                <button
                  onClick={getStudyTips}
                  className="bg-indigo-600 text-white px-4 py-2 rounded-lg text-sm"
                >
                  Get Study Tips
                </button>
                <button
                  onClick={generateSchedule}
                  className="bg-white border border-gray-300 text-gray-700 px-4 py-2 rounded-lg text-sm"
                >
                  Generate Schedule
                </button>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="bg-white rounded-lg shadow p-6">
                <div className="flex justify-between items-center">
                  <h3 className="text-gray-500 text-sm">Study Hours</h3>
                  <Clock className="text-indigo-600 h-5 w-5" />
                </div>
                <p className="text-3xl font-bold mt-2">
                  {studyStats.totalHours}h
                </p>
                <p className="text-green-600 text-sm mt-2">
                   2.5h from last week
                </p>
              </div>

              <div className="bg-white rounded-lg shadow p-6">
                <div className="flex justify-between items-center">
                  <h3 className="text-gray-500 text-sm">Tasks Completed</h3>
                  <CheckCircle className="text-indigo-600 h-5 w-5" />
                </div>
                <p className="text-3xl font-bold mt-2">
                  {studyStats.completedTasks}
                </p>
                <p className="text-green-600 text-sm mt-2">
                   3 from last week
                </p>
              </div>

              <div className="bg-white rounded-lg shadow p-6">
                <div className="flex justify-between items-center">
                  <h3 className="text-gray-500 text-sm">Study Streak</h3>
                  <Calendar className="text-indigo-600 h-5 w-5" />
                </div>
                <p className="text-3xl font-bold mt-2">
                  {studyStats.streak} days
                </p>
                <div className="flex space-x-1 mt-2">
                  {[1, 2, 3, 4, 5, 6, 7].map((day) => (
                    <div
                      key={day}
                      className={`w-6 h-6 rounded-full flex items-center justify-center text-xs
                      ${
                        day <= studyStats.streak
                          ? "bg-indigo-600 text-white"
                          : "bg-gray-100"
                      }`}
                    >
                      {day}
                    </div>
                  ))}
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow">
              <div className="p-4 border-b border-gray-200">
                <h2 className="font-semibold">Upcoming Tasks</h2>
              </div>
              <div className="p-2">
                {tasks
                  .filter((task) => !task.completed)
                  .slice(0, 3)
                  .map((task) => (
                    <div
                      key={task.id}
                      className="p-2 hover:bg-gray-50 rounded-md flex justify-between items-center"
                    >
                      <div className="flex items-center">
                        <button
                          onClick={() => handleToggleComplete(task.id)}
                          className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center
                        ${
                          task.priority === "High"
                            ? "border-red-500"
                            : "border-yellow-500"
                        }`}
                        >
                          {task.completed && (
                            <CheckCircle className="w-4 h-4 text-green-600" />
                          )}
                        </button>
                        <div>
                          <h3 className="text-sm font-medium">{task.title}</h3>
                          <span className="text-xs text-gray-500">
                            Due: {task.dueDate}
                          </span>
                        </div>
                      </div>
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleStartEdit(task)}
                          className="text-gray-400 hover:text-gray-600"
                        >
                          <Edit3 className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => handleDeleteTask(task.id)}
                          className="text-gray-400 hover:text-gray-600"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  ))}
                {tasks.filter((task) => !task.completed).length === 0 && (
                  <div className="py-8 text-center text-gray-500">
                    No upcoming tasks. Add one below!
                  </div>
                )}
              </div>
              <div className="p-4 border-t border-gray-200">
                <button
                  onClick={() => setActiveTab("tasks")}
                  className="text-indigo-600 text-sm font-medium flex items-center"
                >
                  <PlusCircle className="w-4 h-4 mr-1" />
                  Add New Task
                </button>
              </div>
            </div>
          </div>
        )}

        {activeTab === "tasks" && (
          <div className="bg-white rounded-lg shadow">
            <div className="p-4 border-b border-gray-200">
              <h2 className="font-semibold">Manage Tasks</h2>
            </div>

            <div className="p-4 border-b border-gray-200 bg-gray-50">
              <div className="flex flex-wrap md:flex-nowrap gap-3">
                <input
                  type="text"
                  placeholder="Task title"
                  className="flex-grow p-2 border rounded"
                  value={newTask.title}
                  onChange={(e) =>
                    setNewTask({ ...newTask, title: e.target.value })
                  }
                />
                <input
                  type="date"
                  className="p-2 border rounded"
                  value={newTask.dueDate}
                  onChange={(e) =>
                    setNewTask({ ...newTask, dueDate: e.target.value })
                  }
                />
                <select
                  className="p-2 border rounded"
                  value={newTask.priority}
                  onChange={(e) =>
                    setNewTask({ ...newTask, priority: e.target.value })
                  }
                >
                  <option value="Low">Low Priority</option>
                  <option value="Medium">Medium Priority</option>
                  <option value="High">High Priority</option>
                </select>
                <button
                  className="bg-indigo-600 text-white p-2 rounded flex items-center"
                  onClick={handleAddTask}
                >
                  <PlusCircle className="w-4 h-4 mr-1" /> Add Task
                </button>
              </div>
            </div>

            <div className="divide-y divide-gray-200">
              {tasks.map((task) => (
                <div key={task.id} className="p-4 hover:bg-gray-50">
                  {editingTask && editingTask.id === task.id ? (
                    <div className="flex flex-wrap gap-2">
                      <input
                        type="text"
                        className="flex-grow p-2 border rounded"
                        value={editingTask.title}
                        onChange={(e) =>
                          setEditingTask({
                            ...editingTask,
                            title: e.target.value,
                          })
                        }
                      />
                      <input
                        type="date"
                        className="p-2 border rounded"
                        value={editingTask.dueDate}
                        onChange={(e) =>
                          setEditingTask({
                            ...editingTask,
                            dueDate: e.target.value,
                          })
                        }
                      />
                      <select
                        className="p-2 border rounded"
                        value={editingTask.priority}
                        onChange={(e) =>
                          setEditingTask({
                            ...editingTask,
                            priority: e.target.value,
                          })
                        }
                      >
                        <option value="Low">Low Priority</option>
                        <option value="Medium">Medium Priority</option>
                        <option value="High">High Priority</option>
                      </select>
                      <div className="flex space-x-2">
                        <button
                          onClick={handleSaveEdit}
                          className="bg-green-600 text-white p-2 rounded flex items-center"
                        >
                          <Save className="w-4 h-4 mr-1" /> Save
                        </button>
                        <button
                          onClick={handleCancelEdit}
                          className="bg-gray-300 text-gray-700 p-2 rounded flex items-center"
                        >
                          <X className="w-4 h-4 mr-1" /> Cancel
                        </button>
                      </div>
                    </div>
                  ) : (
                    <div className="flex justify-between items-center">
                      <div className="flex items-center">
                        <button
                          onClick={() => handleToggleComplete(task.id)}
                          className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center
                          ${
                            task.completed
                              ? "bg-green-50 border-green-500"
                              : task.priority === "High"
                              ? "border-red-500"
                              : task.priority === "Medium"
                              ? "border-yellow-500"
                              : "border-blue-400"
                          }`}
                        >
                          {task.completed && (
                            <CheckCircle className="w-4 h-4 text-green-600" />
                          )}
                        </button>
                        <div
                          className={
                            task.completed ? "line-through text-gray-400" : ""
                          }
                        >
                          <h3 className="font-medium">{task.title}</h3>
                          <div className="flex items-center text-sm text-gray-500 mt-1">
                            <Calendar className="w-4 h-4 mr-1" /> Due:{" "}
                            {task.dueDate}
                            <span
                              className={`ml-3 px-2 py-1 rounded-full text-xs
                              ${
                                task.priority === "High"
                                  ? "bg-red-100 text-red-800"
                                  : task.priority === "Medium"
                                  ? "bg-yellow-100 text-yellow-800"
                                  : "bg-blue-100 text-blue-800"
                              }`}
                            >
                              {task.priority}
                            </span>
                          </div>
                        </div>
                      </div>
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleStartEdit(task)}
                          className="text-gray-400 hover:text-gray-600"
                        >
                          <Edit3 className="w-5 h-5" />
                        </button>
                        <button
                          onClick={() => handleDeleteTask(task.id)}
                          className="text-gray-400 hover:text-gray-600"
                        >
                          <Trash2 className="w-5 h-5" />
                        </button>
                      </div>
                    </div>
                  )}
                </div>
              ))}
              {tasks.length === 0 && (
                <div className="py-12 text-center text-gray-500">
                  No tasks yet. Add your first task above!
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === "analytics" && (
          <div className="bg-white rounded-lg shadow">
            <div className="p-4 border-b border-gray-200">
              <h2 className="font-semibold">Study Analytics</h2>
            </div>
            <div className="p-8 text-center">
              <div className="inline-block p-12 bg-gray-100 rounded-full mb-4">
                <div className="text-4xl font-bold text-indigo-600">
                  {getTaskCompletionRate()}%
                </div>
              </div>
              <h3 className="text-lg font-medium">Task Completion Rate</h3>
              <p className="text-gray-500 mt-2">
                You've completed {tasks.filter((task) => task.completed).length}{" "}
                out of {tasks.length} tasks
              </p>

              <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h4 className="font-medium mb-2">Subject Distribution</h4>
                  <div className="flex justify-center space-x-2 mt-4">
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-20 bg-indigo-600 rounded-t-sm"></div>
                      <span className="text-xs mt-1">Math</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-16 bg-indigo-500 rounded-t-sm"></div>
                      <span className="text-xs mt-1">Programming</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-12 bg-indigo-400 rounded-t-sm"></div>
                      <span className="text-xs mt-1">Algorithms</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-8 bg-indigo-300 rounded-t-sm"></div>
                      <span className="text-xs mt-1">DB Design</span>
                    </div>
                  </div>
                </div>

                <div className="bg-gray-50 p-4 rounded-lg">
                  <h4 className="font-medium mb-2">Study Time by Day</h4>
                  <div className="flex justify-center space-x-2 mt-4">
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-8 bg-indigo-300 rounded-t-sm"></div>
                      <span className="text-xs mt-1">M</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-12 bg-indigo-400 rounded-t-sm"></div>
                      <span className="text-xs mt-1">T</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-16 bg-indigo-500 rounded-t-sm"></div>
                      <span className="text-xs mt-1">W</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-20 bg-indigo-600 rounded-t-sm"></div>
                      <span className="text-xs mt-1">T</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-10 bg-indigo-400 rounded-t-sm"></div>
                      <span className="text-xs mt-1">F</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-6 bg-indigo-200 rounded-t-sm"></div>
                      <span className="text-xs mt-1">S</span>
                    </div>
                    <div className="flex flex-col items-center">
                      <div className="w-4 h-4 bg-indigo-100 rounded-t-sm"></div>
                      <span className="text-xs mt-1">S</span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="mt-8 p-4 bg-indigo-50 rounded-lg text-left">
                <h4 className="font-medium mb-2">AI Insights</h4>
                <ul className="space-y-2 text-sm">
                  <li className="flex items-start">
                    <div className="w-4 h-4 rounded-full bg-green-500 mt-1 mr-2"></div>
                    <p>
                      You're most productive on Thursday afternoons between
                      2-4pm
                    </p>
                  </li>
                  <li className="flex items-start">
                    <div className="w-4 h-4 rounded-full bg-yellow-500 mt-1 mr-2"></div>
                    <p>
                      Math assignments take you 30% longer than other subjects
                    </p>
                  </li>
                  <li className="flex items-start">
                    <div className="w-4 h-4 rounded-full bg-blue-500 mt-1 mr-2"></div>
                    <p>
                      You complete tasks more efficiently when you break them
                      into 25-minute focused sessions
                    </p>
                  </li>
                </ul>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default StudyBuddyPlanner;
