import React, { useState, useMemo } from "react";
import {
  Calendar,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
  Save,
  X,
  ArrowUpDown,
  Filter,
} from "lucide-react";
import { toast } from "react-toastify";

// Theme configuration for consistent styling
const themeStyles = {
  "Light Mode": {
    bg: "bg-white",
    border: "border-gray-200",
    divide: "divide-gray-200",
    text: "text-gray-800",
    subText: "text-gray-500",
    cardBg: "bg-gray-50",
    inputBg: "bg-white border-gray-300 text-gray-800",
    inputFocus: "focus:ring-indigo-400",
    buttonPrimary: "bg-indigo-600 text-white hover:bg-indigo-700",
    buttonSecondary: "bg-gray-300 text-gray-700 hover:bg-gray-400",
    buttonSuccess: "bg-green-600 text-white hover:bg-green-700",
    buttonDanger: "bg-red-600 text-white hover:bg-red-700",
    hoverBg: "hover:bg-gray-50",
    overdue: "bg-red-50 bg-opacity-50 text-red-600",
    dueToday: "bg-orange-50 bg-opacity-50 text-orange-600",
    future: "bg-green-50 bg-opacity-50 text-green-600",
    priorityHigh: "bg-red-100 text-red-800",
    priorityMedium: "bg-yellow-100 text-yellow-800",
    priorityLow: "bg-blue-100 text-blue-800",
    completed: "bg-green-50 border-green-500",
    icon: "text-gray-500",
    statusEarned: "text-green-600",
    statusNotEarned: "text-gray-400",
  },
  "Dark Mode": {
    bg: "bg-gray-700",
    border: "border-gray-600",
    divide: "divide-gray-600",
    text: "text-gray-200",
    subText: "text-gray-400",
    cardBg: "bg-gray-600",
    inputBg: "bg-gray-800 border-gray-500 text-gray-200",
    inputFocus: "focus:ring-indigo-400",
    buttonPrimary: "bg-indigo-500 text-white hover:bg-indigo-600",
    buttonSecondary: "bg-gray-500 text-gray-200 hover:bg-gray-600",
    buttonSuccess: "bg-green-500 text-white hover:bg-green-600",
    buttonDanger: "bg-red-500 text-white hover:bg-red-600",
    hoverBg: "hover:bg-gray-600",
    overdue: "bg-opacity-50 text-red-400",
    dueToday: "bg-opacity-50 text-orange-400",
    future: "bg-opacity-50 text-green-400",
    priorityHigh: "bg-red-100 text-red-800",
    priorityMedium: "bg-yellow-100 text-yellow-800",
    priorityLow: "bg-blue-100 text-blue-800",
    completed: "bg-green-900 border-green-600",
    icon: "text-gray-200",
    statusEarned: "text-green-600",
    statusNotEarned: "text-gray-500",
  },
  "Ocean Breeze": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-teal-300",
    divide: "divide-teal-300",
    text: "text-teal-800",
    subText: "text-blue-600",
    cardBg: "bg-amber-100 bg-opacity-70 backdrop-blur-sm",
    inputBg: "bg-blue-100 border-teal-300 text-teal-800",
    inputFocus: "focus:ring-indigo-400",
    buttonPrimary: "bg-teal-500 text-white hover:bg-teal-600",
    buttonSecondary: "bg-blue-200 text-blue-700 hover:bg-blue-300",
    buttonSuccess: "bg-teal-500 text-white hover:bg-teal-600",
    buttonDanger: "bg-red-500 text-white hover:bg-red-600",
    hoverBg: "hover:bg-teal-100",
    overdue: "bg-opacity-50 text-red-600",
    dueToday: "bg-opacity-50 text-orange-500",
    future: "bg-opacity-50 text-green-600",
    priorityHigh: "bg-red-100 text-red-800",
    priorityMedium: "bg-yellow-100 text-yellow-800",
    priorityLow: "bg-blue-100 text-blue-800",
    completed: "bg-teal-100 border-teal-500",
    icon: "text-blue-600",
    statusEarned: "text-green-600",
    statusNotEarned: "text-blue-500",
  },
  "Sunset Glow": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-orange-300",
    divide: "divide-orange-300",
    text: "text-orange-800",
    subText: "text-orange-500",
    cardBg: "bg-pink-100 bg-opacity-70 backdrop-blur-sm",
    inputBg: "bg-orange-100 border-orange-300 text-orange-800",
    inputFocus: "focus:ring-orange-400",
    buttonPrimary: "bg-orange-500 text-white hover:bg-orange-600",
    buttonSecondary: "bg-orange-200 text-orange-700 hover:bg-orange-300",
    buttonSuccess: "bg-orange-500 text-white hover:bg-orange-600",
    buttonDanger: "bg-orange-500 text-white hover:bg-orange-600",
    hoverBg: "hover:bg-orange-100",
    overdue: "bg-opacity-50 text-red-700",
    dueToday: "bg-opacity-50 text-orange-600",
    future: "bg-opacity-50 text-green-600",
    priorityHigh: "bg-red-100 text-red-800",
    priorityMedium: "bg-yellow-100 text-yellow-800",
    priorityLow: "bg-blue-100 text-blue-800",
    completed: "bg-orange-100 border-orange-500",
    icon: "text-orange-500",
    statusEarned: "text-green-600",
    statusNotEarned: "text-orange-400",
  },
  "Forest Whisper": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-green-300",
    divide: "divide-green-300",
    text: "text-green-800",
    subText: "text-green-500",
    cardBg: "bg-emerald-100 bg-opacity-70 backdrop-blur-sm",
    inputBg: "bg-green-100 border-green-300 text-green-800",
    inputFocus: "focus:ring-green-400",
    buttonPrimary: "bg-green-500 text-white hover:bg-green-600",
    buttonSecondary: "bg-green-200 text-green-700 hover:bg-green-300",
    buttonSuccess: "bg-green-500 text-white hover:bg-green-600",
    buttonDanger: "bg-green-500 text-white hover:bg-green-600",
    hoverBg: "hover:bg-green-100",
    overdue: "bg-opacity-50 text-red-600",
    dueToday: "bg-opacity-50 text-orange-500",
    future: "bg-opacity-50 text-green-500",
    priorityHigh: "bg-red-100 text-red-800",
    priorityMedium: "bg-yellow-100 text-yellow-800",
    priorityLow: "bg-blue-100 text-blue-800",
    completed: "bg-green-100 border-green-500",
    icon: "text-green-500",
    statusEarned: "text-green-600",
    statusNotEarned: "text-green-400",
  },
};

function TaskManager({
  tasks,
  addTask,
  updateTask,
  deleteTask,
  toggleTaskComplete,
  currentTheme,
}) {
  const [newTask, setNewTask] = useState({
    title: "",
    dueDate: "",
    priority: "Medium",
    completed: false,
  });
  const [editingTask, setEditingTask] = useState(null);
  const [sortBy, setSortBy] = useState("dueDateAsc");
  const [filterBy, setFilterBy] = useState("all");
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(null);

  // Select theme styles, default to Light Mode if theme not found
  const currentThemeStyles =
    themeStyles[currentTheme] || themeStyles["Light Mode"];

  const today = new Date().toISOString().split("T")[0];

  const handleAddTask = () => {
    if (!newTask.title) {
      toast.error("Task title is required.", { autoClose: 2000 });
      return;
    }
    if (!newTask.dueDate) {
      toast.error("Due date is required.", { autoClose: 2000 });
      return;
    }
    if (newTask.dueDate < today) {
      toast.error("Due date cannot be in the past.", { autoClose: 2000 });
      return;
    }

    addTask(newTask);
    setNewTask({
      title: "",
      dueDate: "",
      priority: "Medium",
      completed: false,
    });
    toast.success("Task added successfully!", { autoClose: 2000 });
  };

  const handleStartEdit = (task) => {
    setEditingTask({ ...task });
  };

  const handleSaveEdit = () => {
    if (!editingTask) return;
    if (!editingTask.title) {
      toast.error("Task title is required.", { autoClose: 2000 });
      return;
    }
    if (!editingTask.dueDate) {
      toast.error("Due date is required.", { autoClose: 2000 });
      return;
    }
    if (editingTask.dueDate < today) {
      toast.error("Due date cannot be in the past.", { autoClose: 2000 });
      return;
    }

    updateTask(editingTask);
    setEditingTask(null);
    toast.success("Task updated successfully!", { autoClose: 2000 });
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const confirmDeleteTask = (id) => {
    setShowDeleteConfirm(id);
  };

  const handleDeleteTask = (id) => {
    deleteTask(id);
    setShowDeleteConfirm(null);
    toast.success("Task deleted successfully!", { autoClose: 2000 });
  };

  const cancelDeleteTask = () => {
    setShowDeleteConfirm(null);
  };

  const priorityValue = { High: 3, Medium: 2, Low: 1 };

  // Memoized sorted and filtered tasks
  const sortedAndFilteredTasks = useMemo(() => {
    return [...tasks]
      .filter((task) => {
        if (filterBy === "completed") return task.completed;
        if (filterBy === "incomplete") return !task.completed;
        return true;
      })
      .sort((a, b) => {
        if (sortBy === "dueDateAsc") {
          return new Date(a.dueDate) - new Date(b.dueDate);
        } else if (sortBy === "dueDateDesc") {
          return new Date(b.dueDate) - new Date(a.dueDate);
        } else if (sortBy === "priorityAsc") {
          return priorityValue[a.priority] - priorityValue[b.priority];
        } else if (sortBy === "priorityDesc") {
          return priorityValue[b.priority] - priorityValue[a.priority];
        }
        return 0;
      });
  }, [tasks, sortBy, filterBy]);

  return (
    <div className={`rounded-lg shadow ${currentThemeStyles.bg}`}>
      <div className={`p-4 border-b ${currentThemeStyles.border}`}>
        <h2 className={`font-semibold ${currentThemeStyles.text}`}>
          Manage Tasks
        </h2>
      </div>

      {/* Add Task Form */}
      <div
        className={`p-4 sm:p-6 border-b ${currentThemeStyles.border} ${currentThemeStyles.cardBg}`}
      >
        <div className="flex flex-wrap md:flex-nowrap gap-3">
          <div className="flex-grow">
            <label htmlFor="task-title" className="sr-only">
              Task Title
            </label>
            <input
              id="task-title"
              type="text"
              placeholder="Task title"
              className={`w-full p-2 border rounded ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.inputFocus} transition-all duration-200`}
              value={newTask.title}
              onChange={(e) =>
                setNewTask({ ...newTask, title: e.target.value })
              }
            />
          </div>
          <div>
            <label htmlFor="task-due-date" className="sr-only">
              Due Date
            </label>
            <input
              id="task-due-date"
              type="date"
              className={`w-full p-2 border rounded ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.inputFocus} transition-all duration-200`}
              value={newTask.dueDate}
              onChange={(e) =>
                setNewTask({ ...newTask, dueDate: e.target.value })
              }
            />
          </div>
          <div>
            <label htmlFor="task-priority" className="sr-only">
              Priority
            </label>
            <select
              id="task-priority"
              className={`w-full p-2 border rounded ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.inputFocus} transition-all duration-200`}
              value={newTask.priority}
              onChange={(e) =>
                setNewTask({ ...newTask, priority: e.target.value })
              }
            >
              <option value="Low">Low Priority</option>
              <option value="Medium">Medium Priority</option>
              <option value="High">High Priority</option>
            </select>
          </div>
          <button
            className={`p-2 rounded flex items-center ${currentThemeStyles.buttonPrimary} transition-colors duration-200`}
            onClick={handleAddTask}
            aria-label="Add new task"
          >
            <PlusCircle className="w-4 h-4 mr-1" aria-hidden="true" /> Add Task
          </button>
        </div>
      </div>

      {/* Sort and Filter Controls */}
      <div
        className={`p-4 sm:p-6 flex flex-wrap gap-3 border-b ${currentThemeStyles.border} ${currentThemeStyles.cardBg}`}
      >
        <div className="flex items-center space-x-2">
          <ArrowUpDown
            className={`w-4 h-4 ${currentThemeStyles.icon}`}
            aria-hidden="true"
          />
          <label className={`text-sm ${currentThemeStyles.text}`}>
            Sort by:
          </label>
          <select
            className={`p-2 border rounded ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.inputFocus} transition-all duration-200`}
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            aria-label="Sort tasks by"
          >
            <option value="dueDateAsc">Due Date (Earliest First)</option>
            <option value="dueDateDesc">Due Date (Latest First)</option>
            <option value="priorityAsc">Priority (Low to High)</option>
            <option value="priorityDesc">Priority (High to Low)</option>
          </select>
        </div>
        <div className="flex items-center space-x-2">
          <Filter
            className={`w-4 h-4 ${currentThemeStyles.icon}`}
            aria-hidden="true"
          />
          <label className={`text-sm ${currentThemeStyles.text}`}>
            Filter:
          </label>
          <select
            className={`p-2 border rounded ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.inputFocus} transition-all duration-200`}
            value={filterBy}
            onChange={(e) => setFilterBy(e.target.value)}
            aria-label="Filter tasks by"
          >
            <option value="all">All Tasks</option>
            <option value="completed">Completed Tasks</option>
            <option value="incomplete">Incomplete Tasks</option>
          </select>
        </div>
      </div>

      {/* Task List */}
      <div className={`divide-y ${currentThemeStyles.divide}`}>
        {sortedAndFilteredTasks.map((task) => {
          const isOverdue = !task.completed && task.dueDate < today;
          const isDueToday = !task.completed && task.dueDate === today;
          const isFuture = !task.completed && task.dueDate > today;

          return (
            <div
              key={task.id}
              className={`p-4 sm:p-6 ${currentThemeStyles.hoverBg} ${
                isOverdue
                  ? currentThemeStyles.overdue
                  : isDueToday
                  ? currentThemeStyles.dueToday
                  : isFuture
                  ? currentThemeStyles.future
                  : ""
              } transition-colors duration-200`}
            >
              {editingTask && editingTask.id === task.id ? (
                <div className="flex flex-wrap gap-2">
                  <div className="flex-grow">
                    <label
                      htmlFor={`edit-task-title-${task.id}`}
                      className="sr-only"
                    >
                      Edit Task Title
                    </label>
                    <input
                      id={`edit-task-title-${task.id}`}
                      type="text"
                      className={`w-full p-2 border rounded ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.inputFocus} transition-all duration-200`}
                      value={editingTask.title}
                      onChange={(e) =>
                        setEditingTask({
                          ...editingTask,
                          title: e.target.value,
                        })
                      }
                    />
                  </div>
                  <div>
                    <label
                      htmlFor={`edit-task-due-date-${task.id}`}
                      className="sr-only"
                    >
                      Edit Due Date
                    </label>
                    <input
                      id={`edit-task-due-date-${task.id}`}
                      type="date"
                      className={`w-full p-2 border rounded ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.inputFocus} transition-all duration-200`}
                      value={editingTask.dueDate}
                      onChange={(e) =>
                        setEditingTask({
                          ...editingTask,
                          dueDate: e.target.value,
                        })
                      }
                    />
                  </div>
                  <div>
                    <label
                      htmlFor={`edit-task-priority-${task.id}`}
                      className="sr-only"
                    >
                      Edit Priority
                    </label>
                    <select
                      id={`edit-task-priority-${task.id}`}
                      className={`w-full p-2 border rounded ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.inputFocus} transition-all duration-200`}
                      value={editingTask.priority}
                      onChange={(e) =>
                        setEditingTask({
                          ...editingTask,
                          priority: e.target.value,
                        })
                      }
                    >
                      <option value="Low">Low Priority</option>
                      <option value="Medium">Medium Priority</option>
                      <option value="High">High Priority</option>
                    </select>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={handleSaveEdit}
                      className={`p-2 rounded flex items-center ${currentThemeStyles.buttonSuccess} transition-colors duration-200`}
                      aria-label="Save task changes"
                    >
                      <Save className="w-4 h-4 mr-1" aria-hidden="true" /> Save
                    </button>
                    <button
                      onClick={handleCancelEdit}
                      className={`p-2 rounded flex items-center ${currentThemeStyles.buttonSecondary} transition-colors duration-200`}
                      aria-label="Cancel task edit"
                    >
                      <X className="w-4 h-4 mr-1" aria-hidden="true" /> Cancel
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex justify-between items-center">
                  <div className="flex items-center">
                    <button
                      onClick={() => toggleTaskComplete(task.id)}
                      className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center ${
                        task.completed
                          ? currentThemeStyles.completed
                          : task.priority === "High"
                          ? "border-red-500"
                          : task.priority === "Medium"
                          ? "border-yellow-500"
                          : "border-blue-400"
                      } hover:shadow-sm transition-shadow duration-200`}
                      aria-label={
                        task.completed
                          ? "Mark task as incomplete"
                          : "Mark task as complete"
                      }
                    >
                      {task.completed && (
                        <CheckCircle
                          className="w-4 h-4 text-green-600"
                          aria-hidden="true"
                        />
                      )}
                    </button>
                    <div
                      className={
                        task.completed ? "line-through text-gray-400" : ""
                      }
                    >
                      <h3 className={`font-medium ${currentThemeStyles.text}`}>
                        {task.title}
                        {isOverdue && " (Overdue)"}
                        {isDueToday && " (Due Today)"}
                        {isFuture && " (Future)"}
                      </h3>
                      <div
                        className={`flex items-center text-sm ${currentThemeStyles.subText} mt-1`}
                      >
                        <Calendar
                          className={`w-4 h-4 mr-1 ${currentThemeStyles.icon}`}
                          aria-hidden="true"
                        />
                        Due: {task.dueDate}
                        <span
                          className={`ml-3 px-2 py-1 rounded-full text-xs ${
                            task.priority === "High"
                              ? currentThemeStyles.priorityHigh
                              : task.priority === "Medium"
                              ? currentThemeStyles.priorityMedium
                              : currentThemeStyles.priorityLow
                          }`}
                        >
                          {task.priority}
                        </span>
                      </div>
                    </div>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleStartEdit(task)}
                      className={`hover:text-gray-600 transition-colors duration-200 ${currentThemeStyles.subText}`}
                      aria-label={`Edit task: ${task.title}`}
                    >
                      <Edit3 className="w-5 h-5" aria-hidden="true" />
                    </button>
                    <button
                      onClick={() => confirmDeleteTask(task.id)}
                      className={`hover:text-gray-600 transition-colors duration-200 ${currentThemeStyles.subText}`}
                      aria-label={`Delete task: ${task.title}`}
                    >
                      <Trash2 className="w-5 h-5" aria-hidden="true" />
                    </button>
                  </div>
                </div>
              )}
              {/* Delete Confirmation Dialog */}
              {showDeleteConfirm === task.id && (
                <div
                  className={`mt-2 p-2 rounded-lg ${currentThemeStyles.cardBg}`}
                >
                  <p className={`text-sm ${currentThemeStyles.text}`}>
                    Are you sure you want to delete "{task.title}"?
                  </p>
                  <div className="flex space-x-2 mt-2">
                    <button
                      onClick={() => handleDeleteTask(task.id)}
                      className={`p-2 rounded flex items-center ${currentThemeStyles.buttonDanger} transition-colors duration-200`}
                      aria-label={`Confirm delete task: ${task.title}`}
                    >
                      Yes, Delete
                    </button>
                    <button
                      onClick={cancelDeleteTask}
                      className={`p-2 rounded flex items-center ${currentThemeStyles.buttonSecondary} transition-colors duration-200`}
                      aria-label="Cancel delete task"
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              )}
            </div>
          );
        })}
        {sortedAndFilteredTasks.length === 0 && (
          <div className={`py-12 text-center ${currentThemeStyles.subText}`}>
            {filterBy === "completed"
              ? "No completed tasks yet."
              : filterBy === "incomplete"
              ? "No incomplete tasks yet."
              : "No tasks yet. Add your first task above!"}
          </div>
        )}
      </div>
    </div>
  );
}

export default TaskManager;
