import React, { useState, useRef } from "react";
import {
  Calendar,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
  Save,
  X,
  ArrowUpDown,
  Filter,
} from "lucide-react";
import { toast } from "react-toastify";

function TaskManager({
  tasks,
  addTask,
  updateTask,
  deleteTask,
  toggleTaskComplete,
  currentTheme,
}) {
  const [newTask, setNewTask] = useState({
    title: "",
    dueDate: "",
    priority: "Medium",
    completed: false,
    hours: "",
  });
  const [editingTask, setEditingTask] = useState(null);
  const [sortBy, setSortBy] = useState("dueDateAsc");
  const [filterBy, setFilterBy] = useState("all");

  // Refs for focusing next field on "Enter" press in Add Task form
  const titleInputRef = useRef(null);
  const dueDateInputRef = useRef(null);
  const prioritySelectRef = useRef(null);
  const hoursInputRef = useRef(null);
  const addButtonRef = useRef(null);

  // Refs for focusing next field on "Enter" press in Edit Task form
  const editTitleInputRef = useRef(null);
  const editDueDateInputRef = useRef(null);
  const editPrioritySelectRef = useRef(null);
  const editHoursInputRef = useRef(null);
  const saveButtonRef = useRef(null);

  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";
  const isSunsetGlow = currentTheme === "Sunset Glow";
  const isForestWhisper = currentTheme === "Forest Whisper";

  // Helper function to format the local date as YYYY-MM-DD
  const getLocalDateString = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  const today = getLocalDateString(new Date());
  const currentYear = new Date().getFullYear(); // 2025 as per the system date

  const handleAddTask = () => {
    if (!newTask.title || !newTask.dueDate || !newTask.hours) {
      toast.error(
        "Please fill in all required fields (title, due date, and hours)."
      );
      return;
    }
    const parsedHours = parseFloat(newTask.hours);
    if (isNaN(parsedHours) || parsedHours < 0) {
      toast.error("Please enter a valid number of hours (0 or greater).");
      return;
    }
    addTask({
      ...newTask,
      hours: parsedHours,
    });
    setNewTask({
      title: "",
      dueDate: "",
      priority: "Medium",
      completed: false,
      hours: "",
    });
    // Focus back on the first field after adding
    titleInputRef.current.focus();
  };

  const handleStartEdit = (task) => {
    if (task.completed) {
      toast.info("Cannot edit a completed task.");
      return;
    }
    setEditingTask({
      ...task,
      hours: task.hours || 0,
    });
  };

  const handleSaveEdit = () => {
    if (!editingTask) return;
    if (
      !editingTask.title ||
      !editingTask.dueDate ||
      editingTask.hours === ""
    ) {
      toast.error(
        "Please fill in all required fields (title, due date, and hours)."
      );
      return;
    }
    const parsedHours = parseFloat(editingTask.hours);
    if (isNaN(parsedHours) || parsedHours < 0) {
      toast.error("Please enter a valid number of hours (0 or greater).");
      return;
    }
    updateTask({
      ...editingTask,
      hours: parsedHours,
    });
    setEditingTask(null);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const handleDeleteTaskWithConfirmation = (task) => {
    const message = task.completed
      ? `Are you sure? Deleting "${task.title}" will remove all progress (points, hours).`
      : `Are you sure you want to delete "${task.title}"?`;

    toast.warn(
      ({ closeToast }) => (
        <div>
          <p>{message}</p>
          <div className="mt-2 flex space-x-2">
            <button
              onClick={() => {
                deleteTask(task.id);
                closeToast();
              }}
              className={`px-3 py-1 rounded-lg text-sm font-semibold text-white transition-all duration-200 ${
                isDarkMode
                  ? "bg-red-600 hover:bg-red-700"
                  : isOceanBreeze
                    ? "bg-red-600 hover:bg-red-700"
                    : isSunsetGlow
                      ? "bg-red-600 hover:bg-red-700"
                      : isForestWhisper
                        ? "bg-red-600 hover:bg-red-700"
                        : "bg-red-600 hover:bg-red-700"
              }`}
            >
              Yes, Delete
            </button>
            <button
              onClick={closeToast}
              className={`px-3 py-1 rounded-lg text-sm font-semibold text-white transition-all duration-200 ${
                isDarkMode
                  ? "bg-gray-600 hover:bg-gray-700"
                  : isOceanBreeze
                    ? "bg-blue-500 hover:bg-blue-600"
                    : isSunsetGlow
                      ? "bg-pink-500 hover:bg-pink-600"
                      : isForestWhisper
                        ? "bg-emerald-500 hover:bg-emerald-600"
                        : "bg-gray-500 hover:bg-gray-600"
              }`}
            >
              Cancel
            </button>
          </div>
        </div>
      ),
      {
        autoClose: false,
        closeOnClick: false,
        draggable: false,
        toastId: `delete-confirm-${task.id}`,
      }
    );
  };

  // Validate and correct the year in the dueDate field
  const validateAndCorrectYear = (dateValue, setTaskFunction) => {
    if (!dateValue) return;

    const [year, month, day] = dateValue.split("-");
    if (parseInt(year) !== currentYear) {
      const correctedDate = `${currentYear}-${month}-${day}`;
      setTaskFunction((prev) => ({
        ...prev,
        dueDate: correctedDate,
      }));
      toast.info(`Year corrected to ${currentYear}, the current year.`);
    }
  };

  // Handle "Enter" key press for Add Task form
  const handleAddTaskKeyPress = (e, nextRef) => {
    if (e.key === "Enter") {
      e.preventDefault();
      if (nextRef.current) {
        nextRef.current.focus();
      }
    }
  };

  // Handle "Enter" key press for Edit Task form
  const handleEditTaskKeyPress = (e, nextRef) => {
    if (e.key === "Enter") {
      e.preventDefault();
      if (nextRef.current) {
        nextRef.current.focus();
      }
    }
  };

  const priorityValue = { High: 3, Medium: 2, Low: 1 };

  const sortedAndFilteredTasks = [...tasks]
    .filter((task) => {
      if (filterBy === "completed") return task.completed;
      if (filterBy === "incomplete") return !task.completed;
      return true;
    })
    .sort((a, b) => {
      if (sortBy === "dueDateAsc") {
        return new Date(a.dueDate) - new Date(b.dueDate);
      } else if (sortBy === "dueDateDesc") {
        return new Date(b.dueDate) - new Date(a.dueDate);
      } else if (sortBy === "priorityAsc") {
        return priorityValue[a.priority] - priorityValue[b.priority];
      } else if (sortBy === "priorityDesc") {
        return priorityValue[b.priority] - priorityValue[a.priority];
      }
      return 0;
    });

  return (
    <div
      className={`rounded-xl shadow-lg transition-all duration-300 ${
        isDarkMode
          ? "bg-gray-800 border border-gray-700"
          : isOceanBreeze
            ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
            : isSunsetGlow
              ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
              : isForestWhisper
                ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                : "bg-white border border-gray-200"
      }`}
    >
      <div
        className={`p-4 border-b ${
          isDarkMode
            ? "border-gray-600"
            : isOceanBreeze
              ? "border-teal-400"
              : isSunsetGlow
                ? "border-orange-400"
                : isForestWhisper
                  ? "border-green-400"
                  : "border-gray-300"
        }`}
      >
        <h2
          className={`text-xl font-bold tracking-tight ${
            isDarkMode
              ? "text-gray-100"
              : isOceanBreeze
                ? "text-teal-900"
                : isSunsetGlow
                  ? "text-orange-900"
                  : isForestWhisper
                    ? "text-green-900"
                    : "text-gray-900"
          }`}
        >
          Manage Tasks
        </h2>
      </div>

      {/* Add Task Form */}
      <div
        className={`p-4 border-b ${
          isDarkMode
            ? "border-gray-600 bg-gray-700"
            : isOceanBreeze
              ? "border-teal-400 bg-amber-50 bg-opacity-90 backdrop-blur-md"
              : isSunsetGlow
                ? "border-orange-400 bg-pink-50 bg-opacity-90 backdrop-blur-md"
                : isForestWhisper
                  ? "border-green-400 bg-emerald-50 bg-opacity-90 backdrop-blur-md"
                  : "border-gray-300 bg-gray-50"
        }`}
      >
        <div className="flex flex-wrap md:flex-nowrap gap-3">
          <input
            type="text"
            placeholder="Task title"
            className={`flex-grow p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
              isDarkMode
                ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                : isOceanBreeze
                  ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                  : isSunsetGlow
                    ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                    : isForestWhisper
                      ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                      : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
            }`}
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
            onKeyDown={(e) => handleAddTaskKeyPress(e, dueDateInputRef)}
            ref={titleInputRef}
          />
          <input
            type="date"
            className={`p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
              isDarkMode
                ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                : isOceanBreeze
                  ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                  : isSunsetGlow
                    ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                    : isForestWhisper
                      ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                      : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
            }`}
            value={newTask.dueDate}
            onChange={(e) => {
              const newDate = e.target.value;
              setNewTask({ ...newTask, dueDate: newDate });
              validateAndCorrectYear(newDate, setNewTask);
            }}
            onKeyDown={(e) => handleAddTaskKeyPress(e, prioritySelectRef)}
            ref={dueDateInputRef}
            min={`${currentYear}-01-01`}
            max={`${currentYear}-12-31`}
          />
          <select
            className={`p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
              isDarkMode
                ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                : isOceanBreeze
                  ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                  : isSunsetGlow
                    ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                    : isForestWhisper
                      ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                      : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
            }`}
            value={newTask.priority}
            onChange={(e) =>
              setNewTask({ ...newTask, priority: e.target.value })
            }
            onKeyDown={(e) => handleAddTaskKeyPress(e, hoursInputRef)}
            ref={prioritySelectRef}
          >
            <option value="Low">Low Priority</option>
            <option value="Medium">Medium Priority</option>
            <option value="High">High Priority</option>
          </select>
          <input
            type="number"
            placeholder="Hours"
            className={`p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
              isDarkMode
                ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                : isOceanBreeze
                  ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                  : isSunsetGlow
                    ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                    : isForestWhisper
                      ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                      : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
            }`}
            value={newTask.hours}
            onChange={(e) => setNewTask({ ...newTask, hours: e.target.value })}
            onKeyDown={(e) => handleAddTaskKeyPress(e, addButtonRef)}
            ref={hoursInputRef}
            min="0"
            step="0.1"
          />
          <button
            className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
              isDarkMode
                ? "bg-indigo-600 hover:bg-indigo-700"
                : isOceanBreeze
                  ? "bg-teal-600 hover:bg-teal-700"
                  : isSunsetGlow
                    ? "bg-orange-600 hover:bg-orange-700"
                    : isForestWhisper
                      ? "bg-green-600 hover:bg-green-700"
                      : "bg-indigo-600 hover:bg-indigo-700"
            }`}
            onClick={handleAddTask}
            onKeyDown={(e) => {
              if (e.key === "Enter") {
                e.preventDefault();
                handleAddTask();
              }
            }}
            ref={addButtonRef}
          >
            <PlusCircle className="w-4 h-4 mr-1 inline" /> Add Task
          </button>
        </div>
      </div>

      {/* Sort and Filter Controls */}
      <div
        className={`p-4 flex flex-wrap gap-3 border-b ${
          isDarkMode
            ? "border-gray-600 bg-gray-700"
            : isOceanBreeze
              ? "border-teal-400 bg-amber-50 bg-opacity-90 backdrop-blur-md"
              : isSunsetGlow
                ? "border-orange-400 bg-pink-50 bg-opacity-90 backdrop-blur-md"
                : isForestWhisper
                  ? "border-green-400 bg-emerald-50 bg-opacity-90 backdrop-blur-md"
                  : "border-gray-300 bg-gray-50"
        }`}
      >
        <div className="flex items-center space-x-2">
          <ArrowUpDown
            className={`w-4 h-4 ${
              isDarkMode
                ? "text-gray-300"
                : isOceanBreeze
                  ? "text-teal-600"
                  : isSunsetGlow
                    ? "text-orange-600"
                    : isForestWhisper
                      ? "text-green-600"
                      : "text-gray-500"
            }`}
          />
          <label
            className={`text-sm font-medium ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            Sort by:
          </label>
          <select
            className={`p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
              isDarkMode
                ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                : isOceanBreeze
                  ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                  : isSunsetGlow
                    ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                    : isForestWhisper
                      ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                      : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
            }`}
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="dueDateAsc">Due Date (Earliest First)</option>
            <option value="dueDateDesc">Due Date (Latest First)</option>
            <option value="priorityAsc">Priority (Low to High)</option>
            <option value="priorityDesc">Priority (High to Low)</option>
          </select>
        </div>
        <div className="flex items-center space-x-2">
          <Filter
            className={`w-4 h-4 ${
              isDarkMode
                ? "text-gray-300"
                : isOceanBreeze
                  ? "text-teal-600"
                  : isSunsetGlow
                    ? "text-orange-600"
                    : isForestWhisper
                      ? "text-green-600"
                      : "text-gray-500"
            }`}
          />
          <label
            className={`text-sm font-medium ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            Filter:
          </label>
          <select
            className={`p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
              isDarkMode
                ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                : isOceanBreeze
                  ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                  : isSunsetGlow
                    ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                    : isForestWhisper
                      ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                      : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
            }`}
            value={filterBy}
            onChange={(e) => setFilterBy(e.target.value)}
          >
            <option value="all">All Tasks</option>
            <option value="completed">Completed Tasks</option>
            <option value="incomplete">Incomplete Tasks</option>
          </select>
        </div>
      </div>

      {/* Task List */}
      <div
        className={`divide-y ${
          isDarkMode
            ? "divide-gray-600"
            : isOceanBreeze
              ? "divide-teal-400"
              : isSunsetGlow
                ? "divide-orange-400"
                : isForestWhisper
                  ? "divide-green-400"
                  : "divide-gray-300"
        }`}
      >
        {sortedAndFilteredTasks.length === 0 ? (
          <div
            className={`py-12 text-center ${
              isDarkMode
                ? "text-gray-400"
                : isOceanBreeze
                  ? "text-teal-700"
                  : isSunsetGlow
                    ? "text-orange-700"
                    : isForestWhisper
                      ? "text-green-700"
                      : "text-gray-500"
            }`}
          >
            No tasks to display. Add a new task to get started!
          </div>
        ) : (
          sortedAndFilteredTasks.map((task) => {
            return (
              <div
                key={task.id}
                className={`p-4 transition-all duration-300 hover:shadow-md hover:scale-[1.02] ${
                  isDarkMode
                    ? "hover:bg-gray-700"
                    : isOceanBreeze
                      ? "hover:bg-teal-50"
                      : isSunsetGlow
                        ? "hover:bg-orange-50"
                        : isForestWhisper
                          ? "hover:bg-green-50"
                          : "hover:bg-gray-50"
                }`}
              >
                {editingTask && editingTask.id === task.id ? (
                  <div className="flex flex-wrap gap-2">
                    <input
                      type="text"
                      className={`flex-grow p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
                        isDarkMode
                          ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                          : isOceanBreeze
                            ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                            : isSunsetGlow
                              ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                              : isForestWhisper
                                ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                                : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
                      }`}
                      value={editingTask.title}
                      onChange={(e) =>
                        setEditingTask({
                          ...editingTask,
                          title: e.target.value,
                        })
                      }
                      onKeyDown={(e) =>
                        handleEditTaskKeyPress(e, editDueDateInputRef)
                      }
                      ref={editTitleInputRef}
                    />
                    <input
                      type="date"
                      className={`p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
                        isDarkMode
                          ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                          : isOceanBreeze
                            ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                            : isSunsetGlow
                              ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                              : isForestWhisper
                                ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                                : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
                      }`}
                      value={editingTask.dueDate}
                      onChange={(e) => {
                        const newDate = e.target.value;
                        setEditingTask({
                          ...editingTask,
                          dueDate: newDate,
                        });
                        validateAndCorrectYear(newDate, setEditingTask);
                      }}
                      onKeyDown={(e) =>
                        handleEditTaskKeyPress(e, editPrioritySelectRef)
                      }
                      ref={editDueDateInputRef}
                      min={`${currentYear}-01-01`}
                      max={`${currentYear}-12-31`}
                    />
                    <select
                      className={`p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
                        isDarkMode
                          ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                          : isOceanBreeze
                            ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                            : isSunsetGlow
                              ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                              : isForestWhisper
                                ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                                : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
                      }`}
                      value={editingTask.priority}
                      onChange={(e) =>
                        setEditingTask({
                          ...editingTask,
                          priority: e.target.value,
                        })
                      }
                      onKeyDown={(e) =>
                        handleEditTaskKeyPress(e, editHoursInputRef)
                      }
                      ref={editPrioritySelectRef}
                    >
                      <option value="Low">Low Priority</option>
                      <option value="Medium">Medium Priority</option>
                      <option value="High">High Priority</option>
                    </select>
                    <input
                      type="number"
                      className={`p-2 border rounded-lg text-sm shadow-sm transition-all duration-200 focus:outline-none focus:ring-2 ${
                        isDarkMode
                          ? "bg-gray-900 border-gray-600 text-gray-200 focus:ring-indigo-500"
                          : isOceanBreeze
                            ? "bg-blue-50 border-teal-400 text-teal-800 focus:ring-teal-500"
                            : isSunsetGlow
                              ? "bg-orange-50 border-orange-400 text-orange-800 focus:ring-orange-500"
                              : isForestWhisper
                                ? "bg-green-50 border-green-400 text-green-800 focus:ring-green-500"
                                : "bg-white border-gray-300 text-gray-800 focus:ring-indigo-500"
                      }`}
                      value={editingTask.hours}
                      onChange={(e) =>
                        setEditingTask({
                          ...editingTask,
                          hours: e.target.value,
                        })
                      }
                      onKeyDown={(e) =>
                        handleEditTaskKeyPress(e, saveButtonRef)
                      }
                      ref={editHoursInputRef}
                      min="0"
                      step="0.1"
                    />
                    <div className="flex space-x-2">
                      <button
                        onClick={handleSaveEdit}
                        className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                          isDarkMode
                            ? "bg-green-600 hover:bg-green-700"
                            : isOceanBreeze
                              ? "bg-teal-600 hover:bg-teal-700"
                              : isSunsetGlow
                                ? "bg-orange-600 hover:bg-orange-700"
                                : isForestWhisper
                                  ? "bg-green-600 hover:bg-green-700"
                                  : "bg-green-600 hover:bg-green-700"
                        }`}
                        onKeyDown={(e) => {
                          if (e.key === "Enter") {
                            e.preventDefault();
                            handleSaveEdit();
                          }
                        }}
                        ref={saveButtonRef}
                      >
                        <Save className="w-4 h-4 mr-1 inline" /> Save
                      </button>
                      <button
                        onClick={handleCancelEdit}
                        className={`px-4 py-2 rounded-lg text-sm font-semibold text-white shadow-md transition-all duration-200 transform hover:scale-105 ${
                          isDarkMode
                            ? "bg-gray-600 hover:bg-gray-700"
                            : isOceanBreeze
                              ? "bg-blue-500 hover:bg-blue-600"
                              : isSunsetGlow
                                ? "bg-pink-500 hover:bg-pink-600"
                                : isForestWhisper
                                  ? "bg-emerald-500 hover:bg-emerald-600"
                                  : "bg-gray-500 hover:bg-gray-600"
                        }`}
                      >
                        <X className="w-4 h-4 mr-1 inline" /> Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <button
                        onClick={() => toggleTaskComplete(task.id)}
                        className={`p-1 rounded-full transition-all duration-200 ${
                          task.completed
                            ? isDarkMode
                              ? "text-green-400 hover:text-green-500"
                              : isOceanBreeze
                                ? "text-teal-600 hover:text-teal-700"
                                : isSunsetGlow
                                  ? "text-orange-600 hover:text-orange-700"
                                  : isForestWhisper
                                    ? "text-green-600 hover:text-green-700"
                                    : "text-indigo-600 hover:text-indigo-700"
                            : isDarkMode
                              ? "text-gray-500 hover:text-gray-400"
                              : isOceanBreeze
                                ? "text-blue-400 hover:text-blue-500"
                                : isSunsetGlow
                                  ? "text-orange-400 hover:text-orange-500"
                                  : isForestWhisper
                                    ? "text-green-400 hover:text-green-500"
                                    : "text-gray-400 hover:text-gray-500"
                        }`}
                      >
                        <CheckCircle
                          className={`w-5 h-5 ${
                            task.completed ? "fill-current" : ""
                          }`}
                        />
                      </button>
                      <div>
                        <div className="flex items-center space-x-2">
                          <h3
                            className={`text-base font-semibold ${
                              task.completed
                                ? "line-through " +
                                  (isDarkMode
                                    ? "text-gray-400"
                                    : isOceanBreeze
                                      ? "text-teal-600"
                                      : isSunsetGlow
                                        ? "text-orange-600"
                                        : isForestWhisper
                                          ? "text-green-600"
                                          : "text-gray-600")
                                : isDarkMode
                                  ? "text-gray-200"
                                  : isOceanBreeze
                                    ? "text-teal-800"
                                    : isSunsetGlow
                                      ? "text-orange-800"
                                      : isForestWhisper
                                        ? "text-green-800"
                                        : "text-gray-800"
                            }`}
                          >
                            {task.title}
                          </h3>
                          <span
                            className={`px-2 py-0.5 rounded-full text-xs font-medium ${
                              task.priority === "High"
                                ? isDarkMode
                                  ? "bg-red-600 text-white"
                                  : "bg-red-500 text-white"
                                : task.priority === "Medium"
                                  ? isDarkMode
                                    ? "bg-orange-600 text-white"
                                    : "bg-orange-500 text-white"
                                  : isDarkMode
                                    ? "bg-green-600 text-white"
                                    : "bg-green-500 text-white"
                            }`}
                          >
                            {task.priority}
                          </span>
                        </div>
                        <p
                          className={`text-sm flex items-center space-x-1 ${
                            isDarkMode
                              ? "text-gray-400"
                              : isOceanBreeze
                                ? "text-blue-700"
                                : isSunsetGlow
                                  ? "text-orange-700"
                                  : isForestWhisper
                                    ? "text-green-700"
                                    : "text-gray-600"
                          }`}
                        >
                          <Calendar className="w-4 h-4" />
                          <span>Due: {task.dueDate}</span>
                          <span className="ml-2">Hours: {task.hours || 0}</span>
                        </p>
                        {task.completed && task.completedDate && (
                          <p
                            className={`text-xs mt-1 ${
                              isDarkMode
                                ? "text-gray-500"
                                : isOceanBreeze
                                  ? "text-blue-600"
                                  : isSunsetGlow
                                    ? "text-orange-600"
                                    : isForestWhisper
                                      ? "text-green-600"
                                      : "text-gray-500"
                            }`}
                          >
                            Completed on: {task.completedDate}
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleStartEdit(task)}
                        className={`p-2 rounded-full transition-all duration-200 transform hover:scale-110 ${
                          task.completed
                            ? "opacity-50 cursor-not-allowed"
                            : isDarkMode
                              ? "text-gray-300 hover:text-gray-100"
                              : isOceanBreeze
                                ? "text-teal-600 hover:text-teal-700"
                                : isSunsetGlow
                                  ? "text-orange-600 hover:text-orange-700"
                                  : isForestWhisper
                                    ? "text-green-600 hover:text-green-700"
                                    : "text-gray-600 hover:text-gray-800"
                        }`}
                        disabled={task.completed}
                      >
                        <Edit3 className="w-5 h-5" />
                      </button>
                      <button
                        onClick={() => handleDeleteTaskWithConfirmation(task)}
                        className={`p-2 rounded-full transition-all duration-200 transform hover:scale-110 ${
                          isDarkMode
                            ? "text-red-400 hover:text-red-500"
                            : isOceanBreeze
                              ? "text-red-600 hover:text-red-700"
                              : isSunsetGlow
                                ? "text-red-600 hover:text-red-700"
                                : isForestWhisper
                                  ? "text-red-600 hover:text-red-700"
                                  : "text-red-500 hover:text-red-600"
                        }`}
                      >
                        <Trash2 className="w-5 h-5" />
                      </button>
                    </div>
                  </div>
                )}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

export default TaskManager;
