// src/services/aiService.js
import { OpenAI } from "openai";

const openai = new OpenAI({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY,
});

export const getStudySuggestion = async (tasks, studyHabits) => {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4",
      messages: [
        {
          role: "system",
          content:
            "You are an AI study assistant. Provide personalized study suggestions.",
        },
        {
          role: "user",
          content: `Here are my upcoming tasks: ${JSON.stringify(tasks)}. 
                    And here are my study habits: ${JSON.stringify(
                      studyHabits
                    )}. 
                    What would you recommend I focus on today?`,
        },
      ],
    });

    return response.choices[0].message.content;
  } catch (error) {
    console.error("Error getting AI suggestion:", error);
    return "I'm having trouble generating suggestions right now. Please try again later.";
  }
};
