import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import jwtDecode from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMoon,
  faSun,
  faWater,
  faCloudSun,
  faLeaf,
} from "@fortawesome/free-solid-svg-icons";
import { BookOpen } from "lucide-react";
import Dashboard from "./Dashboard";
import TaskManager from "./TaskManager";
import Analytics from "./Analytics";
import Rewards from "./Rewards";
import { getStudySuggestion } from "../services/aiService";

// Centralized theme configurations
const themeConfig = {
  "Light Mode": {
    background: "bg-white",
    text: "text-gray-800",
    secondaryText: "text-gray-500",
    border: "border-gray-200",
    hoverBg: "hover:bg-gray-50",
    primary: "bg-indigo-600",
    primaryHover: "hover:bg-indigo-700",
    primaryText: "text-white",
    secondaryBg: "bg-gray-50",
    focusRing: "focus:ring-indigo-400",
    headerBg: "bg-gray-50",
    overdueColor: "text-red-600",
    dueTodayColor: "text-orange-600",
    futureColor: "text-green-600",
    completedBg: "bg-green-50 border-green-500",
  },
  "Dark Mode": {
    background: "bg-gray-700",
    text: "text-gray-200",
    secondaryText: "text-gray-400",
    border: "border-gray-600",
    hoverBg: "hover:bg-gray-600",
    primary: "bg-indigo-500",
    primaryHover: "hover:bg-indigo-600",
    primaryText: "text-white",
    secondaryBg: "bg-gray-600",
    focusRing: "focus:ring-indigo-400",
    headerBg: "bg-gray-800",
    overdueColor: "text-red-400",
    dueTodayColor: "text-orange-400",
    futureColor: "text-green-400",
    completedBg: "bg-green-900 border-green-600",
  },
  "Ocean Breeze": {
    background: "bg-opacity-70 backdrop-blur-lg",
    text: "text-teal-800",
    secondaryText: "text-blue-600",
    border: "border-teal-300",
    hoverBg: "hover:bg-teal-100",
    primary: "bg-teal-500",
    primaryHover: "hover:bg-teal-600",
    primaryText: "text-white",
    secondaryBg: "bg-amber-100 bg-opacity-70 backdrop-blur-sm",
    focusRing: "focus:ring-indigo-400",
    headerBg: "bg-blue-50 bg-opacity-70 backdrop-blur-lg",
    overdueColor: "text-red-600",
    dueTodayColor: "text-orange-500",
    futureColor: "text-green-600",
    completedBg: "bg-teal-100 border-teal-500",
  },
  "Sunset Glow": {
    background: "bg-opacity-70 backdrop-blur-lg",
    text: "text-orange-800",
    secondaryText: "text-orange-500",
    border: "border-orange-300",
    hoverBg: "hover:bg-orange-100",
    primary: "bg-orange-500",
    primaryHover: "hover:bg-orange-600",
    primaryText: "text-white",
    secondaryBg: "bg-pink-100 bg-opacity-70 backdrop-blur-sm",
    focusRing: "focus:ring-orange-400",
    headerBg: "bg-orange-50 bg-opacity-70 backdrop-blur-lg",
    overdueColor: "text-red-700",
    dueTodayColor: "text-orange-600",
    futureColor: "text-green-600",
    completedBg: "bg-orange-100 border-orange-500",
  },
  "Forest Whisper": {
    background: "bg-opacity-70 backdrop-blur-lg",
    text: "text-green-800",
    secondaryText: "text-green-500",
    border: "border-green-300",
    hoverBg: "hover:bg-green-100",
    primary: "bg-green-500",
    primaryHover: "hover:bg-green-600",
    primaryText: "text-white",
    secondaryBg: "bg-emerald-100 bg-opacity-70 backdrop-blur-sm",
    focusRing: "focus:ring-green-400",
    headerBg: "bg-green-50 bg-opacity-70 backdrop-blur-lg",
    overdueColor: "text-red-600",
    dueTodayColor: "text-orange-500",
    futureColor: "text-green-500",
    completedBg: "bg-green-100 border-green-500",
  },
};

const themeIcons = {
  "Light Mode": faSun,
  "Dark Mode": faMoon,
  "Ocean Breeze": faWater,
  "Sunset Glow": faCloudSun,
  "Forest Whisper": faLeaf,
};

function Layout({ token }) {
  const navigate = useNavigate();
  const decodedToken = jwtDecode(token);
  const userId = decodedToken.userId;

  const [tasks, setTasks] = useState([]);
  const [studyStats, setStudyStats] = useState({
    totalHours: 0,
    completedTasks: 0,
    streak: 0,
    lastActiveDate: null,
    lastStreakUpdate: null,
    studyHoursLog: [],
  });
  const [points, setPoints] = useState(0);
  const [badges, setBadges] = useState([]);
  const [themes, setThemes] = useState([
    "Light Mode",
    "Dark Mode",
    "Ocean Breeze",
    "Sunset Glow",
    "Forest Whisper",
  ]);
  const [currentTheme, setCurrentTheme] = useState("Light Mode");
  const [unlockedThemes, setUnlockedThemes] = useState(["Light Mode"]);
  const [activeTab, setActiveTab] = useState("dashboard");
  const [aiSuggestion, setAiSuggestion] = useState("");
  const [loading, setLoading] = useState(true);

  const fetchUserData = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/user/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || "Failed to fetch user data");
      }

      setTasks(data.tasks || []);
      setStudyStats(
        data.studyStats || {
          totalHours: 0,
          completedTasks: 0,
          streak: 0,
          lastActiveDate: null,
          lastStreakUpdate: null,
          studyHoursLog: [],
        }
      );
      setPoints(data.points || 0);
      setBadges(data.badges || []);
      setThemes(
        data.themes || [
          "Light Mode",
          "Dark Mode",
          "Ocean Breeze",
          "Sunset Glow",
          "Forest Whisper",
        ]
      );
      setCurrentTheme(data.currentTheme || "Light Mode");
      setUnlockedThemes(data.unlockedThemes || ["Light Mode"]);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching user data:", err);
      toast.error("Failed to load user data. Using defaults.");
      setTasks([
        {
          id: 1,
          title: "Math Homework",
          dueDate: "2025-05-10",
          priority: "High",
          subject: "Math",
          completed: false,
        },
        {
          id: 2,
          title: "History Essay",
          dueDate: "2025-05-12",
          priority: "Medium",
          subject: "History",
          completed: false,
        },
        {
          id: 3,
          title: "Science Project",
          dueDate: "2025-05-15",
          priority: "Low",
          subject: "Science",
          completed: false,
        },
      ]);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUserData();
  }, []);

  const saveUserData = async () => {
    const dataToSend = {
      tasks,
      studyStats,
      points,
      badges,
      themes,
      currentTheme,
      unlockedThemes,
    };
    try {
      const response = await fetch(`http://localhost:5000/api/user/${userId}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(dataToSend),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to save user data");
      }
      toast.success("Progress saved!");
    } catch (err) {
      console.error("Error saving user data:", err);
      toast.error(`Failed to save progress: ${err.message}`);
    }
  };

  const addTask = (newTask) => {
    const taskWithId = {
      ...newTask,
      id: tasks.length > 0 ? Math.max(...tasks.map((t) => t.id)) + 1 : 1,
    };
    setTasks([...tasks, taskWithId]);
    toast.success("Task added successfully!");
    saveUserData();
  };

  const updateTask = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task.id === updatedTask.id ? updatedTask : task))
    );
    saveUserData();
  };

  const deleteTask = (taskId) => {
    setTasks(tasks.filter((task) => task.id !== taskId));
    toast.success("Task deleted successfully!");
    saveUserData();
  };

  const toggleTaskComplete = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) => {
        if (task.id === taskId) {
          const updatedTask = {
            ...task,
            completed: !task.completed,
            completedDate: !task.completed
              ? new Date().toISOString().split("T")[0]
              : null,
          };
          if (updatedTask.completed) {
            setPoints((prev) => prev + 10);
            setStudyStats((prev) => ({
              ...prev,
              completedTasks: prev.completedTasks + 1,
            }));
            checkForBadges(updatedTask);
          }
          return updatedTask;
        }
        return task;
      })
    );
    saveUserData();
  };

  const checkForBadges = (task) => {
    const highPriorityTasksCompleted = tasks.filter(
      (t) => t.priority === "High" && t.completed
    ).length;
    const totalTasksCompleted = tasks.filter((t) => t.completed).length;
    const tasksCompletedBeforeDue = tasks.filter(
      (t) =>
        t.completed &&
        t.completedDate &&
        new Date(t.completedDate) < new Date(t.dueDate)
    ).length;

    const newBadges = [...badges];
    if (
      highPriorityTasksCompleted >= 5 &&
      !badges.includes("Priority Master")
    ) {
      newBadges.push("Priority Master");
      setPoints((prev) => prev + 20);
      toast.success("🎉 You've earned the Priority Master badge!");
    }
    if (totalTasksCompleted >= 10 && !badges.includes("Task Titan")) {
      newBadges.push("Task Titan");
      setPoints((prev) => prev + 20);
      toast.success("🎉 You've earned the Task Titan badge!");
    }
    if (tasksCompletedBeforeDue >= 3 && !badges.includes("Early Bird")) {
      newBadges.push("Early Bird");
      setPoints((prev) => prev + 20);
      toast.success("🎉 You've earned the Early Bird badge!");
    }
    if (studyStats.streak >= 7 && !badges.includes("Streak Star")) {
      newBadges.push("Streak Star");
      setPoints((prev) => prev + 20);
      toast.success("🎉 You've earned the Streak Star badge!");
    }
    setBadges(newBadges);
  };

  const updateStreak = (today) => {
    setStudyStats((prev) => {
      const lastActive = prev.lastActiveDate
        ? new Date(prev.lastActiveDate)
        : null;
      const todayDate = new Date(today);
      let newStreak = prev.streak;
      let newLastActiveDate = today;

      if (!lastActive) {
        newStreak = 1;
      } else {
        const diffTime = todayDate - lastActive;
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
        if (diffDays === 1) {
          newStreak = prev.streak + 1;
        } else if (diffDays > 1) {
          newStreak = 1;
        }
      }

      return {
        ...prev,
        streak: newStreak,
        lastActiveDate: newLastActiveDate,
        lastStreakUpdate: today,
        studyHoursLog: prev.studyHoursLog || [],
      };
    });
  };

  const logStudyHours = (hours) => {
    const today = new Date().toISOString().split("T")[0];
    setStudyStats((prev) => {
      const existingLog = prev.studyHoursLog.find((log) => log.date === today);
      let updatedLog;
      if (existingLog) {
        updatedLog = prev.studyHoursLog.map((log) =>
          log.date === today ? { ...log, hours: log.hours + hours } : log
        );
      } else {
        updatedLog = [...prev.studyHoursLog, { date: today, hours }];
      }
      return {
        ...prev,
        totalHours: prev.totalHours + hours,
        studyHoursLog: updatedLog,
      };
    });
    updateStreak(today);
    setPoints((prev) => prev + Math.floor(hours * 5));
    toast.success(`Logged ${hours} study hours!`);
    saveUserData();
  };

  const handlePointsUpdate = (newPoints) => {
    setPoints(newPoints);
    saveUserData();
  };

  const handleThemeUnlock = (themeName) => {
    setUnlockedThemes((prev) => {
      if (!prev.includes(themeName)) {
        return [...prev, themeName];
      }
      return prev;
    });
    saveUserData();
  };

  const getNextTheme = () => {
    const currentIndex = themes.indexOf(currentTheme);
    const nextIndex = (currentIndex + 1) % themes.length;
    return themes[nextIndex];
  };

  const handleThemeChange = () => {
    const nextTheme = getNextTheme();
    setCurrentTheme(nextTheme);
    saveUserData();
  };

  const nextTheme = getNextTheme();

  const getStudyTips = async () => {
    setAiSuggestion("Loading new suggestions...");
    try {
      const suggestion = await getStudySuggestion(tasks, studyStats);
      setAiSuggestion(suggestion);
    } catch (err) {
      setAiSuggestion("Failed to load study tips. Please try again.");
      toast.error("Failed to load study tips.");
    }
  };

  const generateSchedule = async () => {
    setAiSuggestion("Generating personalized study schedule...");
    try {
      const suggestion = await getStudySuggestion(tasks, studyStats);
      setAiSuggestion(suggestion);
    } catch (err) {
      setAiSuggestion("Failed to generate schedule. Please try again.");
      toast.error("Failed to generate schedule.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className={`min-h-screen`}>
      <ToastContainer />
      <header className={`p-4 ${themeConfig[currentTheme].headerBg}`}>
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center">
            <h1 className="text-2xl font-bold flex items-center">
              <BookOpen className="mr-2" />
              StudyBuddy AI Planner
            </h1>
            <div
              onClick={handleThemeChange}
              className="ml-4 cursor-pointer flex items-center space-x-2"
            >
              <FontAwesomeIcon
                icon={themeIcons[nextTheme]}
                className="text-xl"
              />
              <span className="text-sm">{nextTheme}</span>
            </div>
          </div>
          <div className="flex items-center space-x-4">
            <div
              className={`px-4 py-2 rounded-lg text-sm ${themeConfig[currentTheme].primary} ${themeConfig[currentTheme].primaryText}`}
            >
              {new Date().toLocaleDateString("en-US", {
                weekday: "long",
                year: "numeric",
                month: "long",
                day: "numeric",
              })}
            </div>
            <button
              onClick={handleLogout}
              className={`px-4 py-2 rounded-lg text-sm ${themeConfig[currentTheme].primary} ${themeConfig[currentTheme].primaryText} ${themeConfig[currentTheme].primaryHover}`}
            >
              Logout
            </button>
          </div>
        </div>
        <nav className="mt-4">
          <ul className="flex space-x-4">
            {["dashboard", "tasks", "analytics", "rewards"].map((tab) => (
              <li key={tab}>
                <button
                  onClick={() => setActiveTab(tab)}
                  className={`px-4 py-2 rounded-lg text-sm ${
                    activeTab === tab
                      ? `${themeConfig[currentTheme].primary} ${themeConfig[currentTheme].primaryText}`
                      : `${themeConfig[currentTheme].secondaryBg} ${themeConfig[currentTheme].text} ${themeConfig[currentTheme].hoverBg}`
                  } transition-colors duration-200 capitalize`}
                >
                  {tab}
                </button>
              </li>
            ))}
          </ul>
        </nav>
      </header>
      <main className="container mx-auto p-4">
        {loading ? (
          <div className="text-center py-8">
            <div className="animate-pulse flex justify-center space-x-2">
              <div
                className={`w-4 h-4 rounded-full ${themeConfig[currentTheme].primary}`}
              ></div>
              <div
                className={`w-4 h-4 rounded-full ${themeConfig[currentTheme].primary}`}
              ></div>
              <div
                className={`w-4 h-4 rounded-full ${themeConfig[currentTheme].primary}`}
              ></div>
            </div>
            <p className="mt-2">Loading your study data...</p>
          </div>
        ) : (
          <>
            {activeTab === "dashboard" && (
              <Dashboard
                tasks={tasks}
                studyStats={studyStats}
                aiSuggestion={aiSuggestion}
                getStudyTips={getStudyTips}
                generateSchedule={generateSchedule}
                setActiveTab={setActiveTab}
                toggleTaskComplete={toggleTaskComplete}
                deleteTask={deleteTask}
                themeConfig={themeConfig[currentTheme]}
                logStudyHours={logStudyHours}
              />
            )}
            {activeTab === "tasks" && (
              <TaskManager
                tasks={tasks}
                addTask={addTask}
                updateTask={updateTask}
                deleteTask={deleteTask}
                toggleTaskComplete={toggleTaskComplete}
                themeConfig={themeConfig[currentTheme]}
              />
            )}
            {activeTab === "analytics" && (
              <Analytics
                tasks={tasks}
                themeConfig={themeConfig[currentTheme]}
              />
            )}
            {activeTab === "rewards" && (
              <Rewards
                points={points}
                badges={badges}
                studyStats={studyStats}
                themeConfig={themeConfig[currentTheme]}
                unlockedThemes={unlockedThemes}
                onPointsUpdate={handlePointsUpdate}
                onThemeUnlock={handleThemeUnlock}
              />
            )}
          </>
        )}
      </main>
    </div>
  );
}

export default Layout;
