import React, { useState, useEffect } from "react";
import { BookOpen } from "lucide-react";
import Dashboard from "./Dashboard";
import TaskManager from "./TaskManager";
import Analytics from "./Analytics";
import Rewards from "./Rewards"; // New Rewards component

function Layout() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [tasks, setTasks] = useState([]);
  const [studyStats, setStudyStats] = useState({
    totalHours: 0,
    completedTasks: 0,
    streak: 5,
  });
  const [aiSuggestion, setAiSuggestion] = useState("");
  // New state for gamification
  const [points, setPoints] = useState(0);
  const [badges, setBadges] = useState([]);

  // Simulate loading initial data
  useEffect(() => {
    // Load tasks from localStorage or use default data
    const savedTasks = localStorage.getItem("studyBuddyTasks");
    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    } else {
      // Mock data
      setTasks([
        {
          id: 1,
          title: "Complete Math Assignment",
          dueDate: "2025-04-25",
          priority: "High",
          completed: false,
        },
        {
          id: 2,
          title: "Review Chemistry Notes",
          dueDate: "2025-04-23",
          priority: "Medium",
          completed: true,
        },
        {
          id: 3,
          title: "Research Paper Outline",
          dueDate: "2025-04-30",
          priority: "High",
          completed: false,
        },
      ]);
    }

    // Load stats from localStorage or use default data
    const savedStats = localStorage.getItem("studyBuddyStats");
    if (savedStats) {
      setStudyStats(JSON.parse(savedStats));
    }

    // Load points and badges from localStorage
    const savedPoints = localStorage.getItem("studyBuddyPoints");
    const savedBadges = localStorage.getItem("studyBuddyBadges");
    if (savedPoints) {
      setPoints(parseInt(savedPoints));
    }
    if (savedBadges) {
      setBadges(JSON.parse(savedBadges));
    }

    // Simulate AI suggestion
    setTimeout(() => {
      setAiSuggestion(
        "Based on your schedule and learning patterns, I recommend focusing on your Math Assignment today. You tend to work best on math problems in the afternoon."
      );
    }, 1000);
  }, []);

  // Save tasks, stats, points, and badges to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem("studyBuddyTasks", JSON.stringify(tasks));

    // Update stats based on tasks
    const completedCount = tasks.filter((task) => task.completed).length;
    setStudyStats((prevStats) => {
      const newStats = {
        ...prevStats,
        completedTasks: completedCount,
      };
      localStorage.setItem("studyBuddyStats", JSON.stringify(newStats));
      return newStats;
    });

    // Save points and badges
    localStorage.setItem("studyBuddyPoints", points.toString());
    localStorage.setItem("studyBuddyBadges", JSON.stringify(badges));
  }, [tasks, points, badges]);

  // Get AI study tips
  const getStudyTips = () => {
    setAiSuggestion("Loading new suggestions...");

    // Simulate AI request delay
    setTimeout(() => {
      const tips = [
        "Try using the Pomodoro technique: 25 minutes of focused study followed by a 5-minute break.",
        "For difficult subjects, try teaching the material to an imaginary student to improve your understanding.",
        "Create visual mind maps to connect key concepts in your subjects.",
        "Review your notes within 24 hours of taking them to improve retention.",
        "Study in short, frequent sessions rather than long cramming sessions.",
      ];

      const randomTip = tips[Math.floor(Math.random() * tips.length)];
      setAiSuggestion(randomTip);
    }, 1500);
  };

  // Generate AI schedule
  const generateSchedule = () => {
    setAiSuggestion("Generating personalized study schedule...");

    // Simulate AI request delay
    setTimeout(() => {
      const schedule =
        "Based on your upcoming tasks and past performance, here's a suggested schedule:\n\n" +
        "- Tuesday (Today): Math Assignment (2 hours) in the afternoon\n" +
        "- Wednesday: Chemistry Notes review (1 hour) in the morning\n" +
        "- Thursday: Start Research Paper Outline (1.5 hours)\n" +
        "- Friday: Complete remaining tasks and review progress";

      setAiSuggestion(schedule);
    }, 2000);
  };

  // Task CRUD operations
  const addTask = (newTask) => {
    const task = {
      id: Date.now(),
      ...newTask,
    };
    setTasks([...tasks, task]);
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const toggleTaskComplete = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );

    // Award points when a task is completed
    const task = tasks.find((task) => task.id === id);
    if (task && !task.completed) {
      let pointsToAdd = 0;
      if (task.priority === "High") pointsToAdd = 30;
      else if (task.priority === "Medium") pointsToAdd = 20;
      else pointsToAdd = 10;
      setPoints((prev) => prev + pointsToAdd);

      // Check for badges
      const highPriorityTasks = tasks.filter(
        (t) => t.priority === "High" && t.completed
      ).length;
      if (highPriorityTasks + 1 >= 5 && !badges.includes("Priority Master")) {
        setBadges((prev) => [...prev, "Priority Master"]);
      }
    }
  };

  const updateTask = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task.id === updatedTask.id ? updatedTask : task))
    );
  };

  // Award streak bonus
  useEffect(() => {
    if (studyStats.streak >= 7 && !badges.includes("Streak Star")) {
      setBadges((prev) => [...prev, "Streak Star"]);
      setPoints((prev) => prev + 50); // Bonus points for 7-day streak
    }
  }, [studyStats.streak, badges]);

  return (
    <div className="min-h-screen bg-slate-50 text-gray-800">
      {/* Header */}
      <header className="bg-indigo-600 text-white p-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold flex items-center">
            <BookOpen className="mr-2" />
            StudyBuddy AI Planner
          </h1>
          <div className="bg-indigo-700 px-4 py-2 rounded-lg text-sm">
            {new Date().toLocaleDateString("en-US", {
              weekday: "long",
              year: "numeric",
              month: "long",
              day: "numeric",
            })}
          </div>
        </div>
      </header>

      {/* Navigation */}
      <nav className="bg-white shadow">
        <div className="container mx-auto">
          <div className="flex">
            <button
              onClick={() => setActiveTab("dashboard")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "dashboard"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Dashboard
            </button>
            <button
              onClick={() => setActiveTab("tasks")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "tasks"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Tasks
            </button>
            <button
              onClick={() => setActiveTab("analytics")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "analytics"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Analytics
            </button>
            <button
              onClick={() => setActiveTab("rewards")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "rewards"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : "text-gray-500"
              }`}
            >
              Rewards
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="container mx-auto p-4">
        {activeTab === "dashboard" && (
          <Dashboard
            tasks={tasks}
            studyStats={studyStats}
            aiSuggestion={aiSuggestion}
            getStudyTips={getStudyTips}
            generateSchedule={generateSchedule}
            setActiveTab={setActiveTab}
            toggleTaskComplete={toggleTaskComplete}
            deleteTask={deleteTask}
          />
        )}

        {activeTab === "tasks" && (
          <TaskManager
            tasks={tasks}
            addTask={addTask}
            updateTask={updateTask}
            deleteTask={deleteTask}
            toggleTaskComplete={toggleTaskComplete}
          />
        )}

        {activeTab === "analytics" && <Analytics tasks={tasks} />}

        {activeTab === "rewards" && (
          <Rewards points={points} badges={badges} studyStats={studyStats} />
        )}
      </main>
    </div>
  );
}

export default Layout;
