import React, { useState } from "react";
import { Edit2, Trash2, CheckCircle, Circle } from "lucide-react";
import { toast } from "react-toastify";

function TaskManager({
  tasks,
  addTask,
  updateTask,
  deleteTask,
  toggleTaskComplete,
  currentTheme,
}) {
  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";
  const isSunsetGlow = currentTheme === "Sunset Glow";
  const isForestWhisper = currentTheme === "Forest Whisper";

  const [newTask, setNewTask] = useState({
    title: "",
    dueDate: "",
    priority: "Medium",
    subject: "",
    hours: "", // Added hours field
  });
  const [editingTask, setEditingTask] = useState(null);

  // Helper function to format the local date as YYYY-MM-DD
  const getLocalDateString = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0"); // getMonth() is 0-based
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  const today = getLocalDateString(new Date());

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editingTask) {
      setEditingTask({ ...editingTask, [name]: value });
    } else {
      setNewTask({ ...newTask, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingTask) {
      if (
        !editingTask.title ||
        !editingTask.dueDate ||
        !editingTask.priority ||
        !editingTask.subject
      ) {
        toast.error("Please fill in all required fields.");
        return;
      }
      const hours = parseFloat(editingTask.hours);
      if (isNaN(hours) || hours < 0) {
        toast.error("Please enter a valid number of hours (0 or greater).");
        return;
      }
      updateTask({ ...editingTask, hours });
      setEditingTask(null);
      toast.success("Task updated successfully!");
    } else {
      if (
        !newTask.title ||
        !newTask.dueDate ||
        !newTask.priority ||
        !newTask.subject
      ) {
        toast.error("Please fill in all required fields.");
        return;
      }
      const hours = parseFloat(newTask.hours);
      if (isNaN(hours) || hours < 0) {
        toast.error("Please enter a valid number of hours (0 or greater).");
        return;
      }
      addTask({ ...newTask, hours });
      setNewTask({
        title: "",
        dueDate: "",
        priority: "Medium",
        subject: "",
        hours: "",
      });
      toast.success("Task added successfully!");
    }
  };

  const handleEdit = (task) => {
    setEditingTask(task);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const sortedTasks = [...tasks].sort((a, b) => {
    if (a.completed !== b.completed) {
      return a.completed ? 1 : -1;
    }
    const priorityOrder = { High: 3, Medium: 2, Low: 1 };
    if (a.priority !== b.priority) {
      return priorityOrder[b.priority] - priorityOrder[a.priority];
    }
    return new Date(a.dueDate) - new Date(b.dueDate);
  });

  return (
    <div
      className={`rounded-xl shadow-lg transition-all duration-300 ${
        isDarkMode
          ? "bg-gray-800 border border-gray-700"
          : isOceanBreeze
            ? "bg-gradient-to-br from-teal-100 to-blue-200 bg-opacity-90 backdrop-blur-lg border border-teal-300"
            : isSunsetGlow
              ? "bg-gradient-to-br from-orange-100 to-pink-200 bg-opacity-90 backdrop-blur-lg border border-orange-300"
              : isForestWhisper
                ? "bg-gradient-to-br from-green-100 to-emerald-200 bg-opacity-90 backdrop-blur-lg border border-green-300"
                : "bg-white border border-gray-200"
      }`}
    >
      <div
        className={`p-5 border-b ${
          isDarkMode
            ? "border-gray-600"
            : isOceanBreeze
              ? "border-teal-400"
              : isSunsetGlow
                ? "border-orange-400"
                : isForestWhisper
                  ? "border-green-400"
                  : "border-gray-300"
        }`}
      >
        <h2
          className={`text-xl font-bold tracking-tight ${
            isDarkMode
              ? "text-gray-100"
              : isOceanBreeze
                ? "text-teal-900"
                : isSunsetGlow
                  ? "text-orange-900"
                  : isForestWhisper
                    ? "text-green-900"
                    : "text-gray-900"
          }`}
        >
          Task Manager
        </h2>
      </div>

      <div className="p-6 space-y-6">
        {/* Task Form */}
        <div
          className={`p-5 rounded-lg shadow-md transition-all duration-300 ${
            isDarkMode
              ? "bg-gray-700"
              : isOceanBreeze
                ? "bg-teal-50 bg-opacity-90 backdrop-blur-md"
                : isSunsetGlow
                  ? "bg-pink-50 bg-opacity-90 backdrop-blur-md"
                  : isForestWhisper
                    ? "bg-emerald-50 bg-opacity-90 backdrop-blur-md"
                    : "bg-gray-50"
          }`}
        >
          <h3
            className={`text-lg font-semibold mb-4 tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            {editingTask ? "Edit Task" : "Add New Task"}
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label
                className={`block text-sm font-medium mb-1 ${
                  isDarkMode
                    ? "text-gray-300"
                    : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                          ? "text-green-500"
                          : "text-gray-500"
                }`}
              >
                Title
              </label>
              <input
                type="text"
                name="title"
                value={editingTask ? editingTask.title : newTask.title}
                onChange={handleInputChange}
                className={`w-full p-2 rounded-lg border focus:ring-2 focus:ring-opacity-50 transition-all duration-200 ${
                  isDarkMode
                    ? "bg-gray-800 border-gray-600 text-gray-100 focus:ring-indigo-500"
                    : isOceanBreeze
                      ? "bg-white border-teal-300 text-teal-900 focus:ring-teal-500"
                      : isSunsetGlow
                        ? "bg-white border-orange-300 text-orange-900 focus:ring-orange-500"
                        : isForestWhisper
                          ? "bg-white border-green-300 text-green-900 focus:ring-green-500"
                          : "bg-white border-gray-300 text-gray-900 focus:ring-indigo-500"
                }`}
                placeholder="Enter task title"
              />
            </div>
            <div>
              <label
                className={`block text-sm font-medium mb-1 ${
                  isDarkMode
                    ? "text-gray-300"
                    : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                          ? "text-green-500"
                          : "text-gray-500"
                }`}
              >
                Due Date
              </label>
              <input
                type="date"
                name="dueDate"
                value={editingTask ? editingTask.dueDate : newTask.dueDate}
                onChange={handleInputChange}
                className={`w-full p-2 rounded-lg border focus:ring-2 focus:ring-opacity-50 transition-all duration-200 ${
                  isDarkMode
                    ? "bg-gray-800 border-gray-600 text-gray-100 focus:ring-indigo-500"
                    : isOceanBreeze
                      ? "bg-white border-teal-300 text-teal-900 focus:ring-teal-500"
                      : isSunsetGlow
                        ? "bg-white border-orange-300 text-orange-900 focus:ring-orange-500"
                        : isForestWhisper
                          ? "bg-white border-green-300 text-green-900 focus:ring-green-500"
                          : "bg-white border-gray-300 text-gray-900 focus:ring-indigo-500"
                }`}
              />
            </div>
            <div>
              <label
                className={`block text-sm font-medium mb-1 ${
                  isDarkMode
                    ? "text-gray-300"
                    : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                          ? "text-green-500"
                          : "text-gray-500"
                }`}
              >
                Priority
              </label>
              <select
                name="priority"
                value={editingTask ? editingTask.priority : newTask.priority}
                onChange={handleInputChange}
                className={`w-full p-2 rounded-lg border focus:ring-2 focus:ring-opacity-50 transition-all duration-200 ${
                  isDarkMode
                    ? "bg-gray-800 border-gray-600 text-gray-100 focus:ring-indigo-500"
                    : isOceanBreeze
                      ? "bg-white border-teal-300 text-teal-900 focus:ring-teal-500"
                      : isSunsetGlow
                        ? "bg-white border-orange-300 text-orange-900 focus:ring-orange-500"
                        : isForestWhisper
                          ? "bg-white border-green-300 text-green-900 focus:ring-green-500"
                          : "bg-white border-gray-300 text-gray-900 focus:ring-indigo-500"
                }`}
              >
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
              </select>
            </div>
            <div>
              <label
                className={`block text-sm font-medium mb-1 ${
                  isDarkMode
                    ? "text-gray-300"
                    : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                          ? "text-green-500"
                          : "text-gray-500"
                }`}
              >
                Subject
              </label>
              <input
                type="text"
                name="subject"
                value={editingTask ? editingTask.subject : newTask.subject}
                onChange={handleInputChange}
                className={`w-full p-2 rounded-lg border focus:ring-2 focus:ring-opacity-50 transition-all duration-200 ${
                  isDarkMode
                    ? "bg-gray-800 border-gray-600 text-gray-100 focus:ring-indigo-500"
                    : isOceanBreeze
                      ? "bg-white border-teal-300 text-teal-900 focus:ring-teal-500"
                      : isSunsetGlow
                        ? "bg-white border-orange-300 text-orange-900 focus:ring-orange-500"
                        : isForestWhisper
                          ? "bg-white border-green-300 text-green-900 focus:ring-green-500"
                          : "bg-white border-gray-300 text-gray-900 focus:ring-indigo-500"
                }`}
                placeholder="Enter subject"
              />
            </div>
            <div>
              <label
                className={`block text-sm font-medium mb-1 ${
                  isDarkMode
                    ? "text-gray-300"
                    : isOceanBreeze
                      ? "text-blue-600"
                      : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                          ? "text-green-500"
                          : "text-gray-500"
                }`}
              >
                Estimated Hours
              </label>
              <input
                type="number"
                name="hours"
                value={editingTask ? editingTask.hours : newTask.hours}
                onChange={handleInputChange}
                className={`w-full p-2 rounded-lg border focus:ring-2 focus:ring-opacity-50 transition-all duration-200 ${
                  isDarkMode
                    ? "bg-gray-800 border-gray-600 text-gray-100 focus:ring-indigo-500"
                    : isOceanBreeze
                      ? "bg-white border-teal-300 text-teal-900 focus:ring-teal-500"
                      : isSunsetGlow
                        ? "bg-white border-orange-300 text-orange-900 focus:ring-orange-500"
                        : isForestWhisper
                          ? "bg-white border-green-300 text-green-900 focus:ring-green-500"
                          : "bg-white border-gray-300 text-gray-900 focus:ring-indigo-500"
                }`}
                placeholder="Enter estimated hours"
                min="0"
                step="0.1"
              />
            </div>
          </div>
          <div className="mt-6 flex space-x-3">
            <button
              onClick={handleSubmit}
              className={`px-4 py-2 rounded-lg font-semibold transition-all duration-200 transform hover:scale-105 ${
                isDarkMode
                  ? "bg-indigo-600 text-white hover:bg-indigo-700"
                  : isOceanBreeze
                    ? "bg-teal-600 text-white hover:bg-teal-700"
                    : isSunsetGlow
                      ? "bg-orange-600 text-white hover:bg-orange-700"
                      : isForestWhisper
                        ? "bg-green-600 text-white hover:bg-green-700"
                        : "bg-indigo-600 text-white hover:bg-indigo-700"
              }`}
            >
              {editingTask ? "Update Task" : "Add Task"}
            </button>
            {editingTask && (
              <button
                onClick={handleCancelEdit}
                className={`px-4 py-2 rounded-lg font-semibold transition-all duration-200 transform hover:scale-105 ${
                  isDarkMode
                    ? "bg-gray-600 text-gray-200 hover:bg-gray-500"
                    : isOceanBreeze
                      ? "bg-blue-600 text-white hover:bg-blue-700"
                      : isSunsetGlow
                        ? "bg-red-600 text-white hover:bg-red-700"
                        : isForestWhisper
                          ? "bg-emerald-600 text-white hover:bg-emerald-700"
                          : "bg-gray-500 text-white hover:bg-gray-600"
                }`}
              >
                Cancel
              </button>
            )}
          </div>
        </div>

        {/* Task List */}
        <div>
          <h3
            className={`text-lg font-semibold mb-4 tracking-tight ${
              isDarkMode
                ? "text-gray-100"
                : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                    ? "text-orange-800"
                    : isForestWhisper
                      ? "text-green-800"
                      : "text-gray-800"
            }`}
          >
            Your Tasks
          </h3>
          {sortedTasks.length === 0 ? (
            <p
              className={`text-sm ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                      ? "text-orange-500"
                      : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
              }`}
            >
              No tasks to display. Add a new task to get started!
            </p>
          ) : (
            <div className="space-y-3 max-h-[600px] overflow-y-auto">
              {sortedTasks.map((task) => {
                const isOverdue = !task.completed && task.dueDate < today;
                const isDueToday = !task.completed && task.dueDate === today;
                const isFuture = !task.completed && task.dueDate > today;

                return (
                  <div
                    key={task.id}
                    className={`flex items-center p-4 rounded-lg shadow-sm transition-all duration-200 hover:shadow-md ${
                      isDarkMode
                        ? "bg-gray-700 hover:bg-gray-600"
                        : isOceanBreeze
                          ? "bg-amber-50 hover:bg-teal-100 bg-opacity-90 backdrop-blur-md"
                          : isSunsetGlow
                            ? "bg-pink-50 hover:bg-orange-100 bg-opacity-90 backdrop-blur-md"
                            : isForestWhisper
                              ? "bg-emerald-50 hover:bg-green-100 bg-opacity-90 backdrop-blur-md"
                              : "bg-gray-50 hover:bg-gray-100"
                    } ${task.completed ? "opacity-60" : ""}`}
                  >
                    <button
                      onClick={() => toggleTaskComplete(task.id)}
                      className="mr-3"
                    >
                      {task.completed ? (
                        <CheckCircle
                          className={`w-5 h-5 ${
                            isDarkMode
                              ? "text-indigo-400"
                              : isOceanBreeze
                                ? "text-teal-500"
                                : isSunsetGlow
                                  ? "text-orange-500"
                                  : isForestWhisper
                                    ? "text-green-500"
                                    : "text-indigo-600"
                          }`}
                        />
                      ) : (
                        <Circle
                          className={`w-5 h-5 ${
                            isDarkMode
                              ? "text-gray-400"
                              : isOceanBreeze
                                ? "text-blue-500"
                                : isSunsetGlow
                                  ? "text-orange-400"
                                  : isForestWhisper
                                    ? "text-green-400"
                                    : "text-gray-400"
                          }`}
                        />
                      )}
                    </button>
                    <div className="flex-1">
                      <p
                        className={`text-sm font-medium break-words ${
                          task.completed
                            ? "line-through " +
                              (isDarkMode
                                ? "text-gray-400"
                                : isOceanBreeze
                                  ? "text-blue-600"
                                  : isSunsetGlow
                                    ? "text-orange-500"
                                    : isForestWhisper
                                      ? "text-green-500"
                                      : "text-gray-500")
                            : isDarkMode
                              ? "text-gray-200"
                              : isOceanBreeze
                                ? "text-teal-800"
                                : isSunsetGlow
                                  ? "text-orange-800"
                                  : isForestWhisper
                                    ? "text-green-800"
                                    : "text-gray-800"
                        }`}
                      >
                        {task.title}
                      </p>
                      <p
                        className={`text-xs ${
                          isDarkMode
                            ? "text-gray-400"
                            : isOceanBreeze
                              ? "text-blue-600"
                              : isSunsetGlow
                                ? "text-orange-500"
                                : isForestWhisper
                                  ? "text-green-500"
                                  : "text-gray-500"
                        }`}
                      >
                        Due: {task.dueDate} | Priority: {task.priority} |{" "}
                        Subject: {task.subject} | Hours: {task.hours}
                        {task.completed &&
                          ` | Completed: ${task.completedDate}`}
                        {isOverdue && " (Overdue)"}
                        {isDueToday && " (Due Today)"}
                        {isFuture && " (Future)"}
                      </p>
                    </div>
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleEdit(task)}
                        className={`p-2 rounded-full transition-all duration-200 hover:scale-110 ${
                          isDarkMode
                            ? "text-gray-300 hover:bg-gray-600"
                            : isOceanBreeze
                              ? "text-blue-600 hover:bg-teal-200"
                              : isSunsetGlow
                                ? "text-orange-500 hover:bg-orange-200"
                                : isForestWhisper
                                  ? "text-green-500 hover:bg-green-200"
                                  : "text-gray-500 hover:bg-gray-200"
                        }`}
                      >
                        <Edit2 className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => deleteTask(task.id)}
                        className={`p-2 rounded-full transition-all duration-200 hover:scale-110 ${
                          isDarkMode
                            ? "text-gray-300 hover:bg-gray-600"
                            : isOceanBreeze
                              ? "text-blue-600 hover:bg-teal-200"
                              : isSunsetGlow
                                ? "text-orange-500 hover:bg-orange-200"
                                : isForestWhisper
                                  ? "text-green-500 hover:bg-green-200"
                                  : "text-gray-500 hover:bg-gray-200"
                        }`}
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default TaskManager;
