import React from "react";
import { Trophy, Medal, Flame } from "lucide-react";
import { toast } from "react-toastify";

// Theme configuration for consistent styling
const themeStyles = {
  "Light Mode": {
    bg: "bg-white",
    border: "border-gray-200",
    text: "text-gray-800",
    subText: "text-gray-500",
    accent: "bg-indigo-600",
    cardBg: "bg-gray-50",
    badgeEarned: "bg-indigo-50",
    badgeNotEarned: "bg-gray-100 opacity-50",
    buttonPrimary: "bg-indigo-600 text-white hover:bg-indigo-700",
    buttonDisabled: "bg-gray-400 cursor-not-allowed",
    streakIcon: "text-orange-600",
    statusEarned: "text-green-600",
    statusNotEarned: "text-gray-400",
  },
  "Dark Mode": {
    bg: "bg-gray-700",
    border: "border-gray-600",
    text: "text-gray-200",
    subText: "text-gray-400",
    accent: "bg-indigo-500",
    cardBg: "bg-gray-600",
    badgeEarned: "bg-indigo-900",
    badgeNotEarned: "bg-gray-600 opacity-50",
    buttonPrimary: "bg-indigo-500 text-white hover:bg-indigo-600",
    buttonDisabled: "bg-gray-400 cursor-not-allowed",
    streakIcon: "text-orange-400",
    statusEarned: "text-green-600",
    statusNotEarned: "text-gray-500",
  },
  "Ocean Breeze": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-teal-300",
    text: "text-teal-800",
    subText: "text-blue-600",
    accent: "bg-teal-500",
    cardBg: "bg-amber-100 bg-opacity-70 backdrop-blur-sm",
    badgeEarned: "bg-teal-100 bg-opacity-70 backdrop-blur-sm",
    badgeNotEarned: "bg-gray-200 opacity-50 backdrop-blur-sm",
    buttonPrimary: "bg-teal-500 text-white hover:bg-teal-600",
    buttonDisabled: "bg-gray-400 cursor-not-allowed",
    streakIcon: "text-orange-500",
    statusEarned: "text-green-600",
    statusNotEarned: "text-blue-500",
  },
  "Sunset Glow": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-orange-300",
    text: "text-orange-800",
    subText: "text-orange-500",
    accent: "bg-orange-500",
    cardBg: "bg-pink-100 bg-opacity-70 backdrop-blur-sm",
    badgeEarned: "bg-orange-100 bg-opacity-70 backdrop-blur-sm",
    badgeNotEarned: "bg-gray-200 opacity-50 backdrop-blur-sm",
    buttonPrimary: "bg-orange-500 text-white hover:bg-orange-600",
    buttonDisabled: "bg-gray-400 cursor-not-allowed",
    streakIcon: "text-orange-600",
    statusEarned: "text-green-600",
    statusNotEarned: "text-orange-400",
  },
  "Forest Whisper": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-green-300",
    text: "text-green-800",
    subText: "text-green-500",
    accent: "bg-green-500",
    cardBg: "bg-emerald-100 bg-opacity-70 backdrop-blur-sm",
    badgeEarned: "bg-green-100 bg-opacity-70 backdrop-blur-sm",
    badgeNotEarned: "bg-gray-200 opacity-50 backdrop-blur-sm",
    buttonPrimary: "bg-green-500 text-white hover:bg-green-600",
    buttonDisabled: "bg-gray-400 cursor-not-allowed",
    streakIcon: "text-green-600",
    statusEarned: "text-green-600",
    statusNotEarned: "text-green-400",
  },
};

const unlockableThemes = [
  {
    name: "Dark Mode",
    cost: 50,
    description: "A sleek dark theme for night owls.",
  },
  {
    name: "Ocean Breeze",
    cost: 50,
    description: "A refreshing blue and teal gradient theme.",
  },
  {
    name: "Sunset Glow",
    cost: 50,
    description: "A warm theme with hues of orange and pink.",
  },
  {
    name: "Forest Whisper",
    cost: 50,
    description: "A calming green theme inspired by nature.",
  },
];

function Rewards({
  points,
  badges,
  studyStats,
  currentTheme,
  onPointsUpdate,
  onThemeUnlock,
  unlockedThemes, // New prop to track unlocked themes
}) {
  // Select theme styles, default to Light Mode if theme not found
  const currentThemeStyles =
    themeStyles[currentTheme] || themeStyles["Light Mode"];

  const availableBadges = [
    {
      name: "Priority Master",
      description: "Complete 5 high-priority tasks",
      icon: <Trophy className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Task Titan",
      description: "Complete 10 tasks",
      icon: <Medal className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Early Bird",
      description: "Complete 3 tasks before their due date",
      icon: <Trophy className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Streak Star",
      description: "Maintain a 7-day study streak",
      icon: <Flame className="w-8 h-8 text-yellow-500" />,
    },
  ];

  const handleRedeemTheme = (theme) => {
    if (unlockedThemes.includes(theme.name)) {
      toast.info(`${theme.name} is already unlocked!`, { autoClose: 2000 });
      return;
    }
    if (points < theme.cost) {
      toast.error("Not enough points to redeem this theme!", {
        autoClose: 2000,
      });
      return;
    }

    const newPoints = points - theme.cost;
    onPointsUpdate(newPoints);
    onThemeUnlock(theme.name);
    toast.success(`${theme.name} theme unlocked!`, { autoClose: 2000 });
  };

  return (
    <div className={`rounded-lg shadow ${currentThemeStyles.bg}`}>
      <div className={`p-4 border-b ${currentThemeStyles.border}`}>
        <h2 className={`font-semibold ${currentThemeStyles.text}`}>
          Rewards & Achievements
        </h2>
      </div>
      <div className="p-4 sm:p-8">
        {/* Total Points */}
        <div
          className={`p-4 rounded-lg text-center ${currentThemeStyles.cardBg}`}
        >
          <h3
            className={`text-3xl font-bold mb-2 ${currentThemeStyles.accent}`}
          >
            {points}
          </h3>
          <p className={currentThemeStyles.subText}>Total Points Earned</p>
        </div>

        {/* Your Badges */}
        <div className="mt-8">
          <h3 className={`font-medium mb-4 ${currentThemeStyles.text}`}>
            Your Badges
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {availableBadges.map((badge) => (
              <div
                key={badge.name}
                className={`p-4 rounded-lg text-center ${
                  badges.includes(badge.name)
                    ? currentThemeStyles.badgeEarned
                    : currentThemeStyles.badgeNotEarned
                }`}
                aria-label={`Badge: ${badge.name}, ${
                  badges.includes(badge.name) ? "Earned" : "Not yet earned"
                }`}
              >
                <div className="flex justify-center">{badge.icon}</div>
                <h4 className={`font-medium mt-2 ${currentThemeStyles.text}`}>
                  {badge.name}
                </h4>
                <p className={`text-sm ${currentThemeStyles.subText}`}>
                  {badge.description}
                </p>
                <p
                  className={`text-sm font-medium mt-1 ${
                    badges.includes(badge.name)
                      ? currentThemeStyles.statusEarned
                      : currentThemeStyles.statusNotEarned
                  }`}
                >
                  {badges.includes(badge.name) ? "Earned!" : "Not yet earned"}
                </p>
              </div>
            ))}
          </div>
        </div>

        {/* Study Streaks */}
        <div className="mt-8">
          <h3 className={`font-medium mb-4 ${currentThemeStyles.text}`}>
            Study Streaks
          </h3>
          <div
            className={`p-4 rounded-lg text-center ${currentThemeStyles.cardBg}`}
          >
            <Flame
              className={`inline-block w-8 h-8 mb-2 ${currentThemeStyles.streakIcon}`}
              aria-hidden="true"
            />
            <h4 className={`text-lg font-medium ${currentThemeStyles.text}`}>
              Current Streak: {studyStats.streak} Days
            </h4>
            <p className={`text-sm ${currentThemeStyles.subText}`}>
              Keep studying daily to maintain your streak!
            </p>
          </div>
        </div>

        {/* Redeem Themes */}
        <div className="mt-8">
          <h3 className={`font-medium mb-4 ${currentThemeStyles.text}`}>
            Redeem Themes
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {unlockableThemes.map((theme) => (
              <div
                key={theme.name}
                className={`p-4 rounded-lg text-center ${currentThemeStyles.cardBg}`}
              >
                <h4 className={`font-medium mt-2 ${currentThemeStyles.text}`}>
                  {theme.name}
                </h4>
                <p className={`text-sm ${currentThemeStyles.subText}`}>
                  {theme.description}
                </p>
                <p
                  className={`text-sm font-medium mt-1 ${currentThemeStyles.subText}`}
                >
                  Cost: {theme.cost} Points
                </p>
                {unlockedThemes.includes(theme.name) ? (
                  <p
                    className={`text-sm font-medium mt-2 ${currentThemeStyles.statusEarned}`}
                  >
                    Unlocked!
                  </p>
                ) : (
                  <button
                    onClick={() => handleRedeemTheme(theme)}
                    className={`mt-2 px-4 py-2 rounded text-white text-sm ${
                      points >= theme.cost
                        ? currentThemeStyles.buttonPrimary
                        : currentThemeStyles.buttonDisabled
                    } transition-colors duration-200`}
                    disabled={points < theme.cost}
                    aria-label={`Redeem ${theme.name} theme for ${theme.cost} points`}
                  >
                    Redeem
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Rewards;
