import React from "react";
import { Trophy, Medal, Flame } from "lucide-react";

// Define unlockable themes with point costs
const unlockableThemes = [
  {
    name: "Dark Mode",
    cost: 50,
    description: "A sleek dark theme for night owls.",
  },
  {
    name: "Ocean Breeze",
    cost: 50,
    description: "A refreshing blue and teal gradient theme.",
  },
  {
    name: "Sunset Glow",
    cost: 50,
    description: "A warm theme with hues of orange and pink.",
  },
  {
    name: "Forest Whisper",
    cost: 50,
    description: "A calming green theme inspired by nature.",
  },
];

function Rewards({
  points,
  badges,
  studyStats,
  currentTheme,
  onPointsUpdate,
  onThemeUnlock,
}) {
  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";

  const availableBadges = [
    {
      name: "Priority Master",
      description: "Complete 5 high-priority tasks",
      icon: <Trophy className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Task Titan",
      description: "Complete 10 tasks",
      icon: <Medal className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Early Bird",
      description: "Complete 3 tasks before their due date",
      icon: <Trophy className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Streak Star",
      description: "Maintain a 7-day study streak",
      icon: <Flame className="w-8 h-8 text-yellow-500" />,
    },
  ];

  // Handle redeeming a theme
  const handleRedeemTheme = (theme) => {
    if (points < theme.cost) {
      alert("Not enough points to redeem this theme!");
      return;
    }

    // Deduct points and update via callback
    const newPoints = points - theme.cost;
    onPointsUpdate(newPoints); // Update points in Layout.jsx
    onThemeUnlock(theme.name); // Notify Layout.jsx of the unlocked theme
    alert(`${theme.name} theme unlocked!`);
  };

  return (
    <div
      className={`rounded-lg shadow ${
        isDarkMode
          ? "bg-gray-700"
          : isOceanBreeze
          ? "bg-opacity-70 backdrop-blur-lg"
          : "bg-white"
      }`}
    >
      <div
        className={`p-4 border-b ${
          isDarkMode
            ? "border-gray-600"
            : isOceanBreeze
            ? "border-teal-300"
            : "border-gray-200"
        }`}
      >
        <h2
          className={`font-semibold ${
            isDarkMode
              ? "text-gray-200"
              : isOceanBreeze
              ? "text-teal-800"
              : "text-gray-800"
          }`}
        >
          Rewards & Achievements
        </h2>
      </div>
      <div className="p-8">
        <div
          className={`p-4 rounded-lg text-center ${
            isDarkMode
              ? "bg-gray-600"
              : isOceanBreeze
              ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm"
              : "bg-gray-50"
          }`}
        >
          <h3
            className={`text-3xl font-bold mb-2 ${
              isDarkMode
                ? "text-indigo-400"
                : isOceanBreeze
                ? "text-teal-700"
                : "text-indigo-600"
            }`}
          >
            {points}
          </h3>
          <p
            className={`${
              isDarkMode
                ? "text-gray-400"
                : isOceanBreeze
                ? "text-blue-600"
                : "text-gray-500"
            }`}
          >
            Total Points Earned
          </p>
        </div>

        <div className="mt-8">
          <h3
            className={`font-medium mb-4 ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            Your Badges
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {availableBadges.map((badge) => (
              <div
                key={badge.name}
                className={`p-4 rounded-lg text-center ${
                  badges.includes(badge.name)
                    ? isDarkMode
                      ? "bg-indigo-900"
                      : isOceanBreeze
                      ? "bg-teal-100 bg-opacity-70 backdrop-blur-sm"
                      : "bg-indigo-50"
                    : isDarkMode
                    ? "bg-gray-600 opacity-50"
                    : isOceanBreeze
                    ? "bg-gray-200 opacity-50 backdrop-blur-sm"
                    : "bg-gray-100 opacity-50"
                }`}
              >
                <div className="flex justify-center">{badge.icon}</div>
                <h4
                  className={`font-medium mt-2 ${
                    isDarkMode
                      ? "text-gray-200"
                      : isOceanBreeze
                      ? "text-teal-800"
                      : "text-gray-800"
                  }`}
                >
                  {badge.name}
                </h4>
                <p
                  className={`text-sm ${
                    isDarkMode
                      ? "text-gray-400"
                      : isOceanBreeze
                      ? "text-blue-600"
                      : "text-gray-500"
                  }`}
                >
                  {badge.description}
                </p>
                <p
                  className={`text-sm font-medium mt-1 ${
                    badges.includes(badge.name)
                      ? "text-green-600"
                      : isDarkMode
                      ? "text-gray-500"
                      : isOceanBreeze
                      ? "text-blue-500"
                      : "text-gray-400"
                  }`}
                >
                  {badges.includes(badge.name) ? "Earned!" : "Not yet earned"}
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="mt-8">
          <h3
            className={`font-medium mb-4 ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            Study Streaks
          </h3>
          <div
            className={`p-4 rounded-lg text-center ${
              isDarkMode
                ? "bg-gray-600"
                : isOceanBreeze
                ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm"
                : "bg-gray-50"
            }`}
          >
            <Flame
              className={`inline-block w-8 h-8 mb-2 ${
                isDarkMode
                  ? "text-orange-400"
                  : isOceanBreeze
                  ? "text-orange-500"
                  : "text-orange-600"
              }`}
            />
            <h4
              className={`text-lg font-medium ${
                isDarkMode
                  ? "text-gray-200"
                  : isOceanBreeze
                  ? "text-teal-800"
                  : "text-gray-800"
              }`}
            >
              Current Streak: {studyStats.streak} Days
            </h4>
            <p
              className={`text-sm ${
                isDarkMode
                  ? "text-gray-400"
                  : isOceanBreeze
                  ? "text-blue-600"
                  : "text-gray-500"
              }`}
            >
              Keep studying daily to maintain your streak!
            </p>
          </div>
        </div>

        {/* New Section: Redeem Themes */}
        <div className="mt-8">
          <h3
            className={`font-medium mb-4 ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            Redeem Themes
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {unlockableThemes.map((theme) => (
              <div
                key={theme.name}
                className={`p-4 rounded-lg text-center ${
                  isDarkMode
                    ? "bg-gray-600"
                    : isOceanBreeze
                    ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm"
                    : "bg-gray-50"
                }`}
              >
                <h4
                  className={`font-medium mt-2 ${
                    isDarkMode
                      ? "text-gray-200"
                      : isOceanBreeze
                      ? "text-teal-800"
                      : "text-gray-800"
                  }`}
                >
                  {theme.name}
                </h4>
                <p
                  className={`text-sm ${
                    isDarkMode
                      ? "text-gray-400"
                      : isOceanBreeze
                      ? "text-blue-600"
                      : "text-gray-500"
                  }`}
                >
                  {theme.description}
                </p>
                <p
                  className={`text-sm font-medium mt-1 ${
                    isDarkMode
                      ? "text-gray-400"
                      : isOceanBreeze
                      ? "text-blue-600"
                      : "text-gray-500"
                  }`}
                >
                  Cost: {theme.cost} Points
                </p>
                <button
                  onClick={() => handleRedeemTheme(theme)}
                  className={`mt-2 px-4 py-2 rounded text-white text-sm ${
                    points >= theme.cost
                      ? isDarkMode
                        ? "bg-indigo-500 hover:bg-indigo-600"
                        : isOceanBreeze
                        ? "bg-teal-500 hover:bg-teal-600"
                        : "bg-indigo-600 hover:bg-indigo-700"
                      : "bg-gray-400 cursor-not-allowed"
                  }`}
                  disabled={points < theme.cost}
                >
                  Redeem
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Rewards;
