import React from "react";

function Analytics({ tasks, currentTheme }) {
  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";
  const isSunsetGlow = currentTheme === "Sunset Glow";
  const isForestWhisper = currentTheme === "Forest Whisper";

  const getTaskCompletionRate = () => {
    if (tasks.length === 0) return 0;
    const completedTasks = tasks.filter((task) => task.completed).length;
    return Math.round((completedTasks / tasks.length) * 100);
  };

  const completedTasksCount = tasks.filter((task) => task.completed).length;

  const today = new Date();
  const daysOfWeek = ["S", "M", "T", "W", "T", "F", "S"];
  const past7Days = Array(7)
    .fill(0)
    .map((_, index) => {
      const date = new Date(today);
      date.setDate(today.getDate() - (6 - index));
      return {
        date: date.toISOString().split("T")[0],
        day: daysOfWeek[date.getDay()],
      };
    });

  const completionData = past7Days.map((dayObj) => {
    return tasks.filter(
      (task) => task.completed && task.completedDate === dayObj.date
    ).length;
  });

  const maxCompletions = Math.max(...completionData, 1);
  const completionHeights = completionData.map(
    (count) => (count / maxCompletions) * 20
  );

  const priorityCounts = {
    High: tasks.filter((task) => task.priority === "High").length,
    Medium: tasks.filter((task) => task.priority === "Medium").length,
    Low: tasks.filter((task) => task.priority === "Low").length,
  };
  const totalTasks = tasks.length || 1;
  const priorityPercentages = {
    High: (priorityCounts.High / totalTasks) * 100,
    Medium: (priorityCounts.Medium / totalTasks) * 100,
    Low: (priorityCounts.Low / totalTasks) * 100,
  };

  const todayStr = today.toISOString().split("T")[0];
  const overdueTasks = tasks.filter(
    (task) => !task.completed && task.dueDate && task.dueDate < todayStr
  );

  const completedTasksByDay = past7Days.map((dayObj) => ({
    day: dayObj.day,
    date: dayObj.date,
    count: tasks.filter(
      (task) => task.completed && task.completedDate === dayObj.date
    ).length,
  }));
  const mostProductiveDay = completedTasksByDay.reduce(
    (max, day) => (day.count > max.count ? day : max),
    { day: "", date: "", count: 0 }
  );
  const mostProductiveInsight =
    mostProductiveDay.count > 0
      ? `You're most productive on ${mostProductiveDay.day}s, with ${
          mostProductiveDay.count
        } task${mostProductiveDay.count !== 1 ? "s" : ""} completed on ${
          mostProductiveDay.date
        }.`
      : "You're just getting startedâ€”complete your first task today to build your streak!";

  const overdueInsight =
    overdueTasks.length > 0
      ? `You have ${overdueTasks.length} overdue task${
          overdueTasks.length !== 1 ? "s" : ""
        }. Focus on completing these to stay on track!`
      : "Great job! You have no overdue tasks.";

  const incompleteHighPriorityTasks = tasks.filter(
    (task) => !task.completed && task.priority === "High"
  ).length;
  const priorityInsight =
    incompleteHighPriorityTasks > 0
      ? `You have ${incompleteHighPriorityTasks} incomplete high-priority task${
          incompleteHighPriorityTasks !== 1 ? "s" : ""
        }. Prioritize these to maximize your productivity.`
      : "No high-priority tasks pending. Keep up the good work!";

  return (
    <div
      className={`rounded-lg shadow ${
        isDarkMode
          ? "bg-gray-700"
          : isOceanBreeze
          ? "bg-opacity-70 backdrop-blur-lg"
          : isSunsetGlow
          ? "bg-opacity-70 backdrop-blur-lg"
          : isForestWhisper
          ? "bg-opacity-70 backdrop-blur-lg"
          : "bg-white"
      }`}
    >
      <div
        className={`p-4 border-b ${
          isDarkMode
            ? "border-gray-600"
            : isOceanBreeze
            ? "border-teal-300"
            : isSunsetGlow
            ? "border-orange-300"
            : isForestWhisper
            ? "border-green-300"
            : "border-gray-200"
        }`}
      >
        <h2
          className={`font-semibold ${
            isDarkMode
              ? "text-gray-200"
              : isOceanBreeze
              ? "text-teal-800"
              : isSunsetGlow
              ? "text-orange-800"
              : isForestWhisper
              ? "text-green-800"
              : "text-gray-800"
          }`}
        >
          Study Analytics
        </h2>
      </div>
      <div className="p-8 text-center">
        <div
          className={`inline-block p-12 rounded-full mb-4 ${
            isDarkMode
              ? "bg-gray-600"
              : isOceanBreeze
              ? "bg-teal-100 bg-opacity-70 backdrop-blur-sm"
              : isSunsetGlow
              ? "bg-pink-100 bg-opacity-70 backdrop-blur-sm"
              : isForestWhisper
              ? "bg-emerald-100 bg-opacity-70 backdrop-blur-sm"
              : "bg-gray-100"
          } shadow-sm`}
        >
          <div
            className={`text-4xl font-bold ${
              isDarkMode
                ? "text-indigo-400"
                : isOceanBreeze
                ? "text-teal-700"
                : isSunsetGlow
                ? "text-orange-600"
                : isForestWhisper
                ? "text-green-600"
                : "text-indigo-600"
            }`}
          >
            {getTaskCompletionRate()}%
          </div>
        </div>
        <h3
          className={`text-lg font-medium ${
            isDarkMode
              ? "text-gray-200"
              : isOceanBreeze
              ? "text-teal-800"
              : isSunsetGlow
              ? "text-orange-800"
              : isForestWhisper
              ? "text-green-800"
              : "text-gray-800"
          }`}
        >
          Task Completion Rate
        </h3>
        <p
          className={`mt-2 ${
            isDarkMode
              ? "text-gray-400"
              : isOceanBreeze
              ? "text-blue-600"
              : isSunsetGlow
              ? "text-orange-500"
              : isForestWhisper
              ? "text-green-500"
              : "text-gray-500"
          }`}
        >
          You've completed {completedTasksCount} out of {tasks.length} tasks
        </p>

        <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* Task Completion Timeline */}
          <div
            className={`p-4 rounded-lg ${
              isDarkMode
                ? "bg-gray-600"
                : isOceanBreeze
                ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm"
                : isSunsetGlow
                ? "bg-pink-100 bg-opacity-70 backdrop-blur-sm"
                : isForestWhisper
                ? "bg-emerald-100 bg-opacity-70 backdrop-blur-sm"
                : "bg-gray-50"
            } shadow-sm`}
          >
            <h4
              className={`font-medium mb-2 ${
                isDarkMode
                  ? "text-gray-200"
                  : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                  ? "text-orange-800"
                  : isForestWhisper
                  ? "text-green-800"
                  : "text-gray-800"
              }`}
            >
              Task Completion Timeline
            </h4>
            <div className="flex justify-center space-x-2 mt-4">
              {past7Days.map((dayObj, index) => (
                <div key={dayObj.date} className="flex flex-col items-center">
                  <div
                    className={`w-4 h-${Math.round(
                      completionHeights[index]
                    )} rounded-t-sm ${
                      isDarkMode
                        ? "bg-indigo-500"
                        : isOceanBreeze
                        ? "bg-teal-500"
                        : isSunsetGlow
                        ? "bg-orange-500"
                        : isForestWhisper
                        ? "bg-green-500"
                        : "bg-indigo-600"
                    }`}
                  ></div>
                  <span
                    className={`text-xs mt-1 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                        ? "text-blue-600"
                        : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-800"
                    }`}
                  >
                    {dayObj.day}
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Priority Breakdown */}
          <div
            className={`p-4 rounded-lg ${
              isDarkMode
                ? "bg-gray-600"
                : isOceanBreeze
                ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm"
                : isSunsetGlow
                ? "bg-pink-100 bg-opacity-70 backdrop-blur-sm"
                : isForestWhisper
                ? "bg-emerald-100 bg-opacity-70 backdrop-blur-sm"
                : "bg-gray-50"
            } shadow-sm`}
          >
            <h4
              className={`font-medium mb-2 ${
                isDarkMode
                  ? "text-gray-200"
                  : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                  ? "text-orange-800"
                  : isForestWhisper
                  ? "text-green-800"
                  : "text-gray-800"
              }`}
            >
              Priority Breakdown
            </h4>
            <div className="flex justify-center space-x-4 mt-4">
              {["High", "Medium", "Low"].map((priority, index) => (
                <div key={priority} className="flex flex-col items-center">
                  <div
                    className={`w-12 h-4 rounded ${
                      index === 0
                        ? "bg-red-500"
                        : index === 1
                        ? "bg-yellow-500"
                        : "bg-blue-500"
                    }`}
                    style={{ width: `${priorityPercentages[priority]}%` }}
                  ></div>
                  <span
                    className={`text-xs mt-1 ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                        ? "text-blue-600"
                        : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-800"
                    }`}
                  >
                    {priority}: {Math.round(priorityPercentages[priority])}%
                  </span>
                </div>
              ))}
            </div>
          </div>

          {/* Overdue Tasks Indicator */}
          <div
            className={`p-4 rounded-lg ${
              isDarkMode
                ? "bg-gray-600"
                : isOceanBreeze
                ? "bg-amber-100 bg-opacity-70 backdrop-blur-sm"
                : isSunsetGlow
                ? "bg-pink-100 bg-opacity-70 backdrop-blur-sm"
                : isForestWhisper
                ? "bg-emerald-100 bg-opacity-70 backdrop-blur-sm"
                : "bg-gray-50"
            } shadow-sm`}
          >
            <h4
              className={`font-medium mb-2 ${
                isDarkMode
                  ? "text-gray-200"
                  : isOceanBreeze
                  ? "text-teal-800"
                  : isSunsetGlow
                  ? "text-orange-800"
                  : isForestWhisper
                  ? "text-green-800"
                  : "text-gray-800"
              }`}
            >
              Overdue Tasks ({overdueTasks.length})
            </h4>
            {overdueTasks.length > 0 ? (
              <ul className="space-y-2">
                {overdueTasks.map((task) => (
                  <li
                    key={task.id}
                    className={`text-sm ${
                      isDarkMode
                        ? "text-gray-400"
                        : isOceanBreeze
                        ? "text-blue-600"
                        : isSunsetGlow
                        ? "text-orange-500"
                        : isForestWhisper
                        ? "text-green-500"
                        : "text-gray-500"
                    }`}
                  >
                    {task.title} (Due: {task.dueDate}, Priority: {task.priority}
                    )
                  </li>
                ))}
              </ul>
            ) : (
              <p
                className={`text-sm ${
                  isDarkMode
                    ? "text-gray-400"
                    : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                    ? "text-orange-500"
                    : isForestWhisper
                    ? "text-green-500"
                    : "text-gray-500"
                }`}
              >
                No overdue tasks! You're on track.
              </p>
            )}
          </div>
        </div>

        <div
          className={`mt-8 p-4 rounded-lg ${
            isDarkMode
              ? "bg-indigo-900"
              : isOceanBreeze
              ? "bg-teal-100 bg-opacity-70 backdrop-blur-sm"
              : isSunsetGlow
              ? "bg-pink-100 bg-opacity-70 backdrop-blur-sm"
              : isForestWhisper
              ? "bg-emerald-100 bg-opacity-70 backdrop-blur-sm"
              : "bg-indigo-50"
          } shadow-sm text-left`}
        >
          <h4
            className={`font-medium mb-2 ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : isSunsetGlow
                ? "text-orange-800"
                : isForestWhisper
                ? "text-green-800"
                : "text-gray-800"
            }`}
          >
            AI Insights
          </h4>
          <ul className="space-y-2 text-sm">
            <li className="flex items-start">
              <div className="w-4 h-4 rounded-full bg-green-500 mt-1 mr-2"></div>
              <p
                className={`${
                  isDarkMode
                    ? "text-gray-400"
                    : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                    ? "text-orange-500"
                    : isForestWhisper
                    ? "text-green-500"
                    : "text-gray-500"
                }`}
              >
                {mostProductiveInsight}
              </p>
            </li>
            <li className="flex items-start">
              <div className="w-4 h-4 rounded-full bg-yellow-500 mt-1 mr-2"></div>
              <p
                className={`${
                  isDarkMode
                    ? "text-gray-400"
                    : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                    ? "text-orange-500"
                    : isForestWhisper
                    ? "text-green-500"
                    : "text-gray-500"
                }`}
              >
                {overdueInsight}
              </p>
            </li>
            <li className="flex items-start">
              <div className="w-4 h-4 rounded-full bg-blue-500 mt-1 mr-2"></div>
              <p
                className={`${
                  isDarkMode
                    ? "text-gray-400"
                    : isOceanBreeze
                    ? "text-blue-600"
                    : isSunsetGlow
                    ? "text-orange-500"
                    : isForestWhisper
                    ? "text-green-500"
                    : "text-gray-500"
                }`}
              >
                {priorityInsight}
              </p>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Analytics;
