import React, { useRef } from "react";
import { PlusCircle } from "lucide-react";
import { toast } from "react-toastify";
import { getCardStyles } from "../utils/themeUtils";

function AddTaskForm({ addTask, currentTheme }) {
  const styles = getCardStyles(currentTheme);
  const [newTask, setNewTask] = React.useState({
    title: "",
    dueDate: "",
    priority: "Medium",
    completed: false,
    hours: "",
  });
  const [lastAttemptedDate, setLastAttemptedDate] = React.useState("");
  const [formatError, setFormatError] = React.useState(false);
  const [rawDateInput, setRawDateInput] = React.useState(""); // Reintroduce to capture raw input

  const titleInputRef = useRef(null);
  const dueDateInputRef = useRef(null);
  const prioritySelectRef = useRef(null);
  const hoursInputRef = useRef(null);
  const addButtonRef = useRef(null);

  const currentYear = new Date().getFullYear();

  const validateDate = (dateStr, inputValidity, attemptedDate, rawInput) => {
    // Check if the date is missing
    if (!dateStr) {
      if (inputValidity.valueMissing) {
        return { isValid: false, error: "Date is required." };
      }
      // If dateStr is empty but valueMissing is false, it’s likely an invalid date or format error
      if (formatError && rawInput) {
        // Parse the raw input (e.g., "31/06/2025")
        const parts = rawInput.split(/[-/]/); // Split on "-" or "/"
        let year, month, day;
        if (parts.length === 3) {
          // Check possible formats: DD/MM/YYYY or YYYY-MM-DD
          if (parts[0].length === 4) {
            // YYYY-MM-DD
            [year, month, day] = parts.map(Number);
          } else {
            // DD/MM/YYYY
            [day, month, year] = parts.map(Number);
          }
          const parsedDate = new Date(year, month - 1, day); // month is 0-based in JS
          const monthNames = [
            "January",
            "February",
            "March",
            "April",
            "May",
            "June",
            "July",
            "August",
            "September",
            "October",
            "November",
            "December",
          ];
          // Check if the date is invalid
          if (
            isNaN(parsedDate.getTime()) ||
            parsedDate.getFullYear() !== year ||
            parsedDate.getMonth() + 1 !== month ||
            parsedDate.getDate() !== day
          ) {
            return {
              isValid: false,
              error: `Please enter a valid date (e.g., YYYY-MM-DD). ${monthNames[month - 1]} ${day} is not a valid date.`,
            };
          }
          return {
            isValid: false,
            error:
              "Please enter the date in YYYY-MM-DD format (e.g., 2025-06-03).",
          };
        }
        return {
          isValid: false,
          error:
            "Please enter the date in YYYY-MM-DD format (e.g., 2025-06-03).",
        };
      }
      if (attemptedDate) {
        const [year, month, day] = attemptedDate.split("-").map(Number);
        const parsedDate = new Date(year, month - 1, day); // month is 0-based in JS
        const monthNames = [
          "January",
          "February",
          "March",
          "April",
          "May",
          "June",
          "July",
          "August",
          "September",
          "October",
          "November",
          "December",
        ];
        // Check if the date rolls over or is invalid
        if (
          isNaN(parsedDate.getTime()) ||
          parsedDate.getFullYear() !== year ||
          parsedDate.getMonth() + 1 !== month ||
          parsedDate.getDate() !== day
        ) {
          return {
            isValid: false,
            error: `Please enter a valid date (e.g., YYYY-MM-DD). ${monthNames[month - 1]} ${day} is not a valid date.`,
          };
        }
      }
      return {
        isValid: false,
        error: "Please enter a valid date (e.g., YYYY-MM-DD).",
      };
    }

    const date = new Date(dateStr);
    if (isNaN(date.getTime())) {
      return {
        isValid: false,
        error: "Please enter a valid date (e.g., YYYY-MM-DD).",
      };
    }

    return { isValid: true };
  };

  const handleAddTask = () => {
    // Validate required fields (excluding dueDate for now)
    if (!newTask.title || !newTask.hours) {
      toast.error("Please fill in all required fields (title and hours).");
      return;
    }

    // Validate due date using the input's validity state
    const inputValidity = dueDateInputRef.current.validity;
    const dateValidation = validateDate(
      newTask.dueDate,
      inputValidity,
      lastAttemptedDate,
      rawDateInput
    );
    if (!dateValidation.isValid) {
      toast.error(dateValidation.error);
      setFormatError(false);
      setRawDateInput(""); // Reset raw input after showing the message
      return;
    }

    const parsedHours = parseFloat(newTask.hours);
    if (isNaN(parsedHours) || parsedHours < 0) {
      toast.error("Please enter a valid number of hours (0 or greater).");
      return;
    }

    addTask({ ...newTask, hours: parsedHours });
    setNewTask({
      title: "",
      dueDate: "",
      priority: "Medium",
      completed: false,
      hours: "",
    });
    setLastAttemptedDate("");
    setFormatError(false);
    setRawDateInput("");
    titleInputRef.current.focus();
  };

  const validateAndCorrectYear = (dateValue) => {
    if (!dateValue) return;
    const [year, month, day] = dateValue.split("-");
    if (parseInt(year) !== currentYear) {
      const correctedDate = `${currentYear}-${month}-${day}`;
      setNewTask((prev) => ({ ...prev, dueDate: correctedDate }));
      setLastAttemptedDate(correctedDate);
      setRawDateInput(""); // Reset raw input on correction
    }
  };

  const handleAddTaskKeyPress = (e, nextRef) => {
    if (e.key === "Enter") {
      e.preventDefault();
      if (nextRef.current) {
        nextRef.current.focus();
      }
    }
  };

  return (
    <div className={styles.formSection}>
      <div className="flex flex-wrap md:flex-nowrap gap-3">
        <input
          type="text"
          placeholder="Task title"
          className={`flex-grow ${styles.input}`}
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          onKeyDown={(e) => handleAddTaskKeyPress(e, dueDateInputRef)}
          ref={titleInputRef}
        />
        <input
          type="date"
          className={styles.input}
          value={newTask.dueDate}
          onChange={(e) => {
            const newDate = e.target.value;
            setNewTask({ ...newTask, dueDate: newDate });
            setLastAttemptedDate(newDate);
            setFormatError(false);
            validateAndCorrectYear(newDate);
          }}
          onInput={(e) => {
            setRawDateInput(e.target.value); // Capture raw input as typed
          }}
          onInvalid={(e) => {
            e.preventDefault();
            if (e.target.validity.badInput) {
              setFormatError(true);
            }
          }}
          onKeyDown={(e) => handleAddTaskKeyPress(e, prioritySelectRef)}
          ref={dueDateInputRef}
        />
        <select
          className={styles.input}
          value={newTask.priority}
          onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
          onKeyDown={(e) => handleAddTaskKeyPress(e, hoursInputRef)}
          ref={prioritySelectRef}
        >
          <option value="Low">Low Priority</option>
          <option value="Medium">Medium Priority</option>
          <option value="High">High Priority</option>
        </select>
        <input
          type="number"
          placeholder="Hours"
          className={styles.input}
          value={newTask.hours}
          onChange={(e) => setNewTask({ ...newTask, hours: e.target.value })}
          onKeyDown={(e) => handleAddTaskKeyPress(e, addButtonRef)}
          ref={hoursInputRef}
          min="0"
          step="0.1"
        />
        <button
          className={`px-4 py-2 ${styles.buttonPrimary}`}
          onClick={handleAddTask}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              e.preventDefault();
              handleAddTask();
            }
          }}
          ref={addButtonRef}
        >
          <PlusCircle className="w-4 h-4 mr-1 inline" /> Add Task
        </button>
      </div>
    </div>
  );
}

export default AddTaskForm;
