import React, { createContext, useState, useEffect } from "react";
import { ThemeProvider } from "styled-components";
import Layout from "./Layout";

// Create a context for global state
export const AppContext = createContext();

// Define themes for global theming
const themes = {
  "Light Mode": { background: "white", text: "gray-800", border: "gray-200" },
  "Dark Mode": { background: "gray-700", text: "gray-200", border: "gray-600" },
  "Ocean Breeze": {
    background: "bg-opacity-70 backdrop-blur-lg",
    text: "teal-800",
    border: "teal-300",
  },
  "Sunset Glow": {
    background: "bg-opacity-70 backdrop-blur-lg",
    text: "orange-800",
    border: "orange-300",
  },
  "Forest Whisper": {
    background: "bg-opacity-70 backdrop-blur-lg",
    text: "green-800",
    border: "green-300",
  },
};

// Error Boundary Component
class ErrorBoundary extends React.Component {
  state = { hasError: false };

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="text-center p-4 text-red-600">
          Something went wrong. Please refresh the page.
        </div>
      );
    }
    return this.props.children;
  }
}

function StudyBuddyPlanner() {
  // Global state using context
  const [tasks, setTasks] = useState([]);
  const [studyStats, setStudyStats] = useState({
    totalHours: 0,
    completedTasks: 0,
    streak: 0,
    lastActiveDate: null,
    lastStreakUpdate: null,
    studyHoursLog: [],
  });
  const [points, setPoints] = useState(0);
  const [badges, setBadges] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("Light Mode");
  const [isLoading, setIsLoading] = useState(true);

  // Simulate initial data fetching
  useEffect(() => {
    setTimeout(() => setIsLoading(false), 1000); // Replace with actual data fetching if needed
  }, []);

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-pulse text-gray-600">Loading...</div>
      </div>
    );
  }

  return (
    <AppContext.Provider
      value={{
        tasks,
        setTasks,
        studyStats,
        setStudyStats,
        points,
        setPoints,
        badges,
        setBadges,
        currentTheme,
        setCurrentTheme,
      }}
    >
      <ThemeProvider theme={themes[currentTheme] || themes["Light Mode"]}>
        <ErrorBoundary>
          <Layout />
        </ErrorBoundary>
      </ThemeProvider>
    </AppContext.Provider>
  );
}

export default StudyBuddyPlanner;
