import React, { useState } from "react";
import {
  Calendar,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
  Save,
  X,
  ArrowUpDown,
  Filter,
} from "lucide-react";

function TaskManager({
  tasks,
  addTask,
  updateTask,
  deleteTask,
  toggleTaskComplete,
  currentTheme,
}) {
  const [newTask, setNewTask] = useState({
    title: "",
    dueDate: "",
    priority: "Medium",
    completed: false,
  });
  const [editingTask, setEditingTask] = useState(null);
  const [sortBy, setSortBy] = useState("dueDateAsc"); // Default sort: due date ascending
  const [filterBy, setFilterBy] = useState("all"); // Default filter: show all tasks
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(null); // Track task ID for deletion confirmation

  const isDarkMode = currentTheme === "Dark Mode";
  const isOceanBreeze = currentTheme === "Ocean Breeze";
  const isLightMode = currentTheme === "Light Mode";

  const today = new Date().toISOString().split("T")[0];

  const handleAddTask = () => {
    if (!newTask.title) return;
    addTask(newTask);
    setNewTask({
      title: "",
      dueDate: "",
      priority: "Medium",
      completed: false,
    });
  };

  const handleStartEdit = (task) => {
    setEditingTask({ ...task });
  };

  const handleSaveEdit = () => {
    if (!editingTask) return;
    updateTask(editingTask);
    setEditingTask(null);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const confirmDeleteTask = (id) => {
    setShowDeleteConfirm(id);
  };

  const handleDeleteTask = (id) => {
    deleteTask(id);
    setShowDeleteConfirm(null);
  };

  const cancelDeleteTask = () => {
    setShowDeleteConfirm(null);
  };

  // Sorting and Filtering Logic
  const priorityValue = { High: 3, Medium: 2, Low: 1 };

  const sortedAndFilteredTasks = [...tasks]
    .filter((task) => {
      if (filterBy === "completed") return task.completed;
      if (filterBy === "incomplete") return !task.completed;
      return true; // "all"
    })
    .sort((a, b) => {
      if (sortBy === "dueDateAsc") {
        return new Date(a.dueDate) - new Date(b.dueDate);
      } else if (sortBy === "dueDateDesc") {
        return new Date(b.dueDate) - new Date(a.dueDate);
      } else if (sortBy === "priorityAsc") {
        return priorityValue[a.priority] - priorityValue[b.priority];
      } else if (sortBy === "priorityDesc") {
        return priorityValue[b.priority] - priorityValue[a.priority];
      }
      return 0;
    });

  return (
    <div
      className={`rounded-lg shadow ${
        isDarkMode
          ? "bg-gray-700"
          : isOceanBreeze
          ? "bg-opacity-70 backdrop-blur-lg"
          : "bg-white"
      }`}
    >
      <div
        className={`p-4 border-b ${
          isDarkMode
            ? "border-gray-600"
            : isOceanBreeze
            ? "border-teal-300"
            : "border-gray-200"
        }`}
      >
        <h2
          className={`font-semibold ${
            isDarkMode
              ? "text-gray-200"
              : isOceanBreeze
              ? "text-teal-800"
              : "text-gray-800"
          }`}
        >
          Manage Tasks
        </h2>
      </div>

      {/* Add Task Form */}
      <div
        className={`p-4 border-b ${
          isDarkMode
            ? "border-gray-600 bg-gray-600"
            : isOceanBreeze
            ? "border-teal-300 bg-amber-100 bg-opacity-70 backdrop-blur-sm"
            : "border-gray-200 bg-gray-50"
        }`}
      >
        <div className="flex flex-wrap md:flex-nowrap gap-3">
          <input
            type="text"
            placeholder="Task title"
            className={`flex-grow p-2 border rounded ${
              isDarkMode
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : isOceanBreeze
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : "bg-white border-gray-300 text-gray-800"
            }`}
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          />
          <input
            type="date"
            className={`p-2 border rounded ${
              isDarkMode
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : isOceanBreeze
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : "bg-white border-gray-300 text-gray-800"
            }`}
            value={newTask.dueDate}
            onChange={(e) =>
              setNewTask({ ...newTask, dueDate: e.target.value })
            }
          />
          <select
            className={`p-2 border rounded ${
              isDarkMode
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : isOceanBreeze
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : "bg-white border-gray-300 text-gray-800"
            }`}
            value={newTask.priority}
            onChange={(e) =>
              setNewTask({ ...newTask, priority: e.target.value })
            }
          >
            <option value="Low">Low Priority</option>
            <option value="Medium">Medium Priority</option>
            <option value="High">High Priority</option>
          </select>
          <button
            className={`p-2 rounded flex items-center ${
              isDarkMode
                ? "bg-indigo-500 text-white"
                : isOceanBreeze
                ? "bg-teal-500 text-white"
                : "bg-indigo-600 text-white"
            }`}
            onClick={handleAddTask}
          >
            <PlusCircle className="w-4 h-4 mr-1" /> Add Task
          </button>
        </div>
      </div>

      {/* Sort and Filter Controls */}
      <div
        className={`p-4 flex flex-wrap gap-3 border-b ${
          isDarkMode
            ? "border-gray-600 bg-gray-600"
            : isOceanBreeze
            ? "border-teal-300 bg-amber-100 bg-opacity-70 backdrop-blur-sm"
            : "border-gray-200 bg-gray-50"
        }`}
      >
        <div className="flex items-center space-x-2">
          <ArrowUpDown className="w-4 h-4" />
          <label
            className={`text-sm ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            Sort by:
          </label>
          <select
            className={`p-2 border rounded ${
              isDarkMode
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : isOceanBreeze
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : "bg-white border-gray-300 text-gray-800"
            }`}
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="dueDateAsc">Due Date (Earliest First)</option>
            <option value="dueDateDesc">Due Date (Latest First)</option>
            <option value="priorityAsc">Priority (Low to High)</option>
            <option value="priorityDesc">Priority (High to Low)</option>
          </select>
        </div>
        <div className="flex items-center space-x-2">
          <Filter className="w-4 h-4" />
          <label
            className={`text-sm ${
              isDarkMode
                ? "text-gray-200"
                : isOceanBreeze
                ? "text-teal-800"
                : "text-gray-800"
            }`}
          >
            Filter:
          </label>
          <select
            className={`p-2 border rounded ${
              isDarkMode
                ? "bg-gray-800 border-gray-500 text-gray-200"
                : isOceanBreeze
                ? "bg-blue-100 border-teal-300 text-teal-800"
                : "bg-white border-gray-300 text-gray-800"
            }`}
            value={filterBy}
            onChange={(e) => setFilterBy(e.target.value)}
          >
            <option value="all">All Tasks</option>
            <option value="completed">Completed Tasks</option>
            <option value="incomplete">Incomplete Tasks</option>
          </select>
        </div>
      </div>

      {/* Task List */}
      <div
        className={`divide-y ${
          isDarkMode
            ? "divide-gray-600"
            : isOceanBreeze
            ? "divide-teal-300"
            : "divide-gray-200"
        }`}
      >
        {sortedAndFilteredTasks.map((task) => {
          const isOverdue = !task.completed && task.dueDate < today;
          const isDueToday = !task.completed && task.dueDate === today;
          const isFuture = !task.completed && task.dueDate > today;

          return (
            <div
              key={task.id}
              className={`p-4 ${
                isDarkMode
                  ? "hover:bg-gray-600"
                  : isOceanBreeze
                  ? "hover:bg-teal-100"
                  : "hover:bg-gray-50"
              } ${
                isOverdue
                  ? "bg-red-50 bg-opacity-50"
                  : isDueToday
                  ? "bg-orange-50 bg-opacity-50"
                  : isFuture
                  ? "bg-green-50 bg-opacity-50"
                  : ""
              }`}
            >
              {editingTask && editingTask.id === task.id ? (
                <div className="flex flex-wrap gap-2">
                  <input
                    type="text"
                    className={`flex-grow p-2 border rounded ${
                      isDarkMode
                        ? "bg-gray-800 border-gray-500 text-gray-200"
                        : isOceanBreeze
                        ? "bg-blue-100 border-teal-300 text-teal-800"
                        : "bg-white border-gray-300 text-gray-800"
                    }`}
                    value={editingTask.title}
                    onChange={(e) =>
                      setEditingTask({ ...editingTask, title: e.target.value })
                    }
                  />
                  <input
                    type="date"
                    className={`p-2 border rounded ${
                      isDarkMode
                        ? "bg-gray-800 border-gray-500 text-gray-200"
                        : isOceanBreeze
                        ? "bg-blue-100 border-teal-300 text-teal-800"
                        : "bg-white border-gray-300 text-gray-800"
                    }`}
                    value={editingTask.dueDate}
                    onChange={(e) =>
                      setEditingTask({
                        ...editingTask,
                        dueDate: e.target.value,
                      })
                    }
                  />
                  <select
                    className={`p-2 border rounded ${
                      isDarkMode
                        ? "bg-gray-800 border-gray-500 text-gray-200"
                        : isOceanBreeze
                        ? "bg-blue-100 border-teal-300 text-teal-800"
                        : "bg-white border-gray-300 text-gray-800"
                    }`}
                    value={editingTask.priority}
                    onChange={(e) =>
                      setEditingTask({
                        ...editingTask,
                        priority: e.target.value,
                      })
                    }
                  >
                    <option value="Low">Low Priority</option>
                    <option value="Medium">Medium Priority</option>
                    <option value="High">High Priority</option>
                  </select>
                  <div className="flex space-x-2">
                    <button
                      onClick={handleSaveEdit}
                      className={`p-2 rounded flex items-center ${
                        isDarkMode
                          ? "bg-green-500 text-white"
                          : isOceanBreeze
                          ? "bg-teal-500 text-white"
                          : "bg-green-600 text-white"
                      }`}
                    >
                      <Save className="w-4 h-4 mr-1" /> Save
                    </button>
                    <button
                      onClick={handleCancelEdit}
                      className={`p-2 rounded flex items-center ${
                        isDarkMode
                          ? "bg-gray-500 text-gray-200"
                          : isOceanBreeze
                          ? "bg-blue-200 text-blue-700"
                          : "bg-gray-300 text-gray-700"
                      }`}
                    >
                      <X className="w-4 h-4 mr-1" /> Cancel
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex justify-between items-center">
                  <div className="flex items-center">
                    <button
                      onClick={() => toggleTaskComplete(task.id)}
                      className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center
                      ${
                        task.completed
                          ? isDarkMode
                            ? "bg-green-900 border-green-600"
                            : isOceanBreeze
                            ? "bg-teal-100 border-teal-500"
                            : "bg-green-50 border-green-500"
                          : task.priority === "High"
                          ? "border-red-500"
                          : task.priority === "Medium"
                          ? "border-yellow-500"
                          : "border-blue-400"
                      }`}
                    >
                      {task.completed && (
                        <CheckCircle className="w-4 h-4 text-green-600" />
                      )}
                    </button>
                    <div
                      className={
                        task.completed ? "line-through text-gray-400" : ""
                      }
                    >
                      <h3
                        className={`font-medium ${
                          isDarkMode
                            ? "text-gray-200"
                            : isOceanBreeze
                            ? "text-teal-800"
                            : "text-gray-800"
                        } ${
                          isOverdue
                            ? "text-red-600"
                            : isDueToday
                            ? "text-orange-600"
                            : isFuture
                            ? "text-green-600"
                            : ""
                        }`}
                      >
                        {task.title}
                        {isOverdue && " (Overdue)"}
                        {isDueToday && " (Due Today)"}
                        {isFuture && " (Future)"}
                      </h3>
                      <div
                        className={`flex items-center text-sm ${
                          !task.completed && isDarkMode
                            ? "text-white" // Changed to white for better contrast in Dark Mode for incomplete tasks
                            : isDarkMode
                            ? "text-gray-400"
                            : isOceanBreeze
                            ? "text-blue-600"
                            : "text-gray-500"
                        } mt-1`}
                      >
                        <Calendar className="w-4 h-4 mr-1" /> Due:{" "}
                        {task.dueDate}
                        <span
                          className={`ml-3 px-2 py-1 rounded-full text-xs
                          ${
                            task.priority === "High"
                              ? "bg-red-100 text-red-800"
                              : task.priority === "Medium"
                              ? "bg-yellow-100 text-yellow-800"
                              : "bg-blue-100 text-blue-800"
                          }`}
                        >
                          {task.priority}
                        </span>
                      </div>
                    </div>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleStartEdit(task)}
                      className={`${
                        isDarkMode
                          ? "text-gray-500 hover:text-gray-300"
                          : isOceanBreeze
                          ? "text-blue-600 hover:text-teal-600"
                          : "text-gray-400 hover:text-gray-600"
                      }`}
                    >
                      <Edit3 className="w-5 h-5" />
                    </button>
                    <button
                      onClick={() => confirmDeleteTask(task.id)}
                      className={`${
                        isDarkMode
                          ? "text-gray-500 hover:text-gray-300"
                          : isOceanBreeze
                          ? "text-blue-600 hover:text-teal-600"
                          : "text-gray-400 hover:text-gray-600"
                      }`}
                    >
                      <Trash2 className="w-5 h-5" />
                    </button>
                  </div>
                </div>
              )}
              {/* Delete Confirmation Dialog */}
              {showDeleteConfirm === task.id && (
                <div
                  className={`mt-2 p-2 rounded-lg ${
                    isDarkMode
                      ? "bg-gray-800"
                      : isOceanBreeze
                      ? "bg-teal-100 bg-opacity-70"
                      : "bg-gray-100"
                  }`}
                >
                  <p
                    className={`text-sm ${
                      isDarkMode
                        ? "text-gray-200"
                        : isOceanBreeze
                        ? "text-teal-800"
                        : "text-gray-800"
                    }`}
                  >
                    Are you sure you want to delete "{task.title}"?
                  </p>
                  <div className="flex space-x-2 mt-2">
                    <button
                      onClick={() => handleDeleteTask(task.id)}
                      className={`p-2 rounded flex items-center ${
                        isDarkMode
                          ? "bg-red-500 text-white"
                          : isOceanBreeze
                          ? "bg-red-500 text-white"
                          : "bg-red-600 text-white"
                      }`}
                    >
                      Yes, Delete
                    </button>
                    <button
                      onClick={cancelDeleteTask}
                      className={`p-2 rounded flex items-center ${
                        isDarkMode
                          ? "bg-gray-500 text-gray-200"
                          : isOceanBreeze
                          ? "bg-blue-200 text-blue-700"
                          : "bg-gray-300 text-gray-700"
                      }`}
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              )}
            </div>
          );
        })}
        {sortedAndFilteredTasks.length === 0 && (
          <div
            className={`py-12 text-center ${
              isDarkMode
                ? "text-gray-400"
                : isOceanBreeze
                ? "text-blue-600"
                : "text-gray-500"
            }`}
          >
            {filterBy === "completed"
              ? "No completed tasks yet."
              : filterBy === "incomplete"
              ? "No incomplete tasks yet."
              : "No tasks yet. Add your first task above!"}
          </div>
        )}
      </div>
    </div>
  );
}

export default TaskManager;
