import React, { useState } from "react";
import {
  Calendar,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
  Save,
  X,
  ArrowUpDown,
  Filter,
} from "lucide-react";

function TaskManager({
  tasks,
  addTask,
  updateTask,
  deleteTask,
  toggleTaskComplete,
  themeConfig,
}) {
  const [newTask, setNewTask] = useState({
    title: "",
    dueDate: "",
    priority: "Medium",
    subject: "",
    completed: false,
  });
  const [editingTask, setEditingTask] = useState(null);
  const [sortBy, setSortBy] = useState("dueDateAsc");
  const [filterBy, setFilterBy] = useState("all");
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(null);

  const today = new Date().toISOString().split("T")[0];

  const handleAddTask = () => {
    if (!newTask.title) return;
    addTask(newTask);
    setNewTask({
      title: "",
      dueDate: "",
      priority: "Medium",
      subject: "",
      completed: false,
    });
  };

  const handleStartEdit = (task) => {
    setEditingTask({ ...task });
  };

  const handleSaveEdit = () => {
    if (!editingTask) return;
    updateTask(editingTask);
    setEditingTask(null);
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
  };

  const confirmDeleteTask = (id) => {
    setShowDeleteConfirm(id);
  };

  const handleDeleteTask = (id) => {
    deleteTask(id);
    setShowDeleteConfirm(null);
  };

  const cancelDeleteTask = () => {
    setShowDeleteConfirm(null);
  };

  const priorityValue = { High: 3, Medium: 2, Low: 1 };

  const sortedAndFilteredTasks = [...tasks]
    .filter((task) => {
      if (filterBy === "completed") return task.completed;
      if (filterBy === "incomplete") return !task.completed;
      return true;
    })
    .sort((a, b) => {
      if (sortBy === "dueDateAsc") {
        return new Date(a.dueDate) - new Date(b.dueDate);
      } else if (sortBy === "dueDateDesc") {
        return new Date(b.dueDate) - new Date(a.dueDate);
      } else if (sortBy === "priorityAsc") {
        return priorityValue[a.priority] - priorityValue[b.priority];
      } else if (sortBy === "priorityDesc") {
        return priorityValue[b.priority] - priorityValue[a.priority];
      }
      return 0;
    });

  return (
    <div className={`rounded-lg shadow ${themeConfig.background}`}>
      <div className={`p-4 border-b ${themeConfig.border}`}>
        <h2 className={`font-semibold ${themeConfig.text}`}>Manage Tasks</h2>
      </div>

      <div
        className={`p-4 border-b ${themeConfig.border} ${themeConfig.secondaryBg}`}
      >
        <div className="flex flex-wrap md:flex-nowrap gap-3">
          <input
            type="text"
            placeholder="Task title"
            className={`flex-grow p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          />
          <input
            type="text"
            placeholder="Subject"
            className={`p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
            value={newTask.subject}
            onChange={(e) =>
              setNewTask({ ...newTask, subject: e.target.value })
            }
          />
          <input
            type="date"
            className={`p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
            value={newTask.dueDate}
            onChange={(e) =>
              setNewTask({ ...newTask, dueDate: e.target.value })
            }
          />
          <select
            className={`p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
            value={newTask.priority}
            onChange={(e) =>
              setNewTask({ ...newTask, priority: e.target.value })
            }
          >
            <option value="Low">Low Priority</option>
            <option value="Medium">Medium Priority</option>
            <option value="High">High Priority</option>
          </select>
          <button
            className={`p-2 rounded flex items-center ${themeConfig.primary} ${themeConfig.primaryText} ${themeConfig.primaryHover} transition-colors duration-200`}
            onClick={handleAddTask}
          >
            <PlusCircle className="w-4 h-4 mr-1" /> Add Task
          </button>
        </div>
      </div>

      <div
        className={`p-4 flex flex-wrap gap-3 border-b ${themeConfig.border} ${themeConfig.secondaryBg}`}
      >
        <div className="flex items-center space-x-2">
          <ArrowUpDown className={`w-4 h-4 ${themeConfig.secondaryText}`} />
          <label className={`text-sm ${themeConfig.text}`}>Sort by:</label>
          <select
            className={`p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="dueDateAsc">Due Date (Earliest First)</option>
            <option value="dueDateDesc">Due Date (Latest First)</option>
            <option value="priorityAsc">Priority (Low to High)</option>
            <option value="priorityDesc">Priority (High to Low)</option>
          </select>
        </div>
        <div className="flex items-center space-x-2">
          <Filter className={`w-4 h-4 ${themeConfig.secondaryText}`} />
          <label className={`text-sm ${themeConfig.text}`}>Filter:</label>
          <select
            className={`p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
            value={filterBy}
            onChange={(e) => setFilterBy(e.target.value)}
          >
            <option value="all">All Tasks</option>
            <option value="completed">Completed Tasks</option>
            <option value="incomplete">Incomplete Tasks</option>
          </select>
        </div>
      </div>

      <div className={`divide-y ${themeConfig.border}`}>
        {sortedAndFilteredTasks.map((task) => {
          const isOverdue = !task.completed && task.dueDate < today;
          const isDueToday = !task.completed && task.dueDate === today;
          const isFuture = !task.completed && task.dueDate > today;

          return (
            <div
              key={task.id}
              className={`p-4 ${themeConfig.hoverBg} ${
                isOverdue
                  ? "bg-red-50 bg-opacity-50"
                  : isDueToday
                  ? "bg-orange-50 bg-opacity-50"
                  : isFuture
                  ? "bg-green-50 bg-opacity-50"
                  : ""
              } transition-colors duration-200`}
            >
              {editingTask && editingTask.id === task.id ? (
                <div className="flex flex-wrap gap-2">
                  <input
                    type="text"
                    className={`flex-grow p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
                    value={editingTask.title}
                    onChange={(e) =>
                      setEditingTask({ ...editingTask, title: e.target.value })
                    }
                  />
                  <input
                    type="text"
                    placeholder="Subject"
                    className={`p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
                    value={editingTask.subject}
                    onChange={(e) =>
                      setEditingTask({
                        ...editingTask,
                        subject: e.target.value,
                      })
                    }
                  />
                  <input
                    type="date"
                    className={`p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
                    value={editingTask.dueDate}
                    onChange={(e) =>
                      setEditingTask({
                        ...editingTask,
                        dueDate: e.target.value,
                      })
                    }
                  />
                  <select
                    className={`p-2 border rounded ${themeConfig.secondaryBg} ${themeConfig.border} ${themeConfig.text} ${themeConfig.focusRing} transition-all duration-200`}
                    value={editingTask.priority}
                    onChange={(e) =>
                      setEditingTask({
                        ...editingTask,
                        priority: e.target.value,
                      })
                    }
                  >
                    <option value="Low">Low Priority</option>
                    <option value="Medium">Medium Priority</option>
                    <option value="High">High Priority</option>
                  </select>
                  <div className="flex space-x-2">
                    <button
                      onClick={handleSaveEdit}
                      className={`p-2 rounded flex items-center bg-green-600 text-white hover:bg-green-700 transition-colors duration-200`}
                    >
                      <Save className="w-4 h-4 mr-1" /> Save
                    </button>
                    <button
                      onClick={handleCancelEdit}
                      className={`p-2 rounded flex items-center ${themeConfig.secondaryBg} ${themeConfig.text} ${themeConfig.hoverBg} transition-colors duration-200`}
                    >
                      <X className="w-4 h-4 mr-1" /> Cancel
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex justify-between items-center">
                  <div className="flex items-center">
                    <button
                      onClick={() => toggleTaskComplete(task.id)}
                      className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center ${
                        task.completed
                          ? themeConfig.completedBg
                          : task.priority === "High"
                          ? "border-red-500"
                          : task.priority === "Medium"
                          ? "border-yellow-500"
                          : "border-blue-400"
                      } hover:shadow-sm transition-shadow duration-200`}
                    >
                      {task.completed && (
                        <CheckCircle className="w-4 h-4 text-green-600" />
                      )}
                    </button>
                    <div
                      className={
                        task.completed ? "line-through text-gray-400" : ""
                      }
                    >
                      <h3
                        className={`font-medium ${themeConfig.text} ${
                          isOverdue
                            ? themeConfig.overdueColor
                            : isDueToday
                            ? themeConfig.dueTodayColor
                            : isFuture
                            ? themeConfig.futureColor
                            : ""
                        }`}
                      >
                        {task.title}
                        {isOverdue && " (Overdue)"}
                        {isDueToday && " (Due Today)"}
                        {isFuture && " (Future)"}
                      </h3>
                      <div
                        className={`flex items-center text-sm ${themeConfig.secondaryText} mt-1`}
                      >
                        <Calendar
                          className={`w-4 h-4 mr-1 ${themeConfig.secondaryText}`}
                        />{" "}
                        Due: {task.dueDate}
                        {task.subject && (
                          <span className="ml-3">Subject: {task.subject}</span>
                        )}
                        <span
                          className={`ml-3 px-2 py-1 rounded-full text-xs ${
                            task.priority === "High"
                              ? "bg-red-100 text-red-800"
                              : task.priority === "Medium"
                              ? "bg-yellow-100 text-yellow-800"
                              : "bg-blue-100 text-blue-800"
                          }`}
                        >
                          {task.priority}
                        </span>
                      </div>
                    </div>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleStartEdit(task)}
                      className={`${themeConfig.secondaryText} hover:text-gray-600 transition-colors duration-200`}
                    >
                      <Edit3 className="w-5 h-5" />
                    </button>
                    <button
                      onClick={() => confirmDeleteTask(task.id)}
                      className={`${themeConfig.secondaryText} hover:text-gray-600 transition-colors duration-200`}
                    >
                      <Trash2 className="w-5 h-5" />
                    </button>
                  </div>
                </div>
              )}
              {showDeleteConfirm === task.id && (
                <div
                  className={`mt-2 p-2 rounded-lg ${themeConfig.secondaryBg}`}
                >
                  <p className={`text-sm ${themeConfig.text}`}>
                    Are you sure you want to delete "{task.title}"?
                  </p>
                  <div className="flex space-x-2 mt-2">
                    <button
                      onClick={() => handleDeleteTask(task.id)}
                      className={`p-2 rounded flex items-center bg-red-600 text-white hover:bg-red-700 transition-colors duration-200`}
                    >
                      Yes, Delete
                    </button>
                    <button
                      onClick={cancelDeleteTask}
                      className={`p-2 rounded flex items-center ${themeConfig.secondaryBg} ${themeConfig.text} ${themeConfig.hoverBg} transition-colors duration-200`}
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              )}
            </div>
          );
        })}
        {sortedAndFilteredTasks.length === 0 && (
          <div className={`py-12 text-center ${themeConfig.secondaryText}`}>
            {filterBy === "completed"
              ? "No completed tasks yet."
              : filterBy === "incomplete"
              ? "No incomplete tasks yet."
              : "No tasks yet. Add your first task above!"}
          </div>
        )}
      </div>
    </div>
  );
}

export default TaskManager;
