import React, { useState, useEffect } from "react";
import { BookOpen } from "lucide-react";
import Dashboard from "./Dashboard";
import TaskManager from "./TaskManager";
import Analytics from "./Analytics";
import Rewards from "./Rewards";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Layout() {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [tasks, setTasks] = useState([]);
  const [studyStats, setStudyStats] = useState({
    totalHours: 0,
    completedTasks: 0,
    streak: 0,
    lastActiveDate: null,
    lastStreakUpdate: null, // New field to track when streak was last updated
  });
  const [aiSuggestion, setAiSuggestion] = useState("");
  const [points, setPoints] = useState(0);
  const [badges, setBadges] = useState([]);
  const [themes, setThemes] = useState(["Light Mode"]);
  const [currentTheme, setCurrentTheme] = useState("Light Mode");

  const userId = "user123"; // Hardcoded for now; replace with real user ID from auth

  const themeStyles = {
    "Light Mode": "bg-slate-50 text-gray-800",
    "Dark Mode": "bg-gray-800 text-gray-200",
    "Ocean Breeze": "bg-gradient-to-br from-blue-100 to-teal-200 text-blue-900",
  };

  const headerStyles = {
    "Light Mode": "bg-indigo-600 text-white",
    "Dark Mode": "bg-indigo-900 text-gray-200",
    "Ocean Breeze": "bg-blue-600 text-white",
  };

  const navStyles = {
    "Light Mode": "bg-white text-gray-500",
    "Dark Mode": "bg-gray-700 text-gray-300",
    "Ocean Breeze": "bg-blue-200 text-blue-700",
  };

  // Fetch user data from backend on mount
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/user/${userId}`
        );
        const data = await response.json();

        setTasks(data.tasks || []);
        setStudyStats(
          data.studyStats || {
            totalHours: 0,
            completedTasks: 0,
            streak: 0,
            lastActiveDate: null,
            lastStreakUpdate: null,
          }
        );
        setPoints(data.points || 0);
        setBadges(data.badges || []);
        setThemes(data.themes || ["Light Mode"]);
        setCurrentTheme(data.currentTheme || "Light Mode");

        setTimeout(() => {
          setAiSuggestion(
            "Based on your schedule and learning patterns, I recommend focusing on your Math Assignment today."
          );
        }, 1000);
      } catch (err) {
        console.error("Error fetching user data:", err);
        toast.error("Failed to load user data. Using defaults.");
        setTasks([
          {
            id: 1,
            title: "Complete Math Assignment",
            dueDate: "2025-04-25",
            priority: "High",
            completed: false,
          },
          {
            id: 2,
            title: "Review Chemistry Notes",
            dueDate: "2025-04-23",
            priority: "Medium",
            completed: true,
          },
          {
            id: 3,
            title: "Research Paper Outline",
            dueDate: "2025-04-30",
            priority: "High",
            completed: false,
          },
        ]);
      }
    };

    fetchUserData();
  }, []);

  // Update streak on app load (once per day)
  useEffect(() => {
    const today = new Date().toISOString().split("T")[0];
    const { lastActiveDate, streak, lastStreakUpdate } = studyStats;

    // Check if we've already updated the streak today
    if (lastStreakUpdate === today) return;

    // If no last active date, initialize it
    if (!lastActiveDate) {
      setStudyStats((prev) => ({
        ...prev,
        lastActiveDate: today,
        lastStreakUpdate: today,
      }));
      return;
    }

    const lastDate = new Date(lastActiveDate);
    const currentDate = new Date(today);
    const diffDays = (currentDate - lastDate) / (1000 * 60 * 60 * 24);

    if (diffDays === 1) {
      setStudyStats((prev) => ({
        ...prev,
        streak: prev.streak + 1,
        lastActiveDate: today,
        lastStreakUpdate: today,
      }));
    } else if (diffDays > 1) {
      setStudyStats((prev) => ({
        ...prev,
        streak: 0,
        lastActiveDate: today,
        lastStreakUpdate: today,
      }));
    } else {
      // Same day, just update lastStreakUpdate to prevent multiple updates
      setStudyStats((prev) => ({
        ...prev,
        lastStreakUpdate: today,
      }));
    }
  }, []); // Empty dependency array to run only on mount

  // Update completedTasks when tasks change
  useEffect(() => {
    const completedCount = tasks.filter((task) => task.completed).length;
    setStudyStats((prevStats) => {
      const newStats = { ...prevStats, completedTasks: completedCount };
      return newStats;
    });
  }, [tasks]);

  // Debounced save function
  useEffect(() => {
    const saveUserData = async () => {
      try {
        const response = await fetch(
          `http://localhost:5000/api/user/${userId}`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              tasks,
              studyStats,
              points,
              badges,
              themes,
              currentTheme,
            }),
          }
        );
        if (!response.ok) {
          throw new Error("Failed to save user data");
        }
      } catch (err) {
        console.error("Error saving user data:", err);
        toast.error("Failed to save user data.");
      }
    };

    const debounceSave = setTimeout(() => {
      saveUserData();
    }, 1000);

    return () => clearTimeout(debounceSave);
  }, [tasks, studyStats, points, badges, themes, currentTheme]);

  // Function to log study hours
  const logStudyHours = (hours) => {
    if (hours <= 0) {
      toast.error("Please enter a valid number of hours.");
      return;
    }
    setStudyStats((prev) => ({
      ...prev,
      totalHours: prev.totalHours + hours,
    }));
    toast.success(`Logged ${hours} study hours!`);
  };

  const getStudyTips = () => {
    setAiSuggestion("Loading new suggestions...");
    setTimeout(() => {
      const tips = [
        "Try using the Pomodoro technique: 25 minutes of focused study followed by a 5-minute break.",
        "For difficult subjects, try teaching the material to an imaginary student.",
        "Create visual mind maps to connect key concepts in your subjects.",
        "Review your notes within 24 hours of taking them to improve retention.",
        "Study in short, frequent sessions rather than long cramming sessions.",
      ];
      const randomTip = tips[Math.floor(Math.random() * tips.length)];
      setAiSuggestion(randomTip);
    }, 1500);
  };

  const generateSchedule = () => {
    setAiSuggestion("Generating personalized study schedule...");
    setTimeout(() => {
      const schedule =
        "Based on your upcoming tasks and past performance, here's a suggested schedule:\n\n" +
        "- Tuesday (Today): Math Assignment (2 hours) in the afternoon\n" +
        "- Wednesday: Chemistry Notes review (1 hour) in the morning\n" +
        "- Thursday: Start Research Paper Outline (1.5 hours)\n" +
        "- Friday: Complete remaining tasks and review progress";
      setAiSuggestion(schedule);
    }, 2000);
  };

  const addTask = (newTask) => {
    const task = { id: Date.now(), ...newTask };
    setTasks([...tasks, task]);
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const toggleTaskComplete = (id) => {
    const task = tasks.find((task) => task.id === id);
    let newPoints = points;
    let newBadges = [...badges];
    let notifications = [];

    if (task && !task.completed) {
      let pointsToAdd =
        task.priority === "High" ? 30 : task.priority === "Medium" ? 20 : 10;
      newPoints = points + pointsToAdd;
      notifications.push(`ðŸŽ‰ +${pointsToAdd} Points! Keep it up!`);

      const highPriorityTasks = tasks.filter(
        (t) => t.priority === "High" && t.completed
      ).length;
      if (highPriorityTasks + 1 >= 5 && !badges.includes("Priority Master")) {
        newBadges.push("Priority Master");
        notifications.push("ðŸ† Badge Earned: Priority Master!");
      }

      const totalCompletedTasks = tasks.filter((t) => t.completed).length;
      if (totalCompletedTasks + 1 >= 10 && !badges.includes("Task Titan")) {
        newBadges.push("Task Titan");
        notifications.push("ðŸ† Badge Earned: Task Titan!");
      }

      const dueDate = new Date(task.dueDate);
      const today = new Date();
      if (dueDate > today && !badges.includes("Early Bird")) {
        const earlyTasks = tasks.filter(
          (t) => new Date(t.dueDate) > today && t.completed
        ).length;
        if (earlyTasks + 1 >= 3) {
          newBadges.push("Early Bird");
          notifications.push("ðŸ† Badge Earned: Early Bird!");
        }
      }

      setPoints(newPoints);
      setBadges(newBadges);
      notifications.forEach((msg) => toast.success(msg));
    }

    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const updateTask = (updatedTask) => {
    setTasks(
      tasks.map((task) => (task.id === updatedTask.id ? updatedTask : task))
    );
  };

  useEffect(() => {
    if (studyStats.streak >= 7 && !badges.includes("Streak Star")) {
      let newBadges = [...badges, "Streak Star"];
      let newPoints = points + 50;

      setBadges(newBadges);
      setPoints(newPoints);

      toast.success("ðŸ† Badge Earned: Streak Star!");
      toast.success("ðŸŽ‰ +50 Points for 7-Day Streak!");
    }
  }, [studyStats.streak]);

  const unlockTheme = (theme, cost) => {
    if (points >= cost && !themes.includes(theme)) {
      setThemes((prev) => [...prev, theme]);
      setCurrentTheme(theme);
      setPoints((prev) => prev - cost);
      toast.success(`ðŸŽ¨ Unlocked ${theme}!`);
    } else if (themes.includes(theme)) {
      setCurrentTheme(theme);
      toast.info(`ðŸŽ¨ Switched to ${theme}!`);
    } else {
      toast.error("Not enough points to unlock this theme!");
    }
  };

  return (
    <div className={`min-h-screen ${themeStyles[currentTheme]}`}>
      <ToastContainer />
      <header className={`p-4 ${headerStyles[currentTheme]}`}>
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold flex items-center">
            <BookOpen className="mr-2" />
            StudyBuddy AI Planner
          </h1>
          <div
            className={`px-4 py-2 rounded-lg text-sm ${
              currentTheme === "Dark Mode"
                ? "bg-indigo-700 text-gray-200"
                : "bg-indigo-700 text-white"
            }`}
          >
            {new Date().toLocaleDateString("en-US", {
              weekday: "long",
              year: "numeric",
              month: "long",
              day: "numeric",
            })}
          </div>
        </div>
      </header>

      <nav className={`shadow ${navStyles[currentTheme]}`}>
        <div className="container mx-auto">
          <div className="flex">
            <button
              onClick={() => setActiveTab("dashboard")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "dashboard"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : currentTheme === "Dark Mode"
                  ? "text-gray-300"
                  : "text-gray-500"
              }`}
            >
              Dashboard
            </button>
            <button
              onClick={() => setActiveTab("tasks")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "tasks"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : currentTheme === "Dark Mode"
                  ? "text-gray-300"
                  : "text-gray-500"
              }`}
            >
              Tasks
            </button>
            <button
              onClick={() => setActiveTab("analytics")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "analytics"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : currentTheme === "Dark Mode"
                  ? "text-gray-300"
                  : "text-gray-500"
              }`}
            >
              Analytics
            </button>
            <button
              onClick={() => setActiveTab("rewards")}
              className={`px-4 py-3 font-medium text-sm ${
                activeTab === "rewards"
                  ? "border-b-2 border-indigo-600 text-indigo-600"
                  : currentTheme === "Dark Mode"
                  ? "text-gray-300"
                  : "text-gray-500"
              }`}
            >
              Rewards
            </button>
          </div>
        </div>
      </nav>

      <main className="container mx-auto p-4">
        {activeTab === "dashboard" && (
          <Dashboard
            tasks={tasks}
            studyStats={studyStats}
            aiSuggestion={aiSuggestion}
            getStudyTips={getStudyTips}
            generateSchedule={generateSchedule}
            setActiveTab={setActiveTab}
            toggleTaskComplete={toggleTaskComplete}
            deleteTask={deleteTask}
            currentTheme={currentTheme}
            logStudyHours={logStudyHours} // Pass the function to Dashboard
          />
        )}

        {activeTab === "tasks" && (
          <TaskManager
            tasks={tasks}
            addTask={addTask}
            updateTask={updateTask}
            deleteTask={deleteTask}
            toggleTaskComplete={toggleTaskComplete}
            currentTheme={currentTheme}
          />
        )}

        {activeTab === "analytics" && (
          <Analytics tasks={tasks} currentTheme={currentTheme} />
        )}

        {activeTab === "rewards" && (
          <Rewards
            points={points}
            badges={badges}
            studyStats={studyStats}
            themes={themes}
            currentTheme={currentTheme}
            unlockTheme={unlockTheme}
          />
        )}
      </main>
    </div>
  );
}

export default Layout;
