import React from "react";
import { toast } from "react-toastify";
import { Trophy, Medal, Flame } from "lucide-react";

const unlockableThemes = [
  {
    name: "Dark Mode",
    cost: 50,
    description: "A sleek dark theme for night owls.",
  },
  {
    name: "Ocean Breeze",
    cost: 50,
    description: "A refreshing blue and teal gradient theme.",
  },
  {
    name: "Sunset Glow",
    cost: 50,
    description: "A warm theme with hues of orange and pink.",
  },
  {
    name: "Forest Whisper",
    cost: 50,
    description: "A calming green theme inspired by nature.",
  },
];

function Rewards({
  points,
  badges,
  studyStats,
  themeConfig,
  unlockedThemes,
  onPointsUpdate,
  onThemeUnlock,
}) {
  const availableBadges = [
    {
      name: "Priority Master",
      description: "Complete 5 high-priority tasks",
      icon: <Trophy className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Task Titan",
      description: "Complete 10 tasks",
      icon: <Medal className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Early Bird",
      description: "Complete 3 tasks before their due date",
      icon: <Trophy className="w-8 h-8 text-yellow-500" />,
    },
    {
      name: "Streak Star",
      description: "Maintain a 7-day study streak",
      icon: <Flame className="w-8 h-8 text-yellow-500" />,
    },
  ];

  const handleRedeemTheme = (theme) => {
    if (unlockedThemes.includes(theme.name)) {
      toast.info(`${theme.name} is already unlocked!`);
      return;
    }
    if (points < theme.cost) {
      toast.error("Not enough points to redeem this theme!");
      return;
    }

    const newPoints = points - theme.cost;
    onPointsUpdate(newPoints);
    onThemeUnlock(theme.name);
    toast.success(`${theme.name} theme unlocked!`);
  };

  return (
    <div className={`rounded-lg shadow ${themeConfig.background}`}>
      <div className={`p-4 border-b ${themeConfig.border}`}>
        <h2 className={`font-semibold ${themeConfig.text}`}>
          Rewards & Achievements
        </h2>
      </div>
      <div className="p-8">
        <div
          className={`p-4 rounded-lg text-center ${themeConfig.secondaryBg}`}
        >
          <h3 className={`text-3xl font-bold mb-2 ${themeConfig.primary}`}>
            {points}
          </h3>
          <p className={`${themeConfig.secondaryText}`}>Total Points Earned</p>
        </div>

        <div className="mt-8">
          <h3 className={`font-medium mb-4 ${themeConfig.text}`}>
            Your Badges
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {availableBadges.map((badge) => (
              <div
                key={badge.name}
                className={`p-4 rounded-lg text-center ${
                  badges.includes(badge.name)
                    ? themeConfig.completedBg
                    : `${themeConfig.secondaryBg} opacity-50`
                }`}
              >
                <div className="flex justify-center">{badge.icon}</div>
                <h4 className={`font-medium mt-2 ${themeConfig.text}`}>
                  {badge.name}
                </h4>
                <p className={`text-sm ${themeConfig.secondaryText}`}>
                  {badge.description}
                </p>
                <p
                  className={`text-sm font-medium mt-1 ${
                    badges.includes(badge.name)
                      ? "text-green-600"
                      : themeConfig.secondaryText
                  }`}
                >
                  {badges.includes(badge.name) ? "Earned!" : "Not yet earned"}
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="mt-8">
          <h3 className={`font-medium mb-4 ${themeConfig.text}`}>
            Study Streaks
          </h3>
          <div
            className={`p-4 rounded-lg text-center ${themeConfig.secondaryBg}`}
          >
            <Flame
              className={`inline-block w-8 h-8 mb-2 ${themeConfig.primary}`}
            />
            <h4 className={`text-lg font-medium ${themeConfig.text}`}>
              Current Streak: {studyStats.streak} Days
            </h4>
            <p className={`text-sm ${themeConfig.secondaryText}`}>
              Keep studying daily to maintain your streak!
            </p>
          </div>
        </div>

        <div className="mt-8">
          <h3 className={`font-medium mb-4 ${themeConfig.text}`}>
            Redeem Themes
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {unlockableThemes.map((theme) => (
              <div
                key={theme.name}
                className={`p-4 rounded-lg text-center ${themeConfig.secondaryBg}`}
              >
                <h4 className={`font-medium mt-2 ${themeConfig.text}`}>
                  {theme.name}
                </h4>
                <p className={`text-sm ${themeConfig.secondaryText}`}>
                  {theme.description}
                </p>
                <p
                  className={`text-sm font-medium mt-1 ${themeConfig.secondaryText}`}
                >
                  Cost: {theme.cost} Points
                </p>
                {unlockedThemes.includes(theme.name) ? (
                  <p className="text-sm text-green-600 mt-2">Unlocked!</p>
                ) : (
                  <button
                    onClick={() => handleRedeemTheme(theme)}
                    className={`mt-2 px-4 py-2 rounded text-sm ${
                      points >= theme.cost
                        ? `${themeConfig.primary} ${themeConfig.primaryText} ${themeConfig.primaryHover}`
                        : "bg-gray-400 cursor-not-allowed"
                    }`}
                    disabled={points < theme.cost}
                  >
                    Redeem
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Rewards;
