import React from "react";
import { Award, Star, Flame } from "lucide-react";

function Rewards({ points, badges, studyStats }) {
  // Define available badges with their descriptions and icons
  const badgeDetails = [
    {
      name: "Priority Master",
      description: "Complete 5 high-priority tasks",
      icon: <Star className="w-6 h-6 text-yellow-500" />,
    },
    {
      name: "Streak Star",
      description: "Maintain a 7-day study streak",
      icon: <Flame className="w-6 h-6 text-orange-500" />,
    },
  ];

  return (
    <div className="bg-white rounded-lg shadow">
      <div className="p-4 border-b border-gray-200">
        <h2 className="font-semibold">Your Rewards</h2>
      </div>
      <div className="p-8">
        {/* Points Display */}
        <div className="text-center mb-8">
          <div className="inline-block p-12 bg-indigo-100 rounded-full mb-4">
            <div className="text-4xl font-bold text-indigo-600">{points}</div>
          </div>
          <h3 className="text-lg font-medium">Total Points</h3>
          <p className="text-gray-500 mt-2">
            Earn points by completing tasks and maintaining streaks!
          </p>
        </div>

        {/* Streak Progress */}
        <div className="bg-gray-50 p-4 rounded-lg mb-8">
          <h4 className="font-medium mb-2">Study Streak Progress</h4>
          <div className="flex justify-center space-x-2 mt-4">
            {[1, 2, 3, 4, 5, 6, 7].map((day) => (
              <div
                key={day}
                className={`w-8 h-8 rounded-full flex items-center justify-center text-sm
                ${
                  day <= studyStats.streak
                    ? "bg-indigo-600 text-white"
                    : "bg-gray-200 text-gray-500"
                }`}
              >
                {day}
              </div>
            ))}
          </div>
          <p className="text-center text-sm text-gray-500 mt-2">
            {studyStats.streak >= 7
              ? "Great job! You've earned the Streak Star badge!"
              : `Keep going! ${
                  7 - studyStats.streak
                } more days to earn the Streak Star badge.`}
          </p>
        </div>

        {/* Badges Display */}
        <div className="bg-gray-50 p-4 rounded-lg">
          <h4 className="font-medium mb-4">Your Badges</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {badgeDetails.map((badge) => (
              <div
                key={badge.name}
                className={`p-4 rounded-lg flex items-center space-x-3 ${
                  badges.includes(badge.name)
                    ? "bg-indigo-100"
                    : "bg-gray-100 opacity-50"
                }`}
              >
                <div>{badge.icon}</div>
                <div>
                  <h5 className="font-medium">{badge.name}</h5>
                  <p className="text-sm text-gray-500">{badge.description}</p>
                </div>
              </div>
            ))}
          </div>
          {badges.length === 0 && (
            <p className="text-center text-gray-500 mt-4">
              Complete tasks and maintain streaks to earn badges!
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Rewards;
