import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSun,
  faMoon,
  faWater,
  faFire,
  faLeaf,
  faTrophy,
} from "@fortawesome/free-solid-svg-icons";
import { toast } from "react-toastify";

const Rewards = ({
  points = 0,
  badges = [],
  studyStats = {},
  currentTheme,
  onPointsUpdate,
  onThemeUnlock,
  unlockedThemes = [],
}) => {
  const themes = [
    {
      name: "Light Mode",
      icon: faSun,
      cost: 0,
      description: "A bright and clean look for focused studying.",
    },
    {
      name: "Dark Mode",
      icon: faMoon,
      cost: 50,
      description: "A sleek, eye-friendly theme for late-night sessions.",
    },
    {
      name: "Ocean Breeze",
      icon: faWater,
      cost: 50,
      description: "A calming blue gradient, like a refreshing sea breeze.",
    },
    {
      name: "Sunset Glow",
      icon: faFire,
      cost: 50,
      description: "Warm hues of a sunset to inspire your study sessions.",
    },
    {
      name: "Forest Whisper",
      icon: faLeaf,
      cost: 50,
      description: "Earthy tones to bring a sense of calm and focus.",
    },
  ];

  const themeStyles = {
    "Light Mode": "bg-gray-100 text-gray-800 border-gray-300",
    "Dark Mode": "bg-gray-800 text-gray-200 border-gray-600",
    "Ocean Breeze":
      "bg-gradient-to-r from-blue-100 to-teal-100 text-blue-900 border-blue-300",
    "Sunset Glow":
      "bg-gradient-to-r from-orange-100 to-pink-100 text-orange-900 border-orange-300",
    "Forest Whisper":
      "bg-gradient-to-r from-green-100 to-teal-100 text-green-900 border-green-300",
  };

  const buttonStyles = {
    "Light Mode": "bg-indigo-600 text-white hover:bg-indigo-700",
    "Dark Mode": "bg-indigo-500 text-white hover:bg-indigo-600",
    "Ocean Breeze": "bg-blue-500 text-white hover:bg-blue-600",
    "Sunset Glow": "bg-orange-500 text-white hover:bg-orange-600",
    "Forest Whisper": "bg-green-500 text-white hover:bg-green-600",
  };

  const disabledButtonStyles = {
    "Light Mode": "bg-gray-400 text-gray-200 cursor-not-allowed",
    "Dark Mode": "bg-gray-600 text-gray-400 cursor-not-allowed",
    "Ocean Breeze": "bg-blue-300 text-blue-100 cursor-not-allowed",
    "Sunset Glow": "bg-orange-300 text-orange-100 cursor-not-allowed",
    "Forest Whisper": "bg-green-300 text-green-100 cursor-not-allowed",
  };

  const handleRedeemTheme = (themeName, cost) => {
    if (points < cost) {
      toast.error("Not enough points to redeem this theme!");
      return;
    }
    onPointsUpdate(points - cost);
    onThemeUnlock(themeName);
    toast.success(`ðŸŽ¨ Theme "${themeName}" unlocked!`);
  };

  return (
    <div className="space-y-6">
      {/* Points Display */}
      <div
        className={`p-4 rounded-lg shadow-md text-center ${
          themeStyles[currentTheme]
        } transition-all duration-300 hover:shadow-lg`}
      >
        <h2 className="text-xl font-semibold mb-2">Your Points</h2>
        <p className="text-3xl font-bold text-indigo-600">{points}</p>
      </div>

      {/* Badges Section */}
      <div
        className={`p-4 rounded-lg shadow-md ${themeStyles[currentTheme]} transition-all duration-300 hover:shadow-lg`}
      >
        <h2 className="text-xl font-semibold mb-4 flex items-center">
          <FontAwesomeIcon icon={faTrophy} className="mr-2 text-yellow-500" />
          Your Badges
        </h2>
        {badges.length === 0 ? (
          <p>No badges earned yet. Keep studying to earn some!</p>
        ) : (
          <div className="flex flex-wrap gap-3">
            {badges.map((badge, index) => (
              <span
                key={index}
                className={`px-3 py-1 rounded-full text-sm font-medium flex items-center space-x-1 transition-transform duration-200 hover:scale-105 ${
                  currentTheme === "Dark Mode"
                    ? "bg-yellow-600 text-white"
                    : "bg-yellow-100 text-yellow-800"
                }`}
              >
                <FontAwesomeIcon icon={faTrophy} className="text-yellow-500" />
                <span>{badge}</span>
              </span>
            ))}
          </div>
        )}
      </div>

      {/* Study Stats Section */}
      <div
        className={`p-4 rounded-lg shadow-md ${themeStyles[currentTheme]} transition-all duration-300 hover:shadow-lg`}
      >
        <h2 className="text-xl font-semibold mb-4">Study Stats</h2>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
          <div className="text-center">
            <p className="text-lg font-medium">Total Hours</p>
            <p className="text-2xl font-bold text-indigo-600">
              {studyStats.totalHours || 0}
            </p>
          </div>
          <div className="text-center">
            <p className="text-lg font-medium">Completed Tasks</p>
            <p className="text-2xl font-bold text-indigo-600">
              {studyStats.completedTasks || 0}
            </p>
          </div>
          <div className="text-center">
            <p className="text-lg font-medium">Current Streak</p>
            <p className="text-2xl font-bold text-indigo-600">
              {studyStats.streak || 0} days
            </p>
          </div>
        </div>
      </div>

      {/* Redeem Themes Section */}
      <div
        className={`p-6 rounded-lg shadow-md ${themeStyles[currentTheme]} transition-all duration-300 hover:shadow-xl`}
      >
        <h2 className="text-xl font-semibold mb-6 flex items-center">
          <FontAwesomeIcon icon={faSun} className="mr-2 text-yellow-500" />
          Redeem Themes
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {themes.map((theme) => {
            const isUnlocked = unlockedThemes?.includes(theme.name);
            return (
              <div
                key={theme.name}
                className={`relative p-4 rounded-lg border-2 shadow-sm transform transition-all duration-300 hover:scale-105 hover:shadow-lg ${
                  themeStyles[theme.name]
                } ${isUnlocked ? "opacity-90" : "opacity-100"}`}
              >
                {/* Theme Icon and Name */}
                <div className="flex items-center mb-3">
                  <FontAwesomeIcon
                    icon={theme.icon}
                    className={`text-2xl mr-3 transition-colors duration-200 ${
                      currentTheme === "Dark Mode"
                        ? "text-gray-300"
                        : "text-gray-700"
                    }`}
                  />
                  <h3 className="text-lg font-semibold">{theme.name}</h3>
                </div>

                {/* Theme Description */}
                <p className="text-sm mb-4 opacity-80">{theme.description}</p>

                {/* Cost Display with Animated Badge */}
                <div className="flex items-center mb-4">
                  <span
                    className={`inline-block px-3 py-1 rounded-full text-sm font-medium transition-all duration-200 ${
                      isUnlocked
                        ? "bg-green-100 text-green-800"
                        : "bg-indigo-100 text-indigo-800 animate-pulse"
                    }`}
                  >
                    {isUnlocked ? "Unlocked" : `${theme.cost} Points`}
                  </span>
                </div>

                {/* Redeem Button */}
                <button
                  onClick={() => handleRedeemTheme(theme.name, theme.cost)}
                  disabled={isUnlocked || points < theme.cost}
                  className={`w-full py-2 rounded-lg font-medium transition-all duration-200 transform hover:-translate-y-1 hover:shadow-md ${
                    isUnlocked || points < theme.cost
                      ? disabledButtonStyles[currentTheme]
                      : buttonStyles[currentTheme]
                  }`}
                >
                  {isUnlocked ? "Unlocked" : "Redeem"}
                </button>

                {/* Decorative Overlay for Unlocked Themes */}
                {isUnlocked && (
                  <div className="absolute inset-0 pointer-events-none">
                    <div className="absolute top-2 right-2 w-8 h-8 bg-green-500 rounded-full flex items-center justify-center animate-bounce">
                      <svg
                        className="w-5 h-5 text-white"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth="2"
                          d="M5 13l4 4L19 7"
                        />
                      </svg>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Rewards;
