import React, { useState, useMemo } from "react";
import {
  Calendar,
  Clock,
  CheckCircle,
  PlusCircle,
  Trash2,
  Edit3,
} from "lucide-react";

// Theme configuration for consistent styling
const themeStyles = {
  "Light Mode": {
    bg: "bg-white",
    border: "border-gray-200",
    text: "text-gray-800",
    subText: "text-gray-500",
    accent: "bg-indigo-600",
    cardBg: "bg-gray-100",
    insightBg: "bg-indigo-50",
    buttonPrimary: "bg-indigo-600 text-white hover:bg-indigo-700",
    buttonSecondary:
      "bg-white border border-gray-300 text-gray-700 hover:bg-gray-50",
    inputBg: "bg-white border-gray-300 text-gray-800",
    focusRing: "focus:ring-indigo-400",
    hoverBg: "hover:bg-gray-50",
    activeDot: "bg-indigo-600 text-white",
    inactiveDot: "bg-gray-100",
    aiDot: "bg-indigo-400",
    aiCard: "bg-indigo-100",
    aiContent: "bg-indigo-50 border-indigo-100",
  },
  "Dark Mode": {
    bg: "bg-gray-700",
    border: "border-gray-600",
    text: "text-gray-200",
    subText: "text-gray-400",
    accent: "bg-indigo-500",
    cardBg: "bg-gray-600",
    insightBg: "bg-indigo-900",
    buttonPrimary: "bg-indigo-500 text-white hover:bg-indigo-600",
    buttonSecondary:
      "bg-gray-600 border-gray-500 text-gray-200 hover:bg-gray-500",
    inputBg: "bg-gray-800 border-gray-500 text-gray-200",
    focusRing: "focus:ring-indigo-400",
    hoverBg: "hover:bg-gray-600",
    activeDot: "bg-indigo-500 text-white",
    inactiveDot: "bg-gray-600",
    aiDot: "bg-indigo-500",
    aiCard: "bg-indigo-900",
    aiContent: "bg-indigo-900 border-indigo-800",
  },
  "Ocean Breeze": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-teal-300",
    text: "text-teal-800",
    subText: "text-blue-600",
    accent: "bg-teal-500",
    cardBg: "bg-amber-100 bg-opacity-70 backdrop-blur-sm",
    insightBg: "bg-teal-100 bg-opacity-70 backdrop-blur-sm",
    buttonPrimary: "bg-teal-500 text-white hover:bg-teal-600",
    buttonSecondary:
      "bg-amber-100 bg-opacity-70 backdrop-blur-sm border-teal-300 text-teal-800 hover:bg-amber-200",
    inputBg: "bg-blue-100 border-teal-300 text-teal-800",
    focusRing: "focus:ring-teal-400",
    hoverBg: "hover:bg-teal-100",
    activeDot: "bg-teal-500 text-white",
    inactiveDot: "bg-blue-200",
    aiDot: "bg-teal-500",
    aiCard: "bg-teal-100",
    aiContent: "bg-teal-100 bg-opacity-70 backdrop-blur-sm border-teal-300",
  },
  "Sunset Glow": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-orange-300",
    text: "text-orange-800",
    subText: "text-orange-500",
    accent: "bg-orange-500",
    cardBg: "bg-pink-100 bg-opacity-70 backdrop-blur-sm",
    insightBg: "bg-pink-100 bg-opacity-70 backdrop-blur-sm",
    buttonPrimary: "bg-orange-500 text-white hover:bg-orange-600",
    buttonSecondary:
      "bg-pink-100 bg-opacity-70 backdrop-blur-sm border-orange-300 text-orange-800 hover:bg-pink-200",
    inputBg: "bg-orange-100 border-orange-300 text-orange-800",
    focusRing: "focus:ring-orange-400",
    hoverBg: "hover:bg-orange-100",
    activeDot: "bg-orange-500 text-white",
    inactiveDot: "bg-orange-200",
    aiDot: "bg-orange-500",
    aiCard: "bg-orange-100",
    aiContent: "bg-pink-100 bg-opacity-70 backdrop-blur-sm border-orange-300",
  },
  "Forest Whisper": {
    bg: "bg-opacity-70 backdrop-blur-lg",
    border: "border-green-300",
    text: "text-green-800",
    subText: "text-green-500",
    accent: "bg-green-500",
    cardBg: "bg-emerald-100 bg-opacity-70 backdrop-blur-sm",
    insightBg: "bg-emerald-100 bg-opacity-70 backdrop-blur-sm",
    buttonPrimary: "bg-green-500 text-white hover:bg-green-600",
    buttonSecondary:
      "bg-emerald-100 bg-opacity-70 backdrop-blur-sm border-green-300 text-green-800 hover:bg-emerald-200",
    inputBg: "bg-green-100 border-green-300 text-green-800",
    focusRing: "focus:ring-green-400",
    hoverBg: "hover:bg-green-100",
    activeDot: "bg-green-500 text-white",
    inactiveDot: "bg-green-200",
    aiDot: "bg-green-500",
    aiCard: "bg-green-100",
    aiContent: "bg-emerald-100 bg-opacity-70 backdrop-blur-sm border-green-300",
  },
};

function Dashboard({
  tasks,
  studyStats,
  aiSuggestion,
  getStudyTips,
  generateSchedule,
  setActiveTab,
  toggleTaskComplete,
  deleteTask,
  currentTheme,
  logStudyHours,
}) {
  // Select theme styles, default to Light Mode if theme not found
  const currentThemeStyles =
    themeStyles[currentTheme] || themeStyles["Light Mode"];

  // State for logging hours and error message
  const [hoursInput, setHoursInput] = useState("");
  const [hoursError, setHoursError] = useState("");

  // Memoize upcoming tasks to avoid recomputing on every render
  const upcomingTasks = useMemo(() => {
    return tasks.filter((task) => !task.completed).slice(0, 3);
  }, [tasks]);

  const handleLogHours = () => {
    const hours = parseFloat(hoursInput);
    if (isNaN(hours) || hours <= 0) {
      setHoursError("Please enter a valid number of hours (greater than 0).");
      return;
    }
    setHoursError("");
    logStudyHours(hours);
    setHoursInput("");
  };

  return (
    <div className="space-y-6">
      {/* AI Assistant Card */}
      <div className={`rounded-lg shadow ${currentThemeStyles.bg} p-4 sm:p-6`}>
        <h2
          className={`text-lg font-semibold mb-4 flex items-center ${currentThemeStyles.text}`}
        >
          <div
            className={`w-8 h-8 rounded-full flex items-center justify-center mr-2 ${currentThemeStyles.aiCard}`}
          >
            <div
              className={`w-4 h-4 rounded-full ${currentThemeStyles.aiDot}`}
            />
          </div>
          AI Study Assistant
        </h2>
        <div
          className={`p-4 rounded-lg border min-h-[100px] ${currentThemeStyles.aiContent}`}
        >
          {aiSuggestion ? (
            <div className="whitespace-pre-line">{aiSuggestion}</div>
          ) : (
            <div className="flex items-center justify-center h-16">
              <div className="animate-pulse flex space-x-2">
                <div
                  className={`w-2 h-2 rounded-full ${currentThemeStyles.aiDot}`}
                />
                <div
                  className={`w-2 h-2 rounded-full ${currentThemeStyles.aiDot}`}
                />
                <div
                  className={`w-2 h-2 rounded-full ${currentThemeStyles.aiDot}`}
                />
              </div>
            </div>
          )}
        </div>
        <div className="mt-4 flex flex-col sm:flex-row sm:space-x-2 space-y-2 sm:space-y-0">
          <button
            onClick={getStudyTips}
            className={`px-4 py-2 rounded-lg text-sm ${currentThemeStyles.buttonPrimary} transition-colors duration-200 w-full sm:w-auto`}
            aria-label="Get personalized study tips from the AI assistant"
          >
            Get Study Tips
          </button>
          <button
            onClick={generateSchedule}
            className={`px-4 py-2 rounded-lg text-sm ${currentThemeStyles.buttonSecondary} transition-colors duration-200 w-full sm:w-auto`}
            aria-label="Generate a study schedule using the AI assistant"
          >
            Generate Schedule
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* Study Hours */}
        <div
          className={`rounded-lg shadow p-4 sm:p-6 ${currentThemeStyles.bg}`}
        >
          <div className="flex justify-between items-center">
            <h3 className={`${currentThemeStyles.subText} text-sm`}>
              Study Hours
            </h3>
            <Clock
              className={`${currentThemeStyles.accent} h-5 w-5`}
              aria-hidden="true"
            />
          </div>
          <p className={`text-3xl font-bold mt-2 ${currentThemeStyles.text}`}>
            {studyStats.totalHours}h
          </p>
          <div className="mt-4 space-y-2">
            <div className="flex space-x-2">
              <input
                type="number"
                placeholder="Hours"
                className={`p-2 border rounded w-24 ${currentThemeStyles.inputBg} focus:outline-none focus:ring-2 ${currentThemeStyles.focusRing} transition-all duration-200`}
                value={hoursInput}
                onChange={(e) => {
                  setHoursInput(e.target.value);
                  setHoursError("");
                }}
                min="0"
                step="0.5"
                aria-label="Enter study hours to log"
              />
              <button
                onClick={handleLogHours}
                className={`px-4 py-2 rounded-lg text-sm ${currentThemeStyles.buttonPrimary} transition-colors duration-200`}
                aria-label="Log study hours"
              >
                Log Hours
              </button>
            </div>
            {hoursError && <p className="text-red-500 text-xs">{hoursError}</p>}
          </div>
        </div>

        {/* Tasks Completed */}
        <div
          className={`rounded-lg shadow p-4 sm:p-6 ${currentThemeStyles.bg}`}
        >
          <div className="flex justify-between items-center">
            <h3 className={`${currentThemeStyles.subText} text-sm`}>
              Tasks Completed
            </h3>
            <CheckCircle
              className={`${currentThemeStyles.accent} h-5 w-5`}
              aria-hidden="true"
            />
          </div>
          <p className={`text-3xl font-bold mt-2 ${currentThemeStyles.text}`}>
            {studyStats.completedTasks}
          </p>
        </div>

        {/* Study Streak */}
        <div
          className={`rounded-lg shadow p-4 sm:p-6 ${currentThemeStyles.bg}`}
        >
          <div className="flex justify-between items-center">
            <h3 className={`${currentThemeStyles.subText} text-sm`}>
              Study Streak
            </h3>
            <Calendar
              className={`${currentThemeStyles.accent} h-5 w-5`}
              aria-hidden="true"
            />
          </div>
          <p className={`text-3xl font-bold mt-2 ${currentThemeStyles.text}`}>
            {studyStats.streak} days
          </p>
          <div className="flex space-x-1 mt-2">
            {[1, 2, 3, 4, 5, 6, 7].map((day) => (
              <div
                key={day}
                className={`w-6 h-6 rounded-full flex items-center justify-center text-xs ${
                  day <= studyStats.streak
                    ? currentThemeStyles.activeDot
                    : currentThemeStyles.inactiveDot
                }`}
                aria-label={`Day ${day}: ${
                  day <= studyStats.streak
                    ? "Active in streak"
                    : "Inactive in streak"
                }`}
              >
                {day}
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Upcoming Tasks */}
      <div className={`rounded-lg shadow ${currentThemeStyles.bg}`}>
        <div className={`p-4 border-b ${currentThemeStyles.border}`}>
          <h2 className={`font-semibold ${currentThemeStyles.text}`}>
            Upcoming Tasks
          </h2>
        </div>
        <div className="p-2">
          {upcomingTasks.map((task) => (
            <div
              key={task.id}
              className={`p-2 rounded-md flex justify-between items-center ${currentThemeStyles.hoverBg} transition-colors duration-200`}
            >
              <div className="flex items-center">
                <button
                  onClick={() => toggleTaskComplete(task.id)}
                  className={`w-5 h-5 rounded-full border mr-3 flex items-center justify-center ${
                    task.priority === "High"
                      ? "border-red-500"
                      : "border-yellow-500"
                  } hover:shadow-sm transition-shadow duration-200`}
                  aria-label={`Mark task "${task.title}" as completed`}
                >
                  {task.completed && (
                    <CheckCircle
                      className="w-4 h-4 text-green-600"
                      aria-hidden="true"
                    />
                  )}
                </button>
                <div>
                  <h3
                    className={`text-sm font-medium ${currentThemeStyles.text}`}
                  >
                    {task.title}
                  </h3>
                  <span className={`${currentThemeStyles.subText} text-xs`}>
                    Due: {task.dueDate}
                  </span>
                </div>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => setActiveTab("tasks")}
                  className={`${currentThemeStyles.subText} hover:${currentThemeStyles.accent} transition-colors duration-200`}
                  aria-label={`Edit task "${task.title}"`}
                >
                  <Edit3 className="w-4 h-4" />
                </button>
                <button
                  onClick={() => deleteTask(task.id)}
                  className={`${currentThemeStyles.subText} hover:${currentThemeStyles.accent} transition-colors duration-200`}
                  aria-label={`Delete task "${task.title}"`}
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            </div>
          ))}
          {upcomingTasks.length === 0 && (
            <div className={`py-8 text-center ${currentThemeStyles.subText}`}>
              No upcoming tasks. Add one below!
            </div>
          )}
        </div>
        <div className={`p-4 border-t ${currentThemeStyles.border}`}>
          <button
            onClick={() => setActiveTab("tasks")}
            className={`${currentThemeStyles.accent} hover:${currentThemeStyles.buttonPrimary} text-sm font-medium flex items-center transition-colors duration-200`}
            aria-label="Add a new task"
          >
            <PlusCircle className="w-4 h-4 mr-1" aria-hidden="true" />
            Add New Task
          </button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
